ctCheckPointTalkConf[86]={nIndex=86,nCheckPointId=16,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="宋太医 ",sBackground="bg_zhandou",sTalk="男人吃了以后，更持久、更坚挺，十分安全，没有副作用，而且没有不良反应。但女人吃了，是不起作用的。所以绿篱姑娘，你只需服侍娘娘稍作休息，即可痊愈。",sColor="0",bShake=false,sVoice="0",}
