ctCheckPointTalkConf[98]={nIndex=98,nCheckPointId=17,nTalkIndex=10,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="不过呀，娘娘暂可宽心，奴婢今日听到消息，太后听说娘娘情绪不稳，怀疑是江映月那小妖精，蓄意陷害太子爷和娘娘落水，已经震怒",sColor="0",bShake=false,sVoice="0",}
