ctCheckPointTalkConf[105]={nIndex=105,nCheckPointId=17,nTalkIndex=17,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="我说到哪儿了？嗷，现在呀,太子爷和赵王还在圣宁宫外跪着求情呢！您说赵王担心那小妖精，巴巴儿地在那跪着，也就罢了，毕竟是自家媳妇儿",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[106]={nIndex=106,nCheckPointId=17,nTalkIndex=18,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="咱太子爷去跪个什么劲儿啊？！这不明摆着给赵王添堵吗，也给娘娘您添堵啊。哼，那个江映月，光是摊上蓄意拉太子下水这一条，就很有可能被处死呢！",sColor="0",bShake=false,sVoice="0",}
