ctCheckPointTalkConf[121]={nIndex=121,nCheckPointId=21,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="太后",sBackground="bg_zhandou",sTalk="你们这是干嘛啊？都在这跪这么长时间了，还没过年呢，就管哀家要压岁钱呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[122]={nIndex=122,nCheckPointId=21,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="皇祖母，这次错全在我，您就放了映月吧",sColor="0",bShake=false,sVoice="0",}
