ctCheckPointTalkConf[372]={nIndex=372,nCheckPointId=107,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="如此，本王便不再派你去探太子妃了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[373]={nIndex=373,nCheckPointId=108,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="别！别！别！九哥，我是闹着玩的！我就喜欢太子妃那样的性格！多有活力啊！我觉得她像是迎来了自己的第二春，一点也不像别人口中所说的，额！“不受宠的怨妇啊”!",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[374]={nIndex=374,nCheckPointId=108,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="这样说来太子妃是找回了一些儿时的影子……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[375]={nIndex=375,nCheckPointId=108,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="哎呦..啊..肚子疼…..啊！……啊…哎呀，出来了…弄一手！啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[376]={nIndex=376,nCheckPointId=109,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啧！我说宝贝儿，我这刚来感觉，怎么又对起眼儿了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[377]={nIndex=377,nCheckPointId=109,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘！奴婢没玩过这个“一二三，木头人”，要不然我们重新玩儿吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[378]={nIndex=378,nCheckPointId=109,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="算了，不玩儿了！无聊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[379]={nIndex=379,nCheckPointId=110,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="那要不我陪您下棋？",sColor="0",bShake=false,sVoice="0",}
