ctCheckPointTalkConf[381]={nIndex=381,nCheckPointId=111,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="那~我给您唱个小曲儿？",sColor="0",bShake=false,sVoice="0",}
