ctCheckPointTalkConf[412]={nIndex=412,nCheckPointId=121,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="说！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[413]={nIndex=413,nCheckPointId=121,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我说！我说！我是西王母的第七个女儿，也就是七仙女。那天我和姐妹们下凡，忽然看到一个英俊的书生……我…",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[414]={nIndex=414,nCheckPointId=121,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="斩！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[415]={nIndex=415,nCheckPointId=121,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[416]={nIndex=416,nCheckPointId=122,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这个故事不行，得换一个。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[417]={nIndex=417,nCheckPointId=122,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="如实招来！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[418]={nIndex=418,nCheckPointId=122,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="太子殿下、可曾记得，那一年您在南山狩猎，救过一只可爱的小白兔？那只小白兔化作了人形来到了人间，它发誓它一定要向它的救命恩人报恩！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[419]={nIndex=419,nCheckPointId=122,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="斩！斩！斩！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[420]={nIndex=420,nCheckPointId=122,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[421]={nIndex=421,nCheckPointId=123,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="萨瓦迪卡！太子殿下，这是我特地托朋友从马来给您运供过来的水果，叫榴莲.榴莲是水果之王，绝对配得上您的九五之尊！吃起来特别像臭豆腐，越吃越香！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[422]={nIndex=422,nCheckPointId=123,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="废话少说，说你该说的！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[423]={nIndex=423,nCheckPointId=124,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="话说，在公元2015年，也就是从现在算起，过了很久以后的某一年，有一个人，他的名字叫张鹏，此人参加了一个轰趴晚宴，这个晚宴里有个大池子",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[424]={nIndex=424,nCheckPointId=124,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="所有人呢都在池子里游泳，他就在水池旁边跟人打赌，说如果别人家的媳妇掉进水里，他一定会义不容辞地下去救他",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[425]={nIndex=425,nCheckPointId=124,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="说来也巧了，就在此时，一个貌美如花的女人，扑通！掉进了水里，你说他是救了还是没救？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[426]={nIndex=426,nCheckPointId=124,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="此人水性如何？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[427]={nIndex=427,nCheckPointId=124,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="水性一般。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[428]={nIndex=428,nCheckPointId=124,nTalkIndex=6,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="那必然是救了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[429]={nIndex=429,nCheckPointId=125,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="猜的没错！他的确是跳下水去救人了，但是后来发生了什么事，太子殿下你可猜得到？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[430]={nIndex=430,nCheckPointId=125,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="被救上来的女人视此人为救命恩人，重金感谢了此人？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[431]={nIndex=431,nCheckPointId=125,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="非也。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[432]={nIndex=432,nCheckPointId=125,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="那是怎样？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[433]={nIndex=433,nCheckPointId=125,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="此人被那落水的女人一脚踢晕，当他醒来的时候，他就到了这里，他的名字里就多了一个字，由以前的张鹏变成了张芃芃。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[434]={nIndex=434,nCheckPointId=126,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你这是胡编笑话，来寻我开心？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[435]={nIndex=435,nCheckPointId=126,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我向毛主席保证！我说的都是真心话。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[436]={nIndex=436,nCheckPointId=126,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="如果你说的都是真的，那我倒要问你！你说的故事里，这个落水的，那个落水的，可是在影射那日在太液池里，我没有先救你？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[437]={nIndex=437,nCheckPointId=126,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我可没有含沙射影的意思。您和那江氏什么关系，我一点儿都不在乎，你是太子嘛，将来注定要三宫六院的，嘿嘿,这我理解，男人嘛……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[438]={nIndex=438,nCheckPointId=127,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="别扯远了！",sColor="0",bShake=false,sVoice="0",}
