ctCheckPointTalkConf[655]={nIndex=655,nCheckPointId=204,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你现在知道这件事情不是我做的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[656]={nIndex=656,nCheckPointId=204,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="此事过后不久，我便猜到其中可能有蹊跷，只怪我一时太过鲁莽，对不起",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[657]={nIndex=657,nCheckPointId=204,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="对不起，你知不知道这件事情我比窦娥都冤！所有人都以为是我干的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[658]={nIndex=658,nCheckPointId=204,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="此事是委屈了你。不过，我希望，就到此为止",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[659]={nIndex=659,nCheckPointId=205,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你有一颗想保护小三的心，我理解！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[660]={nIndex=660,nCheckPointId=205,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="其实我一直都知道，江氏虽然外表温柔娴静，实则心思阴鸷、细密",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[661]={nIndex=661,nCheckPointId=206,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你既然知道江氏的为人，无非就是个绿茶婊嘛，你说你喜欢她什么啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[662]={nIndex=662,nCheckPointId=206,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="绿茶？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[663]={nIndex=663,nCheckPointId=206,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="绿茶、就是指那些清纯脱俗，长发飘飘，在人前装作纯天然无公害、岁月静好的样子，实际上呢、却是多愁善感，多病多灾、善于心计的女人。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[664]={nIndex=664,nCheckPointId=207,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="我自幼便与她亲近些，她性情温婉、体贴，与我十分相投。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[665]={nIndex=665,nCheckPointId=207,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（内心）哎～想当年围在哥身边的绿茶婊也是数不胜数啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[666]={nIndex=666,nCheckPointId=207,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="也难怪啦。我在那样的女人身上也跌倒过无数回。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[667]={nIndex=667,nCheckPointId=208,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="又开始胡言乱语了！你好生休息，伤好之前不要乱跑。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[668]={nIndex=668,nCheckPointId=208,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="您慢走。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[669]={nIndex=669,nCheckPointId=209,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="绿篱、哎呀热死了，宝贝儿快给我倒杯水去！人呢？宝贝儿？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[670]={nIndex=670,nCheckPointId=209,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="回来了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[671]={nIndex=671,nCheckPointId=209,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你脸上的伤尚未痊愈，还是多注意休息为好。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[672]={nIndex=672,nCheckPointId=210,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="多谢太子殿下关心，我一定好好保护自己的身体！只是天色已晚了，不知殿下来我这里有何贵干？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[673]={nIndex=673,nCheckPointId=210,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你是我的太子妃，我来你这儿一定要有事儿吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[674]={nIndex=674,nCheckPointId=210,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="没有没有没有没有……只要您开心，一切随您。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[675]={nIndex=675,nCheckPointId=211,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你给我放松一下吧。陪父皇看了一天的奏折，身子乏透了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[676]={nIndex=676,nCheckPointId=211,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="原来是想做马杀鸡！好说好说！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[677]={nIndex=677,nCheckPointId=212,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="怎么了？大保健不做了？你想干什么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[678]={nIndex=678,nCheckPointId=212,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="我有话问你。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[679]={nIndex=679,nCheckPointId=213,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="不知芃芃现在喜欢什么样的男子？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[680]={nIndex=680,nCheckPointId=213,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊？什么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[681]={nIndex=681,nCheckPointId=213,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你是喜欢温润如玉、成熟稳重，还是喜欢浪漫洒脱、年少英俊的？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[682]={nIndex=682,nCheckPointId=213,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（内心）齐晟啊齐晟，老子什么男人也不喜欢，老子只想安安静静的、做个美男子！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[683]={nIndex=683,nCheckPointId=214,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="其实喜欢什么样的人呢不重要！爱是可以跨越性别的！重要的是、那个人是谁。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[684]={nIndex=684,nCheckPointId=214,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="那芃芃觉得，我和老九，谁更讨女人的欢心呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[685]={nIndex=685,nCheckPointId=214,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我不是和你说过了吗，这些都不重要！天注定，而且我现在已经是你的太子妃了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[686]={nIndex=686,nCheckPointId=215,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="今夜就当做是你我之间的闲聊，不必把我当做是太子，你也暂且把太子妃的身份放在一边。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[687]={nIndex=687,nCheckPointId=215,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="真的？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[688]={nIndex=688,nCheckPointId=215,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="真的。无论你说什么,我都只当作是玩笑之谈。我只是想知道，除去太子的身份，我在你眼中，究竟是个什么样的人？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[689]={nIndex=689,nCheckPointId=215,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你若这么说，我就给你分析分析！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[690]={nIndex=690,nCheckPointId=216,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="像你这种成熟沉稳、冷峻腹黑的人，给人的第一印象，就是不太容易相处，就算想和你打情骂俏，也要先练练，对着这张面瘫脸能不能忍住不笑。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[691]={nIndex=691,nCheckPointId=216,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你接着说。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[692]={nIndex=692,nCheckPointId=216,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="不过我觉得像那种英俊潇洒、风流倜傥的人呢，通常就很有亲和力，和他们在一起久了呢你就不会觉得有压力，这样男人才值得女人喜欢喽！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[693]={nIndex=693,nCheckPointId=216,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你终究还是选错了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[694]={nIndex=694,nCheckPointId=216,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这种事，哪有对错之分！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[695]={nIndex=695,nCheckPointId=216,nTalkIndex=6,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="好了，时候不早了，早些休息吧",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[696]={nIndex=696,nCheckPointId=217,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="强公公求见",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[697]={nIndex=697,nCheckPointId=217,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="进",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[698]={nIndex=698,nCheckPointId=218,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="娘娘，太子殿下要替皇上去江北大营巡查，命您陪同前往呢！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[699]={nIndex=699,nCheckPointId=218,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="让我去？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[700]={nIndex=700,nCheckPointId=218,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="是啊，太子殿下吩咐说，到时候他会随护卫队先行一步，娘娘则有专人护送。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[701]={nIndex=701,nCheckPointId=219,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="诶、强公公、我能不去吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[702]={nIndex=702,nCheckPointId=219,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="娘娘、能陪太子殿下出访，那可是至高无上的荣耀呢！娘娘还是赶紧收拾收拾，奴才就先回去了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[703]={nIndex=703,nCheckPointId=219,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="好端端的，去什么江北大营呢？ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[704]={nIndex=704,nCheckPointId=220,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="驾、驾、圉～这么快就到了？太子人呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[705]={nIndex=705,nCheckPointId=220,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="士兵乙",sBackground="bg_zhandou",sTalk="娘娘，这儿并非是江北大营，而是通往江北一个最重要的货物中转站，军中粮草、物资等等都是从这儿中转",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[706]={nIndex=706,nCheckPointId=221,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="呦！什么大事儿能劳我女神姐姐来这儿啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[707]={nIndex=707,nCheckPointId=221,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="随军家属，身不由己！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[708]={nIndex=708,nCheckPointId=222,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="看惯了军中的装束，你这身儿真是巾帼不让须眉啊、肯定能流行起来。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[709]={nIndex=709,nCheckPointId=222,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="流行什么啊！这种制服诱惑早都该过时了。我啊这是掩人耳目。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[710]={nIndex=710,nCheckPointId=223,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="士兵乙",sBackground="bg_zhandou",sTalk="娘娘，我们还是赶快赶路吧！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[711]={nIndex=711,nCheckPointId=223,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="看见没有，我可没有时间闲功夫在这儿搭理、我先走了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[712]={nIndex=712,nCheckPointId=224,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="诶、别走啊！诶、别走啊！ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[713]={nIndex=713,nCheckPointId=224,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="回头带了桂花糕慢慢聊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[714]={nIndex=714,nCheckPointId=225,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="诶、我问你，去江北营中那条路啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[715]={nIndex=715,nCheckPointId=225,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="士兵甲",sBackground="bg_zhandou",sTalk="这条最近。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[716]={nIndex=716,nCheckPointId=225,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="那个方向能到江北营中吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[717]={nIndex=717,nCheckPointId=226,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="士兵甲",sBackground="bg_zhandou",sTalk="那个方向刚好与江北大营相反，是去往青浦的方向。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[718]={nIndex=718,nCheckPointId=226,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="坏了！我就觉得那个士兵不对劲儿。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[719]={nIndex=719,nCheckPointId=227,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="这个方向能通往青浦吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[720]={nIndex=720,nCheckPointId=227,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="士兵甲",sBackground="bg_zhandou",sTalk="这条路不好走，但是条捷径。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[721]={nIndex=721,nCheckPointId=228,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="快！速速通知九王，让他通往青浦方向去。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[722]={nIndex=722,nCheckPointId=228,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="士兵甲",sBackground="bg_zhandou",sTalk="是！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[723]={nIndex=723,nCheckPointId=229,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="喂，我们是不是走错方向了，按说江北应该早就到了吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[724]={nIndex=724,nCheckPointId=229,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="士兵乙",sBackground="bg_zhandou",sTalk="没错，娘娘这就是往江北大营的方向，是娘娘记错了吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[725]={nIndex=725,nCheckPointId=230,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哦！确实是我记错了！这野外的方向我哪儿会有你熟悉啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[726]={nIndex=726,nCheckPointId=230,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="该不会这谋杀案不会让我碰上了吧？我和他没什么仇没什么怨，他为什么要杀我？不行，这下麻烦了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[727]={nIndex=727,nCheckPointId=231,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎呦，我肚子不舒服，我要去上厕所！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[728]={nIndex=728,nCheckPointId=231,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="士兵乙",sBackground="bg_zhandou",sTalk="娘娘，对不住了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[729]={nIndex=729,nCheckPointId=232,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="姐姐，我来救你了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[730]={nIndex=730,nCheckPointId=232,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="弟弟救我！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[731]={nIndex=731,nCheckPointId=232,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="姐姐，快跑！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[732]={nIndex=732,nCheckPointId=233,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="杨严，该不会是他一个人追杀不成，又派来了千军万马的救兵吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[733]={nIndex=733,nCheckPointId=233,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="姐姐，不用跑了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[734]={nIndex=734,nCheckPointId=234,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="看来九王真的是猴子搬来的救兵",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[735]={nIndex=735,nCheckPointId=234,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="什么猴子啊！是我！我早通知九哥了，就知道他一定会来接应咱们的",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[736]={nIndex=736,nCheckPointId=235,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="你没事吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[737]={nIndex=737,nCheckPointId=235,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我……（晕了过去）",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[738]={nIndex=738,nCheckPointId=236,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="杨严。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[739]={nIndex=739,nCheckPointId=236,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="姐姐，你醒了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[740]={nIndex=740,nCheckPointId=237,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你九哥他人呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[741]={nIndex=741,nCheckPointId=237,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="正审问那刺客呢。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[742]={nIndex=742,nCheckPointId=238,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="你在我府中潜伏了数月，心甘情愿地做一名家丁，不辞辛苦地清洗夜壶，打扫茅厕，现在却来行刺太子妃，你既说无人指使，我倒要看看你是何居心？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[743]={nIndex=743,nCheckPointId=238,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="士兵乙",sBackground="bg_zhandou",sTalk="我无话可说，但求九王赐死！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[744]={nIndex=744,nCheckPointId=239,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="死我也会让你死个明白！你以为我现在才注意到你吗？你到我府上的第三天，我便派人查清楚了你的底细。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[745]={nIndex=745,nCheckPointId=239,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="你是谁派来的，我早就一清二楚，只是我不知道你和你背后主子，到底在谋划什么。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[746]={nIndex=746,nCheckPointId=240,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="士兵乙",sBackground="bg_zhandou",sTalk="既然你早知道是谁，又何必过了那么久也不杀了我，现在却来审问我？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[747]={nIndex=747,nCheckPointId=240,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="本王有一事不明，你潜伏了数月，一直没有动静，却在江北现了形。我一直不明白，你为何要对太子妃下手？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[748]={nIndex=748,nCheckPointId=241,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="今日若事成，我定不会亏待各位兄弟！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[749]={nIndex=749,nCheckPointId=241,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="士兵甲",sBackground="bg_zhandou",sTalk="是！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[750]={nIndex=750,nCheckPointId=241,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="把他们一举拿下！统统拿下！全部拿下！我忍了这么久，本王就靠今日翻身了！我看今日过后，谁还敢在我的头上拉屎！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[751]={nIndex=751,nCheckPointId=242,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="你可知道，这刺客是谁派来的？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[752]={nIndex=752,nCheckPointId=242,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这一路上，也有想过，只是……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[753]={nIndex=753,nCheckPointId=242,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="只是不敢相信吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[754]={nIndex=754,nCheckPointId=242,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="那刺客我已经审完了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[755]={nIndex=755,nCheckPointId=243,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="是齐晟吗！？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[756]={nIndex=756,nCheckPointId=243,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="他竟对我下如此狠手？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[757]={nIndex=757,nCheckPointId=243,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="开始我也想不通他为何会对你下此狠手。但是在审问刺客的时候，我终于明白了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[758]={nIndex=758,nCheckPointId=244,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="太子妃深更半夜被我的家丁带走，遇刺。罪责就全部怪到了我的身上！齐晟，真的是好计谋啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[759]={nIndex=759,nCheckPointId=244,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="他为了除掉你，不惜牺牲掉自己的太子妃吗？ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[760]={nIndex=760,nCheckPointId=244,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="对于一个即将要登上皇位的人，一切不过是浮云流水般，不足为提",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[761]={nIndex=761,nCheckPointId=245,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="九王，我知道，你也是想得到皇位的人，如果是你，你也会这样做吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[762]={nIndex=762,nCheckPointId=245,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="芃芃、我定是不会做出这种绝情绝义的事！我与齐晟的不同也恰恰如此。我的心是向着你的",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[763]={nIndex=763,nCheckPointId=246,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="那你此时觉得，“平安康泰，衣食无忧”的诺言，足以让你相信了吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[764]={nIndex=764,nCheckPointId=246,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="此时此刻我受了如此大的刺激，还不能急着答复你。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[765]={nIndex=765,nCheckPointId=247,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="好，我不会急于要你的答案。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[766]={nIndex=766,nCheckPointId=247,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我到底犯了什么错，齐晟会对我下如此狠手，还这么着急？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[767]={nIndex=767,nCheckPointId=248,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="九哥，不好了、齐晟人马朝咱们这儿来了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[768]={nIndex=768,nCheckPointId=248,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="该来的到底还是来了。快去帐外迎接。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[769]={nIndex=769,nCheckPointId=249,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="大胆九王，居然差遣下人行刺太子妃！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[770]={nIndex=770,nCheckPointId=249,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="太子殿下，娘娘深夜流落在外，臣弟怕娘娘清誉受损，便容留娘娘在臣弟营帐中休息",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[771]={nIndex=771,nCheckPointId=250,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我们遇刺与九王毫无关系！反而是九王的人救了我！那行刺之人已经拿去审问了，原来幕后指使另有其人。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[772]={nIndex=772,nCheckPointId=250,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="可知是何人所为？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[773]={nIndex=773,nCheckPointId=250,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="还不就是……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[774]={nIndex=774,nCheckPointId=250,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="刺客是我一个人审的，还有有说出幕后指使是谁，便挥刀自宫而亡了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[775]={nIndex=775,nCheckPointId=251,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="自宫！？他自宫前都说了什么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[776]={nIndex=776,nCheckPointId=251,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="并未说出有用的消息。",sColor="0",bShake=false,sVoice="0",}
