ctCheckPointTalkConf[781]={nIndex=781,nCheckPointId=253,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="杨严",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[782]={nIndex=782,nCheckPointId=254,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="快跑！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[783]={nIndex=783,nCheckPointId=254,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="娘娘快跑！快跑",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[784]={nIndex=784,nCheckPointId=254,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="九哥，你去保护娘娘！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[785]={nIndex=785,nCheckPointId=255,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊打～",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[786]={nIndex=786,nCheckPointId=255,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="快走。",sColor="0",bShake=false,sVoice="0",}
