ctCheckPointTalkConf[788]={nIndex=788,nCheckPointId=256,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="区区几个刺客，又能奈我何？让我出去！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[789]={nIndex=789,nCheckPointId=256,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="士兵甲",sBackground="bg_zhandou",sTalk="我等是奉了皇上的旨意，要保护好太子殿下的安全。恕不能从命！保护好太子殿下！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[790]={nIndex=790,nCheckPointId=257,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="士兵甲",sBackground="bg_zhandou",sTalk="太子殿下，我等已将刺客击溃，卑职这就去四处看一下，是否有同伙。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[791]={nIndex=791,nCheckPointId=257,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你们这群废物！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[792]={nIndex=792,nCheckPointId=258,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="怎么办",sColor="0",bShake=false,sVoice="0",}
