ctCheckPointTalkConf[796]={nIndex=796,nCheckPointId=259,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="张芃芃！张芃芃！",sColor="0",bShake=false,sVoice="0",}
