ctCheckPointTalkConf[798]={nIndex=798,nCheckPointId=260,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="把这条河给我搜遍了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[799]={nIndex=799,nCheckPointId=260,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="士兵甲",sBackground="bg_zhandou",sTalk="是",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[800]={nIndex=800,nCheckPointId=261,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="别跟着我，我去那边搜，你们往那边搜。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[801]={nIndex=801,nCheckPointId=261,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="张芃芃，你的命是我的，你不准死，我不许你死",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[802]={nIndex=802,nCheckPointId=262,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这里总算安全了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[803]={nIndex=803,nCheckPointId=262,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="喂，醒醒！醒醒！喂。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[804]={nIndex=804,nCheckPointId=262,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="不会是让老子做人工呼吸吧？不行！我得看他是不是装的！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[805]={nIndex=805,nCheckPointId=263,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="傻叉！傻叉！傻叉！傻叉！诶、有美女，穿着比基尼，下班了，下班了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[806]={nIndex=806,nCheckPointId=263,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="看来是真晕了。看在你救过老子的份上，老子的基友初吻献给你了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[807]={nIndex=807,nCheckPointId=263,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="咳！咳！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[808]={nIndex=808,nCheckPointId=264,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="亲就亲吗，伸什么舌头呀！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[809]={nIndex=809,nCheckPointId=264,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="迷迷糊糊之中，好像感觉嘴里有什么异物。 ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[810]={nIndex=810,nCheckPointId=264,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我看咱们俩这个样子，怕是不能继续走了。得找个隐蔽一点儿的地方，我们找个山林吧，先缓一缓，恢复元气了，再想办法。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[811]={nIndex=811,nCheckPointId=265,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="刚才是谁说水性好的？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[812]={nIndex=812,nCheckPointId=265,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="刚才在水里，手臂突然使不上力气了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[813]={nIndex=813,nCheckPointId=266,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="谢谢你 ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[814]={nIndex=814,nCheckPointId=266,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="我不忍心看你受伤。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[815]={nIndex=815,nCheckPointId=266,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="疼吗？忍着点儿",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[816]={nIndex=816,nCheckPointId=267,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你冷吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[817]={nIndex=817,nCheckPointId=267,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这么烫！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[818]={nIndex=818,nCheckPointId=268,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="（睡梦中地呢喃）芃芃，芃芃！……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[819]={nIndex=819,nCheckPointId=268,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你快醒醒啊！你不是号称英俊潇洒、风流倜傥吗？你如果就这么死了，后宫得那些宫女们，也都别活了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[820]={nIndex=820,nCheckPointId=269,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="（睡梦中地呢喃）芃芃、芃芃、危险、快走、快走、快走、芃芃……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[821]={nIndex=821,nCheckPointId=269,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（内心）齐晟不仅设计害我，遇刺时他也冷眼旁观，九王却舍命救我。由此看来，九王才更值得信任，要是能逃过这一劫，九王的提议倒是可以考虑！关键是，他要先醒过来啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[822]={nIndex=822,nCheckPointId=269,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（内心）兄弟！你快醒了吧！不管你是出于什么原因救我，但是今天你为兄弟我两肋插刀，这让我很感动，你要是能挺过这一次，今后，我没准儿就和你穿一条裤子了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[823]={nIndex=823,nCheckPointId=270,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="坏了！该换布条了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[824]={nIndex=824,nCheckPointId=270,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你醒了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[825]={nIndex=825,nCheckPointId=271,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你傻笑什么呢？不会是烧傻了吧？唉！还记得我是谁吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[826]={nIndex=826,nCheckPointId=271,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="真不记得了？我是你哥，以后在外面见到我管我叫我大哥，记住了吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[827]={nIndex=827,nCheckPointId=272,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="芃芃，我确实不认识你了，你做的这一切，都让我感到意外。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[828]={nIndex=828,nCheckPointId=272,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="原来没傻啊？哎~不过你做的这一切，也却是在我意料之外",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[829]={nIndex=829,nCheckPointId=272,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="依我所见，你已经成了齐晟欲罢不能的人，得不到，便要毁掉。想必来江北之前，我一直对你过于关切，忽略了齐晟安插的眼线",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[830]={nIndex=830,nCheckPointId=272,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="他早已对我们之间的来往了如指掌，定是他被惹怒了，才对你我起了杀心。这么说来，是本王忽略了自己的言行，害了你。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[831]={nIndex=831,nCheckPointId=273,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哇！好大一条鱼啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[832]={nIndex=832,nCheckPointId=273,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="额，你衣服呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[833]={nIndex=833,nCheckPointId=274,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你怎么在这？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[834]={nIndex=834,nCheckPointId=274,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="我……我也是为了躲避刺客，才落入水中的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[835]={nIndex=835,nCheckPointId=275,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这么说，是刺客赢了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[836]={nIndex=836,nCheckPointId=275,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="都这个时候了，你还有心情关心这个？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[837]={nIndex=837,nCheckPointId=275,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那当然了！就好像看电视剧不让我知道大结局，看足球比赛不告诉我几比几？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[838]={nIndex=838,nCheckPointId=275,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="是我们赢了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[839]={nIndex=839,nCheckPointId=276,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那你还弄得如此狼狈？你的那些侍卫们看着你就像看着天线宝宝一样吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[840]={nIndex=840,nCheckPointId=276,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="没想到下游水流湍急，又撞上了礁石，胳膊动弹不得，便漂到了这里。额！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[841]={nIndex=841,nCheckPointId=276,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="知道疼啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[842]={nIndex=842,nCheckPointId=276,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你撞一个试试？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[843]={nIndex=843,nCheckPointId=277,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="该不会是脱臼了吧，唉~我去给你找个树枝捆一捆吧。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[844]={nIndex=844,nCheckPointId=277,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="诶！你站住！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[845]={nIndex=845,nCheckPointId=278,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="芃芃！我怕你遇到野兽，就跟过来了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[846]={nIndex=846,nCheckPointId=278,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你猜我遇见谁了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[847]={nIndex=847,nCheckPointId=278,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="齐晟！他现在就在河边，你赶紧藏起来，这个时候你们俩还是不见面为好",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[848]={nIndex=848,nCheckPointId=279,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="怎么了？这个小树林里真的有野兽吗？呜~~是狗熊还是狮子？如果是狗熊咱们就在这装死，如果要是狮子你喊一二三，咱们就跑！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[849]={nIndex=849,nCheckPointId=279,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="不用跑了 ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[850]={nIndex=850,nCheckPointId=279,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（装傻）九王，好巧啊！你也在这儿？ 呵呵呵~。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[851]={nIndex=851,nCheckPointId=279,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="穿上！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[852]={nIndex=852,nCheckPointId=280,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="九弟可还好？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[853]={nIndex=853,nCheckPointId=280,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="并无大碍。不知三哥是否受了伤？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[854]={nIndex=854,nCheckPointId=280,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="他胳膊受伤了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[855]={nIndex=855,nCheckPointId=280,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="这样看来，我们还是先找个地方避一避吧，好让我帮你固定一下手臂。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[856]={nIndex=856,nCheckPointId=280,nTalkIndex=5,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="也好",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[857]={nIndex=857,nCheckPointId=281,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="（用树枝为齐晟固定手臂）",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[858]={nIndex=858,nCheckPointId=281,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[859]={nIndex=859,nCheckPointId=282,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="唉~我们不能坐在这儿等死吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[860]={nIndex=860,nCheckPointId=282,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="会有援兵来此处营救。不过是援兵先到，还是刺客先到，就无从而知了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[861]={nIndex=861,nCheckPointId=283,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="这次刺客来的蹊跷。难道，阜平水军中也混入了外人？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[862]={nIndex=862,nCheckPointId=283,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="军中守卫严密，若非对我们的行踪了如指掌，是不可能抓准了时机，趁虚而入的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[863]={nIndex=863,nCheckPointId=283,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="此次遭遇埋伏，很可能是身边人所为。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[864]={nIndex=864,nCheckPointId=284,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="没想到他竟然有胆量做出此事。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[865]={nIndex=865,nCheckPointId=284,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="我也是万万没想到，平日里真是小瞧了他。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[866]={nIndex=866,nCheckPointId=284,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="是谁？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[867]={nIndex=867,nCheckPointId=284,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="该不会是赵王吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[868]={nIndex=868,nCheckPointId=285,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="不可能！他那个怂蛋……也不是完全没有可能。他确实有作案动机，赵王的绿帽子带了那么多年，也太不容易了！能干出这件事情，可见真的是被逼急了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[869]={nIndex=869,nCheckPointId=285,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="绿帽子，还不明白什么意思是吧？我现在告诉你，绿帽子，特指自己的媳妇儿跟别人鬼混。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[870]={nIndex=870,nCheckPointId=286,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="张芃芃。你过来。",sColor="0",bShake=false,sVoice="0",}
