ctCheckPointTalkConf[943]={nIndex=943,nCheckPointId=309,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="哎呀~皇上~赵王~赵王他调戏我！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[944]={nIndex=944,nCheckPointId=309,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="呵呵呵~调戏你？照照你什么样~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[945]={nIndex=945,nCheckPointId=309,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="你，你来干什么！是来看我的笑话的吗？要杀要剐我随你便！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[946]={nIndex=946,nCheckPointId=310,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="我是来看你这烂泥扶不上墙的样子的！成何体统？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[947]={nIndex=947,nCheckPointId=310,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="我知道我自己时日已经不多了，所以我趁着现在我自由我潇洒！管不着我！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[948]={nIndex=948,nCheckPointId=310,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="谁告诉你你会死了？庸人自扰！还是先听旨吧。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[949]={nIndex=949,nCheckPointId=311,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="我不听",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[950]={nIndex=950,nCheckPointId=311,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="奉天承运皇帝诏曰，赵王因误信贼人之言，至使众多皇室贵胄（zhou）被困江北，当判死罪。当今圣上念其手足之情，免其死罪，免其一切官职，留其王位与宅院",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[951]={nIndex=951,nCheckPointId=311,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="手足之情,手足之情，免其死罪，免其一切官职，留其王位与宅院。哈哈哈~我呸我呸我呸我~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[952]={nIndex=952,nCheckPointId=311,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="钦此~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[953]={nIndex=953,nCheckPointId=312,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="我钦你个头啊我！你以为你不杀我，我就会感激你？我呸！我呸！我呸呸呸！呸！你这是诛心！比杀了我更狠！啊！ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[954]={nIndex=954,nCheckPointId=312,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="总有一天，你会明白朕的用心。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[955]={nIndex=955,nCheckPointId=312,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="我不明白！我不明白~我不明白~我永远不会明白~我不明白！我不明~~~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[956]={nIndex=956,nCheckPointId=313,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="唉~真不是我变娘了！这是面子问题！经过那几日的沧桑，我这张脸老了十岁！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[957]={nIndex=957,nCheckPointId=313,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您不用担心！人家都说了，这黄瓜片呀最管用了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[958]={nIndex=958,nCheckPointId=314,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎，遥远的从前，我对我的这张脸也是很在乎的，没了颜值，哪儿来的妞啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[959]={nIndex=959,nCheckPointId=314,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="一想到齐晟我就生气！非要把我关在那个破帐篷里那么久！绿篱，你知道我在里面过得是什么日子吗！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[960]={nIndex=960,nCheckPointId=314,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您是受苦了，不过呀咱们现在回来了，日子就好过了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[961]={nIndex=961,nCheckPointId=315,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哪里好过了？我实在是咽不下这口气！我现在找他理论去！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[962]={nIndex=962,nCheckPointId=315,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="唉~娘娘~娘娘~您的黄瓜片儿~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[963]={nIndex=963,nCheckPointId=316,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="齐晟~额~咳~我来了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[964]={nIndex=964,nCheckPointId=316,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕知道了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[965]={nIndex=965,nCheckPointId=317,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="呵呵~你倒挺会享受的啊！我劝你啊~还是先让这两位美女下去吧接下来我要说的话，恐怕让外人听见，会有损您的“龙颜”！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[966]={nIndex=966,nCheckPointId=317,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="不要紧，你说的话，这里除了朕，没有第三个人能听见。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[967]={nIndex=967,nCheckPointId=318,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那我可说了啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[968]={nIndex=968,nCheckPointId=318,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你把我整的人不人，鬼不鬼的，关在那个破帐篷里待了那么久，你自己却在这里逍遥自在啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[969]={nIndex=969,nCheckPointId=318,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕在江北时肩和胳膊都受了伤，这是太医吩咐的，请按摩师为朕按摩康健。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[970]={nIndex=970,nCheckPointId=319,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哪个男人去洗头房不是说自己去洗头的，去按摩院不是说自己去按摩的？啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[971]={nIndex=971,nCheckPointId=319,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="那你认为该是如何呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[972]={nIndex=972,nCheckPointId=319,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="要真如你所说的纯洁的关系，那也让这俩位按摩师也为我服务服务！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[973]={nIndex=973,nCheckPointId=320,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你、你、你、你！你还摸人家的小手？你该不会是想当着我的面儿？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[974]={nIndex=974,nCheckPointId=320,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="这两位师傅的按摩手艺非常高，只可惜她们看不见、听不见、也不会说话……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[975]={nIndex=975,nCheckPointId=320,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="可惜了这如花的面容……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[976]={nIndex=976,nCheckPointId=321,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="额~娘娘，您见着皇上了吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[977]={nIndex=977,nCheckPointId=321,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="见是见着了。别提了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[978]={nIndex=978,nCheckPointId=322,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="宝贝儿，我好像看到一个不明物体，你胆儿大不大？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[979]={nIndex=979,nCheckPointId=322,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="大！娘娘您说，哪儿有蟑螂，我,我帮您踩死！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[980]={nIndex=980,nCheckPointId=322,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="不是蟑螂……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[981]={nIndex=981,nCheckPointId=322,nTalkIndex=4,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="那是老鼠？娘娘，您说在哪儿，我，我闭着眼睛帮您踩死！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[982]={nIndex=982,nCheckPointId=323,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="报江北之仇的时候到了！我在里面受了那么多的苦，赵王他脱不了干系！这个仇我必须要报！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[983]={nIndex=983,nCheckPointId=323,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，你说怎么办？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[984]={nIndex=984,nCheckPointId=323,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="绿篱，给我找根棍子来！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[985]={nIndex=985,nCheckPointId=323,nTalkIndex=4,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="额~娘娘，我~胆儿小……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[986]={nIndex=986,nCheckPointId=324,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你不是特别讨厌江氏吗！现在我们就把他当成江氏，反正他们俩是夫妻，差不了多少。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[987]={nIndex=987,nCheckPointId=324,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，用不着棍子，我一个人抵十个棍子！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[988]={nIndex=988,nCheckPointId=324,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="上！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[989]={nIndex=989,nCheckPointId=324,nTalkIndex=4,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="啊——",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[990]={nIndex=990,nCheckPointId=324,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎呦我去！偷袭你还喊这么大声！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[991]={nIndex=991,nCheckPointId=325,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="哎，哎……别打了别打了，我找我媳妇呢，你打我干嘛！我招谁惹谁了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[992]={nIndex=992,nCheckPointId=325,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="管你找谁呢！说打你就打你！啊~~~~~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[993]={nIndex=993,nCheckPointId=326,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="手下留人！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[994]={nIndex=994,nCheckPointId=326,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="娘娘，您这什么意思啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[995]={nIndex=995,nCheckPointId=326,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="没什么意思，就是看你不顺眼，揍你一顿出出气。听说你在找媳妇儿，怎么回事？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[996]={nIndex=996,nCheckPointId=326,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="我自从江北回来以后，每天都醉得不省人事，突然有一天醒来，我媳妇儿就没了，他们都说映月已经暴毙而亡！这简直是一派胡言！她肯定是跟三弟一块走了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[997]={nIndex=997,nCheckPointId=327,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="我心里就一直不明白，我除了不是太子之外，我哪里不如三弟了？我如果身份可以和他换一下的话，她还会如此么？所以我就在想在江北把你们一举拿下，除掉我所有的竞争对手！更为了我这么多年受的窝囊气报仇……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[998]={nIndex=998,nCheckPointId=327,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="果然如此。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[999]={nIndex=999,nCheckPointId=327,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你也不容易。现在我终于明白，原来你不是怂啊，你是憋着一股劲呢！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1000]={nIndex=1000,nCheckPointId=328,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="她怎么想的我可不知道。但你是怎么想的，我是彻底明白了。是个男人遇到你这种事儿，十有八九都会闹出人命来！你这么做，我理解。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1001]={nIndex=1001,nCheckPointId=328,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="娘娘，你人真好……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1002]={nIndex=1002,nCheckPointId=328,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，娘娘我可一直等着呢，这人还打不打了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1003]={nIndex=1003,nCheckPointId=328,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这人是一定要打的！交情归交情，打仗归打仗！绿篱，给我狠狠地揍！一定要把江北之仇给我报回来！上！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1004]={nIndex=1004,nCheckPointId=328,nTalkIndex=5,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="啊！！！！！！！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1005]={nIndex=1005,nCheckPointId=328,nTalkIndex=6,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="啊~啊~你怎么又打我啦？！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1006]={nIndex=1006,nCheckPointId=329,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这人是打爽了，可是我这心里啊还是不踏实……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1007]={nIndex=1007,nCheckPointId=329,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您还有什么事儿记挂在心上呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1008]={nIndex=1008,nCheckPointId=330,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="齐晟没有依仗张家的一兵一卒，就当上了皇帝！他现在更没有什么可忌惮的了！我能不能封后可就不一定了……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1009]={nIndex=1009,nCheckPointId=330,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您先别担心呀~皇上才刚刚登基，有很多地方还是用得着咱们张家的，您呢只需要耐心等待封后便可。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1010]={nIndex=1010,nCheckPointId=331,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="齐晟是怎么想的，我心里确实没底。更何况，我和他的关系一向不和，这也是人尽皆知的！唉~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1011]={nIndex=1011,nCheckPointId=331,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="照您这么说，奴婢也觉得不对劲了，都这么多天了，怎么还是不见皇上的踪影呢！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1012]={nIndex=1012,nCheckPointId=331,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="唉唉唉~说曹操曹操就到",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1013]={nIndex=1013,nCheckPointId=331,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="芃芃，跟我走！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1014]={nIndex=1014,nCheckPointId=331,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="欸欸，怎么又这样呢",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1015]={nIndex=1015,nCheckPointId=332,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="给皇上请安！给太子妃娘娘请安！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1016]={nIndex=1016,nCheckPointId=332,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="没长脑子吗？这是皇后！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1017]={nIndex=1017,nCheckPointId=332,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="皇后娘娘恕罪，奴才有眼无珠。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1018]={nIndex=1018,nCheckPointId=332,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="唉唉~没事没事没事，这不是还没举行封后大典嘛！心意到了就好了啊。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1019]={nIndex=1019,nCheckPointId=333,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="唉，你该不会真的封我为后吧?",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1020]={nIndex=1020,nCheckPointId=333,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="不封你还能封谁！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1021]={nIndex=1021,nCheckPointId=333,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="呵呵",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1022]={nIndex=1022,nCheckPointId=334,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="她就在里面，她吃了很多的苦，是朕有负于她。你说的她……难道是江氏？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1023]={nIndex=1023,nCheckPointId=334,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="她没死？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1024]={nIndex=1024,nCheckPointId=334,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="唉~可怜的赵王啊~你是没有看到他那样子，惨啊，我都看不下去",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1025]={nIndex=1025,nCheckPointId=334,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你见过他了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1026]={nIndex=1026,nCheckPointId=334,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="没有。我也是听人说的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1027]={nIndex=1027,nCheckPointId=335,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="赵王，是朕愧对于他在先，可江北一事，他也的确是过了界。这次朕没有重罚他，也是想给他留条后路。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1028]={nIndex=1028,nCheckPointId=335,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="要我说重不重罚不重要，现在呢你也是大仇已报，解了心头之恨就行了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1029]={nIndex=1029,nCheckPointId=335,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕是因为赵王那个烂泥扶不上墙的样子，实在是不能再照顾映月，她又一直体弱多病，朕怕她在赵王府再待下去，不是被赵王逼疯，也会心力憔悴而死，所以才会……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1030]={nIndex=1030,nCheckPointId=335,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="所以你才想了个辙，把她接进了宫？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1031]={nIndex=1031,nCheckPointId=335,nTalkIndex=5,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你已经猜到了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1032]={nIndex=1032,nCheckPointId=335,nTalkIndex=6,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你这不正在征求我的意见，想要金屋藏娇吗？我理解！你想做什么呢，放心大胆的去做！我支持你！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1033]={nIndex=1033,nCheckPointId=336,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="其实，我只是想让你容得下她，给她一条活路",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1034]={nIndex=1034,nCheckPointId=336,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="容！绝对容得下！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1035]={nIndex=1035,nCheckPointId=336,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（心道）你小子那么快地就封我为后，原来是给自己留着备胎呢！哼~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1036]={nIndex=1036,nCheckPointId=336,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎呀~这儿蚊子还不少呢，要不咱们进去吧，别让人家姑娘等久了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1037]={nIndex=1037,nCheckPointId=337,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="咳咳咳咳咳",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1038]={nIndex=1038,nCheckPointId=337,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我对这柔弱的声音，一向没有抵抗力！这不是逼着我犯错吗！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1039]={nIndex=1039,nCheckPointId=337,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="唉~妹妹，妹妹病的这么严重，就别起来了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1040]={nIndex=1040,nCheckPointId=337,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="终于可以占她的便宜了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1041]={nIndex=1041,nCheckPointId=338,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎呀~妹妹~瞧瞧这小脸儿，鹅蛋脸都瘦成锥子脸了。这身材，太骨感了，啊~还有这大长腿，又白又直，别说是皇上，就连我都爱不释手啊！ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1042]={nIndex=1042,nCheckPointId=338,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="妹妹~你受了这么多的苦，知道哥的心里有多疼么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1043]={nIndex=1043,nCheckPointId=339,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你这是做什么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1044]={nIndex=1044,nCheckPointId=339,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我这是在心疼妹妹！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1045]={nIndex=1045,nCheckPointId=339,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你还是出去等着吧！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1046]={nIndex=1046,nCheckPointId=340,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="姐姐刚才的态度真是反常，演的好一出姐妹情深啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1047]={nIndex=1047,nCheckPointId=340,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="难道世间就不存在真正的姐妹情了吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1048]={nIndex=1048,nCheckPointId=341,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="皇上误会了，妾身是觉得，这段时间，姐姐和从前大不相同了，刚才姐姐对臣妾太过热情，妾身一时之间还无法适应。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1049]={nIndex=1049,nCheckPointId=341,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="她的确和以前不太一样了……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1050]={nIndex=1050,nCheckPointId=342,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="咳咳咳~自从陛下从江北回来，就没来看过妾身。现在妾身病了，陛下才带着姐姐来了，陛下是不是觉得，姐姐比我有趣、比我新鲜？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1051]={nIndex=1051,nCheckPointId=342,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕没有那样想。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1052]={nIndex=1052,nCheckPointId=342,nTalkIndex=3,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="新鲜不过是一时的，陛下与映月可是有一生一世之约的……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1053]={nIndex=1053,nCheckPointId=343,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="妾身想知道，陛下把臣妾接进宫，只是为了想让妾身养病？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1054]={nIndex=1054,nCheckPointId=343,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="赵王那副样子，你在赵王府再呆下去，恐怕只会加重你的病情。朕若不把你接进宫中，只怕你性命会难保。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1055]={nIndex=1055,nCheckPointId=344,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="妾身知道陛下是在疼妾身",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1056]={nIndex=1056,nCheckPointId=344,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕把你接进宫中，只是想保住你的性命，并无它意。总之，是朕对不住你。这些日子，朕对你、对芃芃的感情都在发生变化，朕发现……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1057]={nIndex=1057,nCheckPointId=344,nTalkIndex=3,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="咳咳咳~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1058]={nIndex=1058,nCheckPointId=344,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你既然身体不适，那便改日再说吧。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1059]={nIndex=1059,nCheckPointId=345,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="呵呵~啊啊啊~麻了麻了~哎呀~哎呦~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1060]={nIndex=1060,nCheckPointId=345,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你马上就是要做皇后的人了，要随时随地端庄优雅！不要像个山野村夫一样粗鲁？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1061]={nIndex=1061,nCheckPointId=346,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你们俩在里面一个坐着一个躺着，我哪有地儿藏有地儿躲啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1062]={nIndex=1062,nCheckPointId=346,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你既然累了，为何不进去？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1063]={nIndex=1063,nCheckPointId=346,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我哪敢进去？你们俩个人卿卿我我、你侬我侬，再说了，我怕自己把持不住！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1064]={nIndex=1064,nCheckPointId=347,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="回去吧。走啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1065]={nIndex=1065,nCheckPointId=347,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊~麻了~麻了麻了麻了~哎呦~麻了麻了~",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1066]={nIndex=1066,nCheckPointId=348,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="呵呵~娘娘！该起床了！这封后大典可是一辈子就一次的事儿！这可不能怠慢了呀！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1067]={nIndex=1067,nCheckPointId=348,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="唉~原来是册封大典啊！ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1068]={nIndex=1068,nCheckPointId=349,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘！您的眼光什么时候变得这么挑剔了呀？以前奴婢挑的东西，没有一样不是您可心儿的！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1069]={nIndex=1069,nCheckPointId=349,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="难怪张芃芃以前不受宠啊……就是因为你这个设计师啊~品位太low！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1070]={nIndex=1070,nCheckPointId=349,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="low?",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1071]={nIndex=1071,nCheckPointId=349,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="绿篱，不是我眼光高，因为你啊是彻彻底底的女人的审美，你根本就不知道男人喜欢什么！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1072]={nIndex=1072,nCheckPointId=349,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你看啊，这支就比你刚刚挑的那支好看，简单而不失大方，优雅而不失身份，重要的是，还带着一点点的骚气。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1073]={nIndex=1073,nCheckPointId=349,nTalkIndex=6,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="骚气？",sColor="0",bShake=false,sVoice="0",}
