ctCheckPointTalkConf[1170]={nIndex=1170,nCheckPointId=382,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（心道）我还是先撤吧。哎呀！哎呀！哎呀！哎呀！",sColor="0",bShake=false,sVoice="0",}
