ctCheckPointTalkConf[1172]={nIndex=1172,nCheckPointId=383,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="太后",sBackground="bg_zhandou",sTalk="芃芃？芃芃？人呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1173]={nIndex=1173,nCheckPointId=383,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="回禀太皇太后，娘娘…娘娘好像身体不适，奴婢刚刚看到娘娘捂着肚子跑出去了！",sColor="0",bShake=false,sVoice="0",}
