ctCheckPointTalkConf[1217]={nIndex=1217,nCheckPointId=401,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕下了早朝便来看你了，你可还好？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1218]={nIndex=1218,nCheckPointId=401,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="妾身并无大碍，现在陛下来了，妾身就感觉好很多了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1219]={nIndex=1219,nCheckPointId=401,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你放心养好身子便可，其它的事情，你不必想太多",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1220]={nIndex=1220,nCheckPointId=402,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="映月还有个不情之请。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1221]={nIndex=1221,nCheckPointId=402,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你说便是",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1222]={nIndex=1222,nCheckPointId=403,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="这几日……我时不常便会难受，看见陛下就会好些，还希望陛下能多抽些空闲，到幽兰殿里来看我。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1223]={nIndex=1223,nCheckPointId=403,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你既然提了，朕又怎么会不来看你呢。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1224]={nIndex=1224,nCheckPointId=403,nTalkIndex=3,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="妾身说的，可不是一朝一夕，而是日日如此",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1225]={nIndex=1225,nCheckPointId=404,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="这是朕的责任，朕若是拒绝你的要求，倒显得真是个薄情寡义之人了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1226]={nIndex=1226,nCheckPointId=404,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="自映月七岁那年，第一次见到陛下，就知道陛下是个有情有义之人，现在虽然陛下繁冗之事缠身，但是映月知道，毕竟陛下与映月，有着这么多年的情……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1227]={nIndex=1227,nCheckPointId=404,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="过去的事情，就不要再提了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1228]={nIndex=1228,nCheckPointId=405,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="陛下肯来陪着我，妾身就放心了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1229]={nIndex=1229,nCheckPointId=405,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕还有很多政务要处理，明日再来看你",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1230]={nIndex=1230,nCheckPointId=406,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="挂在哪呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1231]={nIndex=1231,nCheckPointId=406,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，都说财不外现，咱们还是把这个金子藏起来吧！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1232]={nIndex=1232,nCheckPointId=406,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="宝贝儿，这你又不懂了吧？这叫卧薪尝胆！这东西啊会时刻提醒我的人生目标，做我的指路明灯！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1233]={nIndex=1233,nCheckPointId=407,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘、娘娘、",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1234]={nIndex=1234,nCheckPointId=407,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="干什么呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1235]={nIndex=1235,nCheckPointId=407,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这个灯歪了、呵",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1236]={nIndex=1236,nCheckPointId=407,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕在你这儿坐会儿便走",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1237]={nIndex=1237,nCheckPointId=407,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="怎么着？昨晚没睡好？跑我这儿补回笼觉来了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1238]={nIndex=1238,nCheckPointId=408,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你说话能不能不要如此夹枪带棒的？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1239]={nIndex=1239,nCheckPointId=408,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="对不起啊，皇上，如果你喜欢听那种撒娇妩媚的话，麻烦您出门左转幽兰殿去。这个我还真不会。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1240]={nIndex=1240,nCheckPointId=408,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕，本来是想向你道歉，昨晚确实是有要紧事突然离开，是迫不得已。你既然如此，朕这就走了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1241]={nIndex=1241,nCheckPointId=408,nTalkIndex=4,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您怎么能这样跟皇上说话呀！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1242]={nIndex=1242,nCheckPointId=408,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="他先不仁，我要还讲义，我就是天下第一大小白！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1243]={nIndex=1243,nCheckPointId=409,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="疼吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1244]={nIndex=1244,nCheckPointId=409,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="太医已经处理过伤口了，妾身现在已经好多了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1245]={nIndex=1245,nCheckPointId=410,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你为何要做这样伤害自己的事？ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1246]={nIndex=1246,nCheckPointId=410,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="妾身觉得，对不起陛下。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1247]={nIndex=1247,nCheckPointId=410,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="此话怎讲？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1248]={nIndex=1248,nCheckPointId=410,nTalkIndex=4,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="臣妾怀了陛下的孩子，太医说，已经有三个多月的身孕了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1249]={nIndex=1249,nCheckPointId=411,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="是映月对不起陛下，妾身知道，现在这个时候来这么个孩子，就是给陛下添麻烦，所以妾身才想，不如和这孩子一起死了算了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1250]={nIndex=1250,nCheckPointId=411,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你这说的是什么话！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1251]={nIndex=1251,nCheckPointId=411,nTalkIndex=3,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="映月明白，陛下留映月在这宫中调养，已是极大的恩赐，可如今这个孩子……是所有人都容不下的呀！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1252]={nIndex=1252,nCheckPointId=412,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你放心，朕定会尽力保全这个孩子。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1253]={nIndex=1253,nCheckPointId=412,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="陛下保了他又能如何，他活在这个世上，只是一个无名无分，连和自己的爹都无法相认的可怜的孩子……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1254]={nIndex=1254,nCheckPointId=412,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你放心，既是朕的骨肉，朕定不会让他受一点委屈。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1255]={nIndex=1255,nCheckPointId=413,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="若是这孩子真的有了名分，姐姐知道了这件事情，会不会心里过不去呢，是妾身对不起姐姐，可是这孩子，毕竟是妾身的亲生骨肉，妾身也不能对不起自己的孩子呀",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1256]={nIndex=1256,nCheckPointId=413,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="哎，皇后那里，朕会亲自去说的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1257]={nIndex=1257,nCheckPointId=413,nTalkIndex=3,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="听见陛下这么说，妾身才能勉强安下心。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1258]={nIndex=1258,nCheckPointId=414,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你好生休养，朕还有要事，先走了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1259]={nIndex=1259,nCheckPointId=414,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="不知陛下，明日是否能来看妾身和孩子呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1260]={nIndex=1260,nCheckPointId=414,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="（没有说话走了出去）",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1261]={nIndex=1261,nCheckPointId=415,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="皇……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1262]={nIndex=1262,nCheckPointId=415,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="不必通报，朕这就走了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1263]={nIndex=1263,nCheckPointId=416,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="您不进去了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1264]={nIndex=1264,nCheckPointId=416,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕不知道该如何向她解释。他知道了一定会很伤心，就让她再无忧、快乐的过几日吧",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1265]={nIndex=1265,nCheckPointId=417,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="两只小蜜蜂呀，飞到花丛中呀，飞呀，嘛嘛，飞呀，啪啪、飞呀…",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1266]={nIndex=1266,nCheckPointId=417,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="两只小蜜蜂呀，飞到花丛中呀，飞呀，嘛嘛，飞呀，啊啊、飞呀…",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1267]={nIndex=1267,nCheckPointId=418,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="宋太医驾到、",sColor="0",bShake=false,sVoice="0",}
