ctCheckPointTalkConf[1269]={nIndex=1269,nCheckPointId=418,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您忘了呀？咱们宫里，太医要定期例行为娘娘把脉，看一下可否有隐藏的病症。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1270]={nIndex=1270,nCheckPointId=418,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="把他叫进来吧。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1271]={nIndex=1271,nCheckPointId=419,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="宋太医，您这边请。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1272]={nIndex=1272,nCheckPointId=419,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="微臣给皇后娘娘请安、",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1273]={nIndex=1273,nCheckPointId=420,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="小宋，看你这表情，我是不是病的很复杂？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1274]={nIndex=1274,nCheckPointId=420,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="恭喜皇后娘娘，娘娘这是喜脉啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1275]={nIndex=1275,nCheckPointId=420,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1276]={nIndex=1276,nCheckPointId=421,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你丫说什么呢你？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1277]={nIndex=1277,nCheckPointId=421,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="娘娘确实有喜了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1278]={nIndex=1278,nCheckPointId=422,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="哎呀！我们家娘娘有喜啦！我们家娘娘有喜啦！我们家娘娘有喜啦！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1279]={nIndex=1279,nCheckPointId=422,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="绿篱，你滚一边抽抽去、",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1280]={nIndex=1280,nCheckPointId=422,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="奴婢这就滚，我们家娘娘有喜啦！我们家娘娘有喜啦！我们家娘娘有喜啦！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1281]={nIndex=1281,nCheckPointId=423,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="小宋，诶，我这个月事啊向来都不准，你是不是搞错了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1282]={nIndex=1282,nCheckPointId=423,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="阴搏阳别，寸脉沉，尺脉浮，鼓动滑利，阴中见阳，又有和调之象，娘娘确是有孕的脉像。为保万全，老臣再给娘娘开些安胎药，调养一下。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1283]={nIndex=1283,nCheckPointId=423,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你给我 滚～",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1284]={nIndex=1284,nCheckPointId=423,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="微臣这就滚，啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1285]={nIndex=1285,nCheckPointId=424,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="滚滚滚滚滚滚",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1286]={nIndex=1286,nCheckPointId=424,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我怎么没有发现呢！难道我是男人，没感觉？！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1287]={nIndex=1287,nCheckPointId=425,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您该起来啦、快起来吃点儿东西。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1288]={nIndex=1288,nCheckPointId=425,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我已经没脸面在这个世界了，让我再逃避一会儿吧。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1289]={nIndex=1289,nCheckPointId=426,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="那、那我可先走喽，娘娘，我先下去了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1290]={nIndex=1290,nCheckPointId=426,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="站住！你手里拿着什么东西？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1291]={nIndex=1291,nCheckPointId=426,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="没，没什么。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1292]={nIndex=1292,nCheckPointId=427,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，娘娘真的没什么，是张家托人送来的东西。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1293]={nIndex=1293,nCheckPointId=427,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="拿出来给我看看，拿出来给我看看",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1294]={nIndex=1294,nCheckPointId=427,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，这是张家托了城里最好的裁缝，专门做的小孩的衣服。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1295]={nIndex=1295,nCheckPointId=427,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="张家？这是什么意思？我前脚刚怀孕，后脚衣服都做出来了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1296]={nIndex=1296,nCheckPointId=427,nTalkIndex=5,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您不是常说……有备无患嘛？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1297]={nIndex=1297,nCheckPointId=427,nTalkIndex=6,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="到底是什么情况？给我说清楚了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1298]={nIndex=1298,nCheckPointId=428,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘……我真没打算骗您，只是这事要是提前跟您说了，您定是不会答应的，所以才生米煮成了熟饭……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1299]={nIndex=1299,nCheckPointId=428,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="说下去！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1300]={nIndex=1300,nCheckPointId=428,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="我说了娘娘您可千万要受住了，万不可再做出什么傻事来！其实娘娘并未有怀孕，那张太医是家里买通了，才说娘娘怀孕的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1301]={nIndex=1301,nCheckPointId=428,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="什么，我没怀孕，太好了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1302]={nIndex=1302,nCheckPointId=429,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那你快和我说说，家里人为什么要这么做？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1303]={nIndex=1303,nCheckPointId=429,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="前几日幽兰殿又召了太医，家里觉得蹊跷，便暗中托了人询问，谁知道江氏那小贱人竟然被诊出了身孕来。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1304]={nIndex=1304,nCheckPointId=430,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="江氏怀孕了？难怪前段时间皇上去幽兰殿，原来是为了这事儿？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1305]={nIndex=1305,nCheckPointId=430,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您可一定要忍啊，就算是打落了牙齿也要和着血往肚里咽！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1306]={nIndex=1306,nCheckPointId=431,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你再说一遍，怀孕的不是我，是江氏？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1307]={nIndex=1307,nCheckPointId=431,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="家里人怕江氏有了身孕便可肆意妄为，于是便收买了张太医，将娘娘也把出喜脉来。这样一来，找个机会除掉江氏的孩子！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1308]={nIndex=1308,nCheckPointId=431,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你们真的太狠了！真是最毒不过妇人心啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1309]={nIndex=1309,nCheckPointId=432,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，家里人知道您性子烈，怕事先告诉了您，没准啊您会打到幽兰殿去，到时候再惹得皇上厌弃。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1310]={nIndex=1310,nCheckPointId=432,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="以后这种事情千万要与我先商量。假怀孕是正房该干的的事儿吗？到时候玩儿现了，会不会也害我呀！你赶快去跟夫人传个话，下面我要说的，你都记清楚了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1311]={nIndex=1311,nCheckPointId=432,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘您说。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1312]={nIndex=1312,nCheckPointId=432,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="第一，江氏肚子里的孩子必须保住！这件事情我另有安排。第二，齐晟不是傻子，赶紧叫家里想办法，把我肚子里的这个假货，名正言顺的拿出去！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1313]={nIndex=1313,nCheckPointId=433,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="皇上吉祥。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1314]={nIndex=1314,nCheckPointId=433,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="不必多礼。你怀孕的事，太医已经告诉朕了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1315]={nIndex=1315,nCheckPointId=433,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="就算太医不跟你说啊你也知道了，今天早上绿篱啊在我宫门口发羊角疯，闹得沸沸扬扬的整个宫都知道了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1316]={nIndex=1316,nCheckPointId=434,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="有什么吃穿用度上的需要，尽管和内务府提。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1317]={nIndex=1317,nCheckPointId=434,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="皇上，您也别因为我有了身孕，就冷落了幽兰殿那边儿，毕竟，那边也需要您啊",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1318]={nIndex=1318,nCheckPointId=435,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你知道了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1319]={nIndex=1319,nCheckPointId=435,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="诶～皇宫啊就这么大，有什么事情能瞒得住啊？再说了，这又不是什么坏事，我们大家都为您高兴呢！是吧，强公公？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1320]={nIndex=1320,nCheckPointId=435,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你这是在说反话吧？朕并不是有意要瞒着你的，只是那天你的态度，令朕实在无法开口",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1321]={nIndex=1321,nCheckPointId=436,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="皇上，我可是真心实意的为你高兴！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1322]={nIndex=1322,nCheckPointId=436,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你高兴什么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1323]={nIndex=1323,nCheckPointId=436,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我为皇上高兴，为江妹妹高兴，也为我自己高兴!",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1324]={nIndex=1324,nCheckPointId=436,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="替你自己高兴又是为什么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1325]={nIndex=1325,nCheckPointId=436,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（内心）因为怀孕的不是我啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1326]={nIndex=1326,nCheckPointId=437,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="皇上，你看哈，如果我跟江妹妹，各生一男一女那就太好了，将来也是个伴儿，皇上呢也早日儿女双全了！所以我是真心实意为你开心啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1327]={nIndex=1327,nCheckPointId=437,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="别说了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1328]={nIndex=1328,nCheckPointId=437,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="皇上，你可千万别因为我就冷落了江妹妹，你就大胆放心的去照顾她。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1329]={nIndex=1329,nCheckPointId=438,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘您没事吧，有没有伤到，您快让我看看。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1330]={nIndex=1330,nCheckPointId=438,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="没事，齐晟啊，这是在和自己发泄呢。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1331]={nIndex=1331,nCheckPointId=438,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="皇上真的没有打您吗？刚才动静那么大？快让我好好看看",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1332]={nIndex=1332,nCheckPointId=439,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="嘿嘿～娘娘、娘娘、您看这小衣服多可爱啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1333]={nIndex=1333,nCheckPointId=439,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="可爱！可爱有什么用啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1334]={nIndex=1334,nCheckPointId=439,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="您、真就不打算自己生一个？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1335]={nIndex=1335,nCheckPointId=439,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我？让我生孩子还不如让我去死！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1336]={nIndex=1336,nCheckPointId=440,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎呀、我这怀孕的消息一传出去，怕是要几家欢喜几家愁啊。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1337]={nIndex=1337,nCheckPointId=440,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="可不是吗，江氏那头呀肯定恨死娘娘了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1338]={nIndex=1338,nCheckPointId=440,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我说的不是她。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1339]={nIndex=1339,nCheckPointId=440,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="江氏是怎么想的，我真的没有心思去猜。只是九王这一次，一定是受到很大的困扰，我与他刚立下盟约，就马上传出来我怀孕的消息，换做是谁，恐怕都难以接受啊。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1340]={nIndex=1340,nCheckPointId=441,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="呦，好巧哈！九王混得可还不错？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1341]={nIndex=1341,nCheckPointId=441,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="确实不错，这宫中的大小茅厕都归我管……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1342]={nIndex=1342,nCheckPointId=442,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="原来你成了“茅厕君”啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1343]={nIndex=1343,nCheckPointId=442,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="哎、这也是无奈之举啊。自从上次晋升后，皇上就总让我管理宫中的鸡毛蒜皮之事，我心中，也是愤懑至极啊……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1344]={nIndex=1344,nCheckPointId=443,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="对了，有关我怀孕一事……其实是张家人的意思，我并没有怀孕，你我二人之间的盟约，也不会受到任何的影响。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1345]={nIndex=1345,nCheckPointId=443,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="我了解。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1346]={nIndex=1346,nCheckPointId=444,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="九王真是料事如神啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1347]={nIndex=1347,nCheckPointId=444,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那我再说一件你绝对不知道的事！江映月怀了齐晟的孩子！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1348]={nIndex=1348,nCheckPointId=444,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="我已知道此事",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1349]={nIndex=1349,nCheckPointId=445,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那九王对此件，有什么看法？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1350]={nIndex=1350,nCheckPointId=445,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="那就略陈拙见了。本王以为，江氏现在身份见不了光，但只要生下皇子，齐晟为了不委屈那孩子，必然会叫江氏改头换面……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1351]={nIndex=1351,nCheckPointId=446,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="这一点正是我们可以利用的。只要皇上这样做，届时张家与本王借着齐晟“有悖纲常”齐齐发难，想必会一呼百应……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1352]={nIndex=1352,nCheckPointId=446,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你说的太对了！江氏是容不下我做皇后的。她早晚会给齐晟吹枕边风，取代我的后位。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1353]={nIndex=1353,nCheckPointId=447,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="可到那时，满朝文武大臣、皇亲国戚，哪一个又会容下这个蛊惑皇上、乱了纲常的女人啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1354]={nIndex=1354,nCheckPointId=447,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="九王，如果我是宇宙智商第一的话，那你就是老二了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1355]={nIndex=1355,nCheckPointId=447,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="虽然你说的话我听不太懂，但是现在还是觉得挺有意思的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1356]={nIndex=1356,nCheckPointId=448,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="现在的重中之重就是保住江氏的孩子！我得赶紧和张家通个气，让他们万万不能动了江氏的孩子。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1357]={nIndex=1357,nCheckPointId=448,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="九王",sBackground="bg_zhandou",sTalk="是，我们现在什么都不需要做，只需看着齐晟一路错下去，一定会抓住他的把柄",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1358]={nIndex=1358,nCheckPointId=449,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，该喝药了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1359]={nIndex=1359,nCheckPointId=449,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="先把宋太医的药停了吧，咱们啊是时候该计划一下，出演那场出流产的戏了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1360]={nIndex=1360,nCheckPointId=450,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您可真想好了？要是江氏真生了皇子可就……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1361]={nIndex=1361,nCheckPointId=450,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我现在就是要力保她顺顺利利地生下皇子！谁要是敢坏了她生孩子的大事，我跟谁急！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1362]={nIndex=1362,nCheckPointId=450,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="那就等您月事一来，咱们就报小产。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1363]={nIndex=1363,nCheckPointId=451,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="嗯。哎呀，这个撒谎的最高境界就是连自己都信了！到时候只要咱们自己入戏，齐晟他不信也得信！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1364]={nIndex=1364,nCheckPointId=451,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="把宋太医叫来，我也好久没见他了，跟他叙叙旧。 ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1365]={nIndex=1365,nCheckPointId=452,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="奴婢去把宋太医请来吧。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1366]={nIndex=1366,nCheckPointId=452,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎、去吧去吧。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1367]={nIndex=1367,nCheckPointId=453,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="啊、啊、娘娘、娘娘，娘娘。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1368]={nIndex=1368,nCheckPointId=453,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="姑娘家家的，注意一下仪态！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1369]={nIndex=1369,nCheckPointId=454,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="不好了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1370]={nIndex=1370,nCheckPointId=454,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="什么呀不好了！宋太医人呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1371]={nIndex=1371,nCheckPointId=454,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="我刚刚路过幽兰殿，发现幽兰殿里已经乱成一锅粥了！说是太皇太后知道了幽兰殿的事，震怒，已经吩咐嬷嬷带着药去了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1372]={nIndex=1372,nCheckPointId=455,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="什么药啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1373]={nIndex=1373,nCheckPointId=455,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="那种药！落胎的！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1374]={nIndex=1374,nCheckPointId=456,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你不早说！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1375]={nIndex=1375,nCheckPointId=456,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，娘娘，注意仪态，仪态啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1376]={nIndex=1376,nCheckPointId=457,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你喝了？！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1377]={nIndex=1377,nCheckPointId=457,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你怎么就这么着急啊！你再等哥一会儿，哥不就来救你啦？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1378]={nIndex=1378,nCheckPointId=458,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="你救我？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1379]={nIndex=1379,nCheckPointId=458,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我不救你救谁啊？哎呀，这都憔悴成什么样了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1380]={nIndex=1380,nCheckPointId=459,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="乖，想哭就哭吧！哥是你可以依靠的肩膀！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1381]={nIndex=1381,nCheckPointId=459,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="姐姐，你这是做什么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1382]={nIndex=1382,nCheckPointId=459,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="妹妹，我当然是关心你啊！这种事情我不是没遇到过，哎，我知道，刚掉了孩子的女人最需的就是关心和照顾！来吧，给一个爱的抱抱！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1383]={nIndex=1383,nCheckPointId=460,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="你别再这么假惺惺的了，这没有外人，你装给谁看？我告诉你也无妨，这药，我喝与不喝都没什么两样。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1384]={nIndex=1384,nCheckPointId=460,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="什么没两样！对我来说完全不一样！虽然你肚里的孩子不是我的，但孩子没了，我可是最伤心的人！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1385]={nIndex=1385,nCheckPointId=460,nTalkIndex=3,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="够了！你不要再装了！我现在就告诉你！其实我根本就没有怀孕。这只不过是我挽留皇上走的一步棋而已。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1386]={nIndex=1386,nCheckPointId=461,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你说什么？你没怀孕？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1387]={nIndex=1387,nCheckPointId=461,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="我赌定依照姐姐的性格，定会置我于死地，这样一来，皇上就会站到我这一边。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1388]={nIndex=1388,nCheckPointId=461,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="说你傻你还的傻啊，我关心你都来不及，我怎么会害你呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1389]={nIndex=1389,nCheckPointId=461,nTalkIndex=4,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="正是你这种假情假意，害得我走到如此地步！想不到，你对我百般热情和嘘寒问暖，反而让皇上高看你一眼。我现在已经是无计可施了……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1390]={nIndex=1390,nCheckPointId=462,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你无计可施了？你这一假怀孕，我、我比你更无计可施了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1391]={nIndex=1391,nCheckPointId=462,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="行了行了！别哭了！齐晟不还老来你这儿吗！以后把握机会就是了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1392]={nIndex=1392,nCheckPointId=462,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我好心劝你啊，男人都是自由的鸟儿，最烦被束缚。如果你想和你的晟晟一生一世一双人，以后不要再用这种方式了。哎呀。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1393]={nIndex=1393,nCheckPointId=463,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，怎么样了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1394]={nIndex=1394,nCheckPointId=463,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="江氏她没怀孕。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1395]={nIndex=1395,nCheckPointId=463,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="啊，您说什么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1396]={nIndex=1396,nCheckPointId=463,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="她是用了小三的惯用伎俩之假怀孕，为了拢络齐晟的心。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1397]={nIndex=1397,nCheckPointId=464,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，既然江氏她没怀孕，您怎么还失望了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1398]={nIndex=1398,nCheckPointId=464,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="江映月生不下孩子，我用什么借口来搞掉齐晟？现在看来，之前的所有计划都无法实施了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1399]={nIndex=1399,nCheckPointId=464,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，来日方长，咱们以后再想办法吧。我们先回宫吧，太医已经在那候着了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1400]={nIndex=1400,nCheckPointId=464,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="也只能这样了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1401]={nIndex=1401,nCheckPointId=465,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="小宋，别睡了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1402]={nIndex=1402,nCheckPointId=465,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="宋太医？娘娘跟您说话呢！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1403]={nIndex=1403,nCheckPointId=466,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="诶、你说咱们什么时候装这个小产比较合适啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1404]={nIndex=1404,nCheckPointId=466,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="娘娘、娘娘脉象稳定，想、想必这孩子、也极好啊。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1405]={nIndex=1405,nCheckPointId=466,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="嗯呵呵呵！别装了昂！你就直接跟我说，咱们什么时候……把这个孩子拿掉？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1406]={nIndex=1406,nCheckPointId=466,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="娘娘，这孩子是真有的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1407]={nIndex=1407,nCheckPointId=467,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊？！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1408]={nIndex=1408,nCheckPointId=467,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="小宋啊小宋！这个孩子不能有！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1409]={nIndex=1409,nCheckPointId=467,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，这个孩子可以有！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1410]={nIndex=1410,nCheckPointId=467,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="虽然之前臣受张老夫人之托，要称娘娘怀了孩子，但臣上次给娘娘诊脉时，确实发现娘娘有了身孕啊。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1411]={nIndex=1411,nCheckPointId=468,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那你上一次为什么不说？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1412]={nIndex=1412,nCheckPointId=468,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="娘娘，臣和您如实说了啊，临走的时候，还嘱咐娘娘吃安胎药。臣也是如实向皇上禀报的啊。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1413]={nIndex=1413,nCheckPointId=468,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你！呀！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1414]={nIndex=1414,nCheckPointId=469,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您都怀孕了、你不能这样。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1415]={nIndex=1415,nCheckPointId=469,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="娘娘怀孕了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1416]={nIndex=1416,nCheckPointId=469,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，这都怀上孕了，您怎么更心烦了呀？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1417]={nIndex=1417,nCheckPointId=469,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="不可能啊，我跟齐晟根本就没有……，难道是，三个月前，我和他在张府那次？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1418]={nIndex=1418,nCheckPointId=469,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎哎哎。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1419]={nIndex=1419,nCheckPointId=470,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊，你不要这样像跟屁虫一样烦着我啦。好不好！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1420]={nIndex=1420,nCheckPointId=470,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您真是太大意了！怎么一直没来月事，你自己都没发现啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1421]={nIndex=1421,nCheckPointId=470,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（内心）我一个大老爷们儿我又没生过孩子，我怎么会知道！阿西吧！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1422]={nIndex=1422,nCheckPointId=471,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘？那定是您底子好，没有什么害喜的反应，这次在江北遭了那么大的难，都没有动了胎气，说明这孩子是上天对您的眷顾，是命中注定啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1423]={nIndex=1423,nCheckPointId=471,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="老天爷，你这是要照死了玩儿我啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1424]={nIndex=1424,nCheckPointId=472,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，今天江氏没了孩子，咱们又有了孩子！这真是双喜临门啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1425]={nIndex=1425,nCheckPointId=472,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，娘娘，娘娘您怎么啦？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1426]={nIndex=1426,nCheckPointId=472,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘、娘娘、您怎么啦？来人啊，来人啊！娘娘、娘娘、来人啊，",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1427]={nIndex=1427,nCheckPointId=473,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您怎么下地了呀？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1428]={nIndex=1428,nCheckPointId=473,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我是怀孕啊，不是瘫痪，别老让我躺着",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1429]={nIndex=1429,nCheckPointId=474,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="那，新熬好的燕窝粥，您吃两口？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1430]={nIndex=1430,nCheckPointId=474,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="让朕来吧",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1431]={nIndex=1431,nCheckPointId=475,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="其实，这个孩子不能生。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1432]={nIndex=1432,nCheckPointId=475,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="一开始朕也这么觉得。 ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1433]={nIndex=1433,nCheckPointId=475,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="既然你也这么觉得，那咱们就趁现在还来得及，赶紧把这个孩子给拿掉吧！啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1434]={nIndex=1434,nCheckPointId=475,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="可当朕知道有了这个孩子的时候，心里竟然很欢喜。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1435]={nIndex=1435,nCheckPointId=476,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="可是，这个孩子我生不了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1436]={nIndex=1436,nCheckPointId=476,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="这又是为何？上次你不是说爱吃酸的，无酸不欢，现在怎么又不能生了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1437]={nIndex=1437,nCheckPointId=476,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我这些都是为你好啊，这个孩子生了会增加张家的势力，为了不让张家势力过大，这个孩子还是不生为好！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1438]={nIndex=1438,nCheckPointId=476,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="孩子和张家之间，朕当然会选择孩子。若你如此替我放心不下张家……朕明天就扳倒它！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1439]={nIndex=1439,nCheckPointId=477,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="别闹别闹别闹，既然你不担心张家，你就不害怕，诶，我这个性格把你的孩子带坏吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1440]={nIndex=1440,nCheckPointId=477,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="若你担心的是这个，那大可不必，到时候会有奶妈替你看好孩子。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1441]={nIndex=1441,nCheckPointId=477,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="皇上！我……我不能给你生这个孩子！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1442]={nIndex=1442,nCheckPointId=478,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你不想和朕共同生养一个孩子了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1443]={nIndex=1443,nCheckPointId=478,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这不是和谁生的问题，而是让我生孩子，我根本就没有这个功……我接受不了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1444]={nIndex=1444,nCheckPointId=478,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你可知道你这话是大逆不道？生儿育女是你的责任，怎么能由你的意愿左右？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1445]={nIndex=1445,nCheckPointId=478,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="一定要生吗？  啊。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1446]={nIndex=1446,nCheckPointId=479,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你若是敢对这孩子做什么，朕是不会放过你的！朕命你好生休养，不要胡思乱想，你听明白了吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1447]={nIndex=1447,nCheckPointId=479,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎呀",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1448]={nIndex=1448,nCheckPointId=479,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="怎么了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1449]={nIndex=1449,nCheckPointId=479,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我头疼！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1450]={nIndex=1450,nCheckPointId=480,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕命御厨，做了你最喜欢吃的榴莲粥，一会就送来。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1451]={nIndex=1451,nCheckPointId=480,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我不想吃榴莲粥。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1452]={nIndex=1452,nCheckPointId=480,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="（内心）我吃鸭脖，鸭肠，鸭心，鸭锁骨。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1453]={nIndex=1453,nCheckPointId=480,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我头疼，哎～",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1454]={nIndex=1454,nCheckPointId=481,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="鸭脖、鸭翅、鸭锁骨。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1455]={nIndex=1455,nCheckPointId=481,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="猜猜我是谁？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1456]={nIndex=1456,nCheckPointId=481,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="杨严",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1457]={nIndex=1457,nCheckPointId=482,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="没劲，一下子就猜到了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1458]={nIndex=1458,nCheckPointId=482,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="除了你呀，还有谁！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1459]={nIndex=1459,nCheckPointId=483,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="那你猜猜这是什么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1460]={nIndex=1460,nCheckPointId=483,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="桂花糕。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1461]={nIndex=1461,nCheckPointId=483,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="啊～不要再提“桂花糕”三个字了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1462]={nIndex=1462,nCheckPointId=484,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="叫我好人，不用谢！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1463]={nIndex=1463,nCheckPointId=484,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊，你怎么知道我想吃鸭脖子了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1464]={nIndex=1464,nCheckPointId=484,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="我平时就喜欢在宫中飞檐走壁，要是知道我心上记挂的某个人，有个小小心愿，我就一定会送去。当然，你可以叫我夜行侠……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1465]={nIndex=1465,nCheckPointId=485,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="诶～那……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1466]={nIndex=1466,nCheckPointId=485,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="无需赞我……哥就是这么潇洒，做好事从不留名！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1467]={nIndex=1467,nCheckPointId=485,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我就是想问问你，我是不是洗澡、睡觉你都要看？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1468]={nIndex=1468,nCheckPointId=485,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="你这是在侮辱我！我有职业操守的，不该看的我绝不会看！我是夜行侠，不是偷窥狂！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1469]={nIndex=1469,nCheckPointId=486,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="诶，杨严，我怎么觉得你总在我旁边晃悠啊，你老实交代，你是不是对我有什么歪心思？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1470]={nIndex=1470,nCheckPointId=486,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="那不能够！你是皇后！又是九哥心尖儿上的人！我是喜欢你，但不是那种喜欢。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1471]={nIndex=1471,nCheckPointId=486,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那你说说看，你是喜欢我的模样呢，还是喜欢我的内心呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1472]={nIndex=1472,nCheckPointId=486,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="我当然喜欢你的内心啊，我可不是那种肤浅之人！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1473]={nIndex=1473,nCheckPointId=486,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那看来你是喜欢男人了，鉴定完毕。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1474]={nIndex=1474,nCheckPointId=487,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="姐姐，你说什么呢！我就想着有朝一日，我要娶媳妇了，也要娶你这样的！最好也姓张，和你长得一样就最好了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1475]={nIndex=1475,nCheckPointId=487,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我跟你说，我可是独一无二的，没有双胞胎兄弟。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1476]={nIndex=1476,nCheckPointId=487,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="表姐妹也行啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1477]={nIndex=1477,nCheckPointId=487,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你这是让我在给你介绍对象吗?",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1478]={nIndex=1478,nCheckPointId=487,nTalkIndex=5,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="姐姐，难道让我守着你嫁不出去吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1479]={nIndex=1479,nCheckPointId=487,nTalkIndex=6,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你也到年纪了，该娶媳妇了哈。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1480]={nIndex=1480,nCheckPointId=488,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="行，我就当回一月老啊，等我回了家，给你撒吧撒吧昂。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1481]={nIndex=1481,nCheckPointId=488,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="那我就等你信儿了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1482]={nIndex=1482,nCheckPointId=488,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="嗯",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1483]={nIndex=1483,nCheckPointId=489,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="陛下多久没有看过，妾身为您跳舞了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1484]={nIndex=1484,nCheckPointId=489,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="有时候朕在想，把你接入宫里，究竟是对还是错。你进了宫，倒是变得心狠手辣，诡计多端。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1485]={nIndex=1485,nCheckPointId=490,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="皇上……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1486]={nIndex=1486,nCheckPointId=490,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你处处与皇后争锋相对，我没有深究，一是念在你一时糊涂，二是皇后嘱咐我不要与你为难。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1487]={nIndex=1487,nCheckPointId=491,nTalkIndex=1,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="皇上竟是因为姐姐，才不责罚妾身的？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1488]={nIndex=1488,nCheckPointId=491,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="不然呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1489]={nIndex=1489,nCheckPointId=491,nTalkIndex=3,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="妾身以为，皇上对我还有情……才会忍耐我的小性子。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1490]={nIndex=1490,nCheckPointId=492,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="这已经不是耍耍小性子那么简单的问题了！你居然谎称怀孕来欺骗朕？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1491]={nIndex=1491,nCheckPointId=492,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="皇上夜宿皇后那里，妾身心里实在难受，才出此下策，那都是对您的一份爱、一份情才会这么做的啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1492]={nIndex=1492,nCheckPointId=493,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="因为爱？你已经被私情迷了心智，做出那么多有悖本心之事，你可知道，你现在这般对待自己，已不再惹人怜惜。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1493]={nIndex=1493,nCheckPointId=493,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="皇上，您难道感觉不到我对你的爱吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1494]={nIndex=1494,nCheckPointId=494,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你究竟还要利用“爱”这个字做多少糊涂事？朕一直不喜张氏从前的娇蛮，只觉得你比她更大度，明事理，善解人意……令朕没想到，",sColor="0",bShake=false,sVoice="0",}
