ctCheckPointTalkConf[1499]={nIndex=1499,nCheckPointId=496,nTalkIndex=2,sIcon="icon_npc_jiangyingyue",sName="江映月",sBackground="bg_zhandou",sTalk="皇上……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1500]={nIndex=1500,nCheckPointId=497,nTalkIndex=1,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="这些日子娘一直都吃斋念佛，求菩萨保佑娘娘，没想到真是灵验了，芃儿你总算是熬出头了。",sColor="0",bShake=false,sVoice="0",}
