ctCheckPointTalkConf[1508]={nIndex=1508,nCheckPointId=500,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="打住，打住！我这次把你找过来啊，就是想跟你说一件更重要的事情。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1509]={nIndex=1509,nCheckPointId=501,nTalkIndex=1,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="难道不是让为娘帮你出谋划策，看住皇上吗？",sColor="0",bShake=false,sVoice="0",}
