ctCheckPointTalkConf[1511]={nIndex=1511,nCheckPointId=502,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我就是想问问你，咱们张家还有没有那种闭月羞花，沉鱼落雁的小美眉？ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1512]={nIndex=1512,nCheckPointId=502,nTalkIndex=2,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="沉鱼落雁，闭月羞花？是何物？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1513]={nIndex=1513,nCheckPointId=502,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="就是，人见人爱，花见花开，车见车载的那种。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1514]={nIndex=1514,nCheckPointId=502,nTalkIndex=4,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="有，你叔父张翎家的姑娘张灵灵。（八卦地）诶，也就是那姑娘，那长相还能与你媲美。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1515]={nIndex=1515,nCheckPointId=503,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哈哈哈，那真是太好了！诶，她可曾许配过婚事？",sColor="0",bShake=false,sVoice="0",}
