ctCheckPointTalkConf[1517]={nIndex=1517,nCheckPointId=504,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="这次把你找过来啊，就是想告诉你，我想给杨豫将军的儿子许配一门亲事。杨家跟咱们家也算是门当户对，您觉得呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1518]={nIndex=1518,nCheckPointId=504,nTalkIndex=2,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="好啊！太好了！这回咱们家可算是双喜临门了！",sColor="0",bShake=false,sVoice="0",}
