ctCheckPointTalkConf[1551]={nIndex=1551,nCheckPointId=517,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="行了行了行了，姑娘一摆这个表情我最清楚了！我就说嘛，就我们杨严那种小鲜肉、高富帅，肯定是抢手货！",sColor="0",bShake=false,sVoice="0",}
