ctCheckPointTalkConf[1565]={nIndex=1565,nCheckPointId=523,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="这砖头，不会是要来拍我的吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1566]={nIndex=1566,nCheckPointId=524,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我们灵灵说了，就喜欢那种身强力壮、饱读诗书的男人，你除了会耍嘴皮子你还会干什么啊？别说我没帮你昂，把这些都给我搬回去，好好练习！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1567]={nIndex=1567,nCheckPointId=524,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="我练？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1568]={nIndex=1568,nCheckPointId=524,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那我练！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1569]={nIndex=1569,nCheckPointId=525,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="让我？自己？用手？搬回去？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1570]={nIndex=1570,nCheckPointId=525,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="想不想娶媳妇儿你自己看着办。七天之后来我宫里找我，我要对你进行抽查。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1571]={nIndex=1571,nCheckPointId=526,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，这榴莲这么臭，您怎么下得去口呀。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1572]={nIndex=1572,nCheckPointId=526,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="吃一块，吃一块，吃一块。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1573]={nIndex=1573,nCheckPointId=526,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="看样子，你是准备好了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1574]={nIndex=1574,nCheckPointId=527,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="姐姐，这里怎么充满一股屎的味道。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1575]={nIndex=1575,nCheckPointId=527,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="没文化没文化，我吃的这哪是屎。我吃这个东西叫榴莲，这是我特地托朋友从马来西亚运过来的，高级货，赏你一口。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1576]={nIndex=1576,nCheckPointId=527,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="姐姐，我才不和你们同流合污呢，我绝不吃屎。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1577]={nIndex=1577,nCheckPointId=528,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="哼，我准备好了。这对我来说，也太小儿科了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1578]={nIndex=1578,nCheckPointId=528,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="啊！你干嘛呀！你干嘛呀！娘娘！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1579]={nIndex=1579,nCheckPointId=528,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="女神姐姐，我这只手还空着呢，还能再扛起一人。姐姐，要不你也来试一试？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1580]={nIndex=1580,nCheckPointId=528,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="行啦，明天带你去一个地方。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1581]={nIndex=1581,nCheckPointId=529,nTalkIndex=1,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="你别紧张，灵灵的母亲早世，她的婚姻大事，作为长辈，我应该帮她把把关才行。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1582]={nIndex=1582,nCheckPointId=529,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="夫人放心！我力大无穷，徒手搬砖绝不含糊！而且学富五车，有皇后娘娘权威认证的学霸八级证书呢。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1583]={nIndex=1583,nCheckPointId=529,nTalkIndex=3,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="谁问你这些啊？你说的这些只是作为一个寻常男子基本的素质。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1584]={nIndex=1584,nCheckPointId=529,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="母亲大人，您就别为难他了，张灵灵，你说呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1585]={nIndex=1585,nCheckPointId=529,nTalkIndex=5,sIcon="icon_npc_zhanglingling",sName="张灵灵",sBackground="bg_zhandou",sTalk="在家里，我都听大伯母的",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1586]={nIndex=1586,nCheckPointId=530,nTalkIndex=1,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="我并不是为难他，只是作为我们张家的女婿，没别的要求，只要求他是个纯爷们儿！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1587]={nIndex=1587,nCheckPointId=530,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="我是纯爷们儿",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1588]={nIndex=1588,nCheckPointId=530,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="母亲大人，他确实跟九王呢关系走的密切了一些，不过我可以保证他们两人确实是纯洁的兄弟关系！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1589]={nIndex=1589,nCheckPointId=531,nTalkIndex=1,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="我不是那个意思。来人，好酒好菜伺候！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1590]={nIndex=1590,nCheckPointId=531,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="这是什么情况？ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1591]={nIndex=1591,nCheckPointId=531,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我也不知道啊！ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1592]={nIndex=1592,nCheckPointId=531,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="这竹管是？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1593]={nIndex=1593,nCheckPointId=531,nTalkIndex=5,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="这竹管是我家灵灵专用的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1594]={nIndex=1594,nCheckPointId=531,nTalkIndex=6,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="奥。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1595]={nIndex=1595,nCheckPointId=532,nTalkIndex=1,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="老四、老五、老六你们好好陪杨公子喝酒助助兴！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1596]={nIndex=1596,nCheckPointId=532,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我有这么多表哥？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1597]={nIndex=1597,nCheckPointId=532,nTalkIndex=3,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="我们家有四个门房，以酒量排了辈分，四斤哥、五斤哥、六斤哥就是这么来的",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1598]={nIndex=1598,nCheckPointId=532,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="不是四个门房吗？另外一个呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1599]={nIndex=1599,nCheckPointId=532,nTalkIndex=5,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="老六已经喝的身先士卒了，这老七就顺势挪了上来，就是现在的老六。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1600]={nIndex=1600,nCheckPointId=533,nTalkIndex=1,sIcon="icon_npc_zhanglingling",sName="张灵灵",sBackground="bg_zhandou",sTalk="这酒，我也是能喝一点的，不如我先干为敬。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1601]={nIndex=1601,nCheckPointId=533,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="请。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1602]={nIndex=1602,nCheckPointId=533,nTalkIndex=3,sIcon="icon_npc_zhanglingling",sName="张灵灵",sBackground="bg_zhandou",sTalk="不用这个。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1603]={nIndex=1603,nCheckPointId=533,nTalkIndex=4,sIcon="icon_npc_zhanglingling",sName="张灵灵",sBackground="bg_zhandou",sTalk="小酌怡情，小酌怡情",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1604]={nIndex=1604,nCheckPointId=533,nTalkIndex=5,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="这是小酌？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1605]={nIndex=1605,nCheckPointId=533,nTalkIndex=6,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我只能祝你身体健康了",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1606]={nIndex=1606,nCheckPointId=534,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="倒酒！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1607]={nIndex=1607,nCheckPointId=534,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="不能喝就别喝了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1608]={nIndex=1608,nCheckPointId=534,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="谁说我不能喝的！谁说我不能喝！我告诉你，（一句比一句语调高地）一斤两斤我不算酒，三斤四斤涮涮口，五斤六斤有点儿抖，七斤八斤我扶墙走，九斤十斤我还在吼——！呜～",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1609]={nIndex=1609,nCheckPointId=535,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="终于解脱了……水啊，救命啊，我不会水啊，救救我，啊～我要死了、救命啊",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1610]={nIndex=1610,nCheckPointId=535,nTalkIndex=2,sIcon="icon_npc_zhanglingling",sName="张灵灵",sBackground="bg_zhandou",sTalk="喂，喂",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1611]={nIndex=1611,nCheckPointId=535,nTalkIndex=3,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="杨严，你没事吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1612]={nIndex=1612,nCheckPointId=535,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="灵灵，你平日里挺有主见的，你说你今天为什么眼睁睁地看着别人灌他酒呢？ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1613]={nIndex=1613,nCheckPointId=535,nTalkIndex=5,sIcon="icon_npc_zhanglingling",sName="张灵灵",sBackground="bg_zhandou",sTalk="这件事是我的不对，我也不知道这小子一喝起来就停不下来了呀……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1614]={nIndex=1614,nCheckPointId=536,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="母亲大人你也是的，杨严今天要是在这喝出个好歹来，日后让我怎么跟杨将军交代？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1615]={nIndex=1615,nCheckPointId=536,nTalkIndex=2,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="这，杨公子以后在府上，跟咱自家公子一样对待！一样对待！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1616]={nIndex=1616,nCheckPointId=536,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="还不快道谢，这代表认了你这个人了！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1617]={nIndex=1617,nCheckPointId=536,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="杨严",sBackground="bg_zhandou",sTalk="岳母……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1618]={nIndex=1618,nCheckPointId=536,nTalkIndex=5,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="啊～",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1619]={nIndex=1619,nCheckPointId=537,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，皇上没跟那些娘娘们睡在一起，是因为对您一片真心啊！活该她们都变成熊猫眼儿！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1620]={nIndex=1620,nCheckPointId=537,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="绿篱啊，你就相信一代帝王会变成情种么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1621]={nIndex=1621,nCheckPointId=537,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，咱们就不能再信皇上一次吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1622]={nIndex=1622,nCheckPointId=537,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="宝贝儿，齐晟他不是情种，啊即便他以前情种过，那个对象也不是我！以前不是，以后也不是！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1623]={nIndex=1623,nCheckPointId=538,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，您消消火，奴婢不再说了便是？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1624]={nIndex=1624,nCheckPointId=538,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我这火那那么容易消啊！他总在关键时候就给我掉链子，还让我给他擦屁股！哎，这件事情我一定要查出真相！可是，我找谁呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1625]={nIndex=1625,nCheckPointId=538,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="诶...对，赵王！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1626]={nIndex=1626,nCheckPointId=539,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="赵王，我与你的关系呢，是剪不断理还乱。所以今天我把你找来，是有很重要的事情想要问你……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1627]={nIndex=1627,nCheckPointId=539,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="我就说吗，重要的事情，你还得问我!",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1628]={nIndex=1628,nCheckPointId=540,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你少得瑟啊，我就是想问你，皇上他……是不是受过什么刺激啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1629]={nIndex=1629,nCheckPointId=540,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="嗯？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1630]={nIndex=1630,nCheckPointId=540,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="比如说……女人的刺激",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1631]={nIndex=1631,nCheckPointId=541,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="娘娘，您这话什么意思啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1632]={nIndex=1632,nCheckPointId=541,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你弟他明明是个可以坐拥三千佳丽的皇帝，为什么要过着像苦行僧一样的生活，不沾雨露呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1633]={nIndex=1633,nCheckPointId=541,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="此话当真？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1634]={nIndex=1634,nCheckPointId=541,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="当然了，我亲自查的房！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1635]={nIndex=1635,nCheckPointId=542,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="哎，皇上一向都是一个专情的人！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1636]={nIndex=1636,nCheckPointId=542,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="赵王",sBackground="bg_zhandou",sTalk="不过也难怪，我们齐家的男子向来如此，洁身自好、至情至性、贯出情种。比如说坐在你对面的……我！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1637]={nIndex=1637,nCheckPointId=542,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="冤大头我见过，你这么冤的大头我还是第一次见！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1638]={nIndex=1638,nCheckPointId=543,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，赵王今日可跟您透露了什么有用的消息没？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1639]={nIndex=1639,nCheckPointId=543,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="可靠情报一点儿没有，反倒说了齐晟好话。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1640]={nIndex=1640,nCheckPointId=543,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="啊？他都说什么啦？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1641]={nIndex=1641,nCheckPointId=543,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="他说，齐晟是个用情专一的人。我就纳闷儿了！你说人家都拐跑了你媳妇，你怎么还帮人家数钱呢？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1642]={nIndex=1642,nCheckPointId=544,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="不过，话说回来，这些日子娘娘您也看到了，皇上呀，他不仅对您百依百顺，就连您亲自送到大明宫的那些娘娘们，他看都不看一眼，我觉得啊，皇上用情专一的对象就是您啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1643]={nIndex=1643,nCheckPointId=544,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎呀妈呀，你说的好有道理啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1644]={nIndex=1644,nCheckPointId=544,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="对呀对呀！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1645]={nIndex=1645,nCheckPointId=544,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="对你个头啊！女人的脑子就这么不记仇吗！人家给你叼两颗糖豆豆，你就什么豆忘了！在江北的时候齐晟差点儿要了老子的命！告诉你啊！就算他把我当成亲爹，也动摇不了我要灭了他的决心！关灯！睡觉！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1646]={nIndex=1646,nCheckPointId=545,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="为何今日起的这么晚？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1647]={nIndex=1647,nCheckPointId=545,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="最近实在有太多事情需要我操劳了，太辛苦了，多睡了会儿。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1648]={nIndex=1648,nCheckPointId=546,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="操劳？朕看你是整日没什么正经事儿，不是帮人相亲，就是忙着组局喝酒，你别忘了，你肚子里还有个孩子呢！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1649]={nIndex=1649,nCheckPointId=546,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="我啊，这不正给他树立一个乐于助人的好榜样吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1650]={nIndex=1650,nCheckPointId=546,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="吃饭喝酒是好榜样吗？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1651]={nIndex=1651,nCheckPointId=547,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="放下吧",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1652]={nIndex=1652,nCheckPointId=547,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="强公公",sBackground="bg_zhandou",sTalk="是。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1653]={nIndex=1653,nCheckPointId=547,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="这些史书、兵书、以及四书五经，你定要抽时间熟读。听太医说，这是对胎儿极好的早教。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1654]={nIndex=1654,nCheckPointId=548,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎，我这种连人物小传都读不会的人，读什么史诗啊？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1655]={nIndex=1655,nCheckPointId=548,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你若不行，朕会从翰林院派个太傅来教你。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1656]={nIndex=1656,nCheckPointId=548,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="皇上，我错了。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1657]={nIndex=1657,nCheckPointId=548,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="真知错了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1658]={nIndex=1658,nCheckPointId=548,nTalkIndex=5,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="真知道错了。大错特错。我应该好好的在宫中养胎，注意胎教。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1659]={nIndex=1659,nCheckPointId=549,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="罢了，书就不用读了，退而求其次，不如你去学学刺绣，说不定怀的是个女儿呢。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1660]={nIndex=1660,nCheckPointId=549,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="不、不～～万一是男孩儿呢，学刺绣那岂不是让他在我肚子里的时候就变得娘里娘气的吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1661]={nIndex=1661,nCheckPointId=549,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕的儿子，绝不会像你说的那样！就请皇后为朕绣个荷包吧，也好修身养性。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1662]={nIndex=1662,nCheckPointId=550,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="咱们就不能再商量商量？万一他真的是个儿子呢！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1663]={nIndex=1663,nCheckPointId=550,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="三日后，朕来取你的荷包！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1664]={nIndex=1664,nCheckPointId=550,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="(心道）我一大男人那会绣荷包啊！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1665]={nIndex=1665,nCheckPointId=551,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="宝贝儿，你过来帮我绣吧？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1666]={nIndex=1666,nCheckPointId=551,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，荷包这种东西本是男女之间的信物，奴婢是万万不敢帮您绣的。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1667]={nIndex=1667,nCheckPointId=551,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="好宝贝儿，我保证不让任何人知道。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1668]={nIndex=1668,nCheckPointId=552,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="嗯～娘娘，人家还没给男人绣过荷包呢！人家要把第一次绣的荷包留给将来的那个他呢……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1669]={nIndex=1669,nCheckPointId=552,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="行了行了行了！求人不如求己！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1670]={nIndex=1670,nCheckPointId=553,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊啊～绿篱！绿篱！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1671]={nIndex=1671,nCheckPointId=553,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="啊！娘娘！娘娘！啊！娘娘！你怎么了？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1672]={nIndex=1672,nCheckPointId=553,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="快去！快把太医叫来！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1673]={nIndex=1673,nCheckPointId=554,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="宋太医您这边请，刚才娘娘不小心拌了一下，你快给看看有没有事！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1674]={nIndex=1674,nCheckPointId=554,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="微臣给娘娘请安！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1675]={nIndex=1675,nCheckPointId=554,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="今天我不打你！快点！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1676]={nIndex=1676,nCheckPointId=555,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="啊……娘娘的孩子安好！估计就要生了，娘娘再让微臣给您看看吧！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1677]={nIndex=1677,nCheckPointId=555,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="看他妈什么看！信不信我拍死你！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1678]={nIndex=1678,nCheckPointId=555,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="别别，娘娘别！微臣告退！微臣告退！啊～ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1679]={nIndex=1679,nCheckPointId=556,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="要生了？你们怎么不进去？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1680]={nIndex=1680,nCheckPointId=556,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="罪臣该死……罪臣怕惹娘娘烦，就出来了……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1681]={nIndex=1681,nCheckPointId=556,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕进去看看！ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1682]={nIndex=1682,nCheckPointId=556,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="皇上还是不要进去为好，人多了，不利于生产。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1683]={nIndex=1683,nCheckPointId=557,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕问你，朕听人说，生孩子的痛苦比身上挨刀伤疼十倍，此话当真？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1684]={nIndex=1684,nCheckPointId=557,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="微臣认为，是二十倍！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1685]={nIndex=1685,nCheckPointId=557,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="有什么办法可以解痛？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1686]={nIndex=1686,nCheckPointId=557,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="皇上恕罪，罪臣确是无计可施。从古至今，千万人都要经过这生产的阵痛，才能生子。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1687]={nIndex=1687,nCheckPointId=557,nTalkIndex=5,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="大胆！朕的皇后岂能和寻常妇人相提并论？朕命你速速想出办法，解除皇后娘娘的疼痛！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1688]={nIndex=1688,nCheckPointId=557,nTalkIndex=6,sIcon="icon_npc_qianggonggong",sName="宋太医",sBackground="bg_zhandou",sTalk="微臣遵旨！微臣遵旨！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1689]={nIndex=1689,nCheckPointId=558,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊～",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1690]={nIndex=1690,nCheckPointId=558,nTalkIndex=2,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，娘娘。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1691]={nIndex=1691,nCheckPointId=558,nTalkIndex=3,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="腿出来了、大出血啦",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1692]={nIndex=1692,nCheckPointId=558,nTalkIndex=4,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="啊～",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1693]={nIndex=1693,nCheckPointId=558,nTalkIndex=5,sIcon="icon_npc_lvli",sName="张母",sBackground="bg_zhandou",sTalk="我们一起把他拔出来、一二三、",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1694]={nIndex=1694,nCheckPointId=559,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="芃芃，你受苦了。你生产的前一刻，屋子里没声了，令朕着实担心。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1695]={nIndex=1695,nCheckPointId=559,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="都喊的没有力气了！哪还喊的出声！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1696]={nIndex=1696,nCheckPointId=559,nTalkIndex=3,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="生了皇子，你爱吃的那些酸，也算没白吃。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1697]={nIndex=1697,nCheckPointId=560,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="生男生女对我来说都一样，只要他健康就好。你见了孩子么？",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1698]={nIndex=1698,nCheckPointId=560,nTalkIndex=2,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="嗯，眉眼像你！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1699]={nIndex=1699,nCheckPointId=560,nTalkIndex=3,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="那我就放心了。要是眉眼像你，生出来还得带他去韩国整容……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1700]={nIndex=1700,nCheckPointId=560,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="你这些胡言乱语，可千万别教给孩子。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1701]={nIndex=1701,nCheckPointId=561,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘，自打您生了小皇子以后呀，就变得越来越有女人味儿了！今日是小皇子的百日宴，您一定能成为万众瞩目的焦点！",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1702]={nIndex=1702,nCheckPointId=561,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="你行啦！少跟我提什么女人味儿！我不过就是生了个孩子，我的心啊还和从前一样！ ",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1703]={nIndex=1703,nCheckPointId=562,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="皇上吉祥",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1704]={nIndex=1704,nCheckPointId=562,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="皇上吉祥",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1705]={nIndex=1705,nCheckPointId=562,nTalkIndex=3,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="皇上，奴婢给您倒杯茶吧？哎呀",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1706]={nIndex=1706,nCheckPointId=562,nTalkIndex=4,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="不必了。朕不喝。",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1707]={nIndex=1707,nCheckPointId=562,nTalkIndex=5,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="是。",sColor="0",bShake=false,sVoice="0",}
