ctCheckPointTalkConf[1715]={nIndex=1715,nCheckPointId=565,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="奥，起名字这么大的事儿，你一个人就决定了、啊？你去问问，谁认识这个字！",sColor="0",bShake=false,sVoice="0",}
