ctCheckPointTalkConf[1725]={nIndex=1725,nCheckPointId=568,nTalkIndex=1,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="哎呀，我跟您说吧，他们两个人真的是特别特别好的朋友，直得都不能再直了！我偷偷告诉您吧，以前我还在青楼碰到他们呢！",sColor="0",bShake=false,sVoice="0",}
