ctCheckPointTalkConf[1732]={nIndex=1732,nCheckPointId=569,nTalkIndex=5,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="奴婢把赵王给打了……",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1733]={nIndex=1733,nCheckPointId=569,nTalkIndex=6,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="尼玛！赵王再没权没势好歹也是个王爷啊？他是齐晟的哥哥，皇祖母的亲孙儿。哎呀~",sColor="0",bShake=false,sVoice="0",}
