ctCheckPointTalkConf[1740]={nIndex=1740,nCheckPointId=571,nTalkIndex=3,sIcon="icon_npc_jiangyingyue",sName="太后",sBackground="bg_zhandou",sTalk="李公公！去看看，别在外面睡着了，要是真睡着了就把他扶回府里，别染了风寒",sColor="0",bShake=false,sVoice="0",}
