ctCheckPointTalkConf[1746]={nIndex=1746,nCheckPointId=574,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="朕问你，今晚你为何那么晚才回筵席，赵王是怎么回事！你在外面遇见了谁？",sColor="0",bShake=false,sVoice="0",}
