ctCheckPointTalkConf[1752]={nIndex=1752,nCheckPointId=576,nTalkIndex=1,sIcon="icon_npc_qianggonggong",sName="齐晟",sBackground="bg_zhandou",sTalk="（见张芃芃迟迟不说话，冷哼一声，气呼呼地走了）",sColor="0",bShake=false,sVoice="0",}
ctCheckPointTalkConf[1753]={nIndex=1753,nCheckPointId=576,nTalkIndex=2,sIcon="icon_npc_taizifei",sName="张芃芃",sBackground="bg_zhandou",sTalk="皇上慢走啊",sColor="0",bShake=false,sVoice="0",}
