ctCheckPointTalkConf[1828]={nIndex=1828,nCheckPointId=601,nTalkIndex=1,sIcon="icon_npc_lvli",sName="绿篱",sBackground="bg_zhandou",sTalk="娘娘！据可靠消息称，有人在大明宫看到了江氏！",sColor="0",bShake=false,sVoice="0",}
