ctBeginGuideConf[4]={nID=4,nGuideType=1,sIcon="icon_npc_lvli",sName="女子",sConversation="娘娘，你醒啦！娘娘你总算是醒啦！您要是再不醒啊，江氏还不得谋害亲夫，先把赵王给害了，再跟咱们的太子爷整天整夜腻在一起，卿卿我我......",sBackground="bg_qingong",sColor="5b3a29",nFontSize=24,nJiachu=0,}
ctBeginGuideConf[5]={nID=5,nGuideType=1,sIcon="icon_npc_lvli",sName="女子",sConversation="可怜的赵王，赵王死得也太惨了，但是任凭谁可怜，也没有小姐您可怜哪......",sBackground="bg_qingong",sColor="5b3a29",nFontSize=24,nJiachu=0,}
ctBeginGuideConf[6]={nID=6,nGuideType=1,sIcon="icon_npc_taizifei",sName="我",sConversation="你谁啊！你说什么呢！",sBackground="bg_qingong",sColor="5b3a29",nFontSize=24,nJiachu=0,}
ctBeginGuideConf[7]={nID=7,nGuideType=1,sIcon="icon_npc_lvli",sName="绿篱",sConversation="我...我是绿篱呀，小姐，你不记得我了小姐？宋太医，你快给她看看吧，娘娘可病得不轻…",sBackground="bg_qingong",sColor="5b3a29",nFontSize=24,nJiachu=0,}
ctBeginGuideConf[8]={nID=8,nGuideType=1,sIcon="icon_npc_qianggonggong",sName="宋太医",sConversation="是，臣为娘娘把把脉",sBackground="bg_qingong",sColor="5b3a29",nFontSize=24,nJiachu=0,}
