ctBeginGuideConf[12]={nID=12,nGuideType=1,sIcon="icon_npc_taizifei",sName="我",sConversation="太医？难道...小美女，我问你啊，这是哪里？",sBackground="bg_qingong",sColor="5b3a29",nFontSize=24,nJiachu=0,}
