ctTaskPlotConf[101000]={nID=101000,nIndex=1,nTaskId=101000,sTaskName="主线-与师傅谈话",sTaskDescription="师傅要见你",tTalk={{1,"徒儿修炼许久了，本门功法修的如何？",},{0,"师傅，弟子潜心学习，早已熟透！",},{1,"好，为师有任务交予你，你先去收复一个宠物，看看你的功法如何！",},{0,"好的，弟子马上去！",},},sSeekNpc="秦琼",sBackground="heyang",}
ctTaskPlotConf[101001]={nID=101001,nIndex=2,nTaskId=101001,sTaskName="主线-与师傅谈话",sTaskDescription="与宠物切磋",tTalk={{0,"好的，弟子马上去！",},},sSeekNpc="宠物",sBackground="heyang",}
ctTaskPlotConf[101002]={nID=101002,nIndex=3,nTaskId=101002,sTaskName="主线-与师傅谈话",sTaskDescription="展捕快失踪事件",tTalk={{1,"徒儿虽灵根不俗，但缺少历练，现长安衙府传信称日前有位姓展的捕快离奇失踪，为师命你前去查看此事，你可愿意？",},{0,"是，师傅放心，徒儿定查个水落石出，救回展捕快！",},},sSeekNpc="秦琼",sBackground="heyang",}
ctTaskPlotConf[101003]={nID=101003,nIndex=4,nTaskId=101003,sTaskName="主线-与师傅谈话",sTaskDescription="前往长安城",tTalk={{1,"人命关天，为师这就作法送你至长安城。",},{0,"弟子拜别恩师！",},},sSeekNpc="秦琼",sBackground="caomiaocun",}
ctTaskPlotConf[101004]={nID=101004,nIndex=5,nTaskId=101004,sTaskName="主线-长安风云",sTaskDescription="热心人搭讪",tTalk={{2,"少侠，老夫看你愁眉莫展的，给你算上一卦如何？",},{0,"这个，我不是来算命的…",},{2,"老夫不仅会算命，算人也是一流的哟！",},{0,"真的吗，我想打听一下，我…",},{2,"哎呀突然想起还有点事，下次再给你算了哈，先走了！",},{0,"呃...慢着，我的钱袋呢，好你个老贼，快把钱袋留下！",},},sSeekNpc="算无遗",sBackground="caomiaocun",}
ctTaskPlotConf[101005]={nID=101005,nIndex=6,nTaskId=101005,sTaskName="主线-长安风云",sTaskDescription="热心人原来是贼",tTalk={{2,"呀，你哪只眼睛看到这是你的钱袋了？",},{0,"我两只眼睛都看到，天子脚下竟有如此胆大嚣张之徒，讨打！",},},sSeekNpc="算无遗",sBackground="caomiaocun",}
ctTaskPlotConf[101006]={nID=101006,nIndex=7,nTaskId=101006,sTaskName="主线-长安风云",sTaskDescription="教训小偷算无遗",tTalk={{0,"吃我一招，呀…！",},},sSeekNpc="算无遗",sBackground="caomiaocun",}
ctTaskPlotConf[101007]={nID=101007,nIndex=8,nTaskId=101007,sTaskName="主线-长安风云",sTaskDescription="前往长安衙府",tTalk={{0,"在下奉师傅之命来长安调查展捕快失踪一案，大人这边可有什么消息吗？",},{3,"原来是名师之后，有失远迎！此前展捕快一直在追查城中少女失踪之事…",},{4,"大人大人，牢犯铁牛逃跑了，展捕快不在，我们拦不住他们呀。。。",},{3,"这群饭桶…",},{0,"大人莫急，让在下前去协助抓人，您在此等候，想想展捕快的事!",},},sSeekNpc="刘铭扬",sBackground="caomiaocun",}
ctTaskPlotConf[101008]={nID=101008,nIndex=9,nTaskId=101008,sTaskName="主线-长安风云",sTaskDescription="捉拿逃犯铁牛",tTalk={{0,"大胆狂徒，竟敢逃跑，还不乖乖束手就擒!",},{5,"小子，别多管闲事，不想要命了，赶紧给老子走开！",},{0,"那就打一顿再说吧，哟，想不到还有接应人。",},},sSeekNpc="铁牛",sBackground="caomiaocun",}
ctTaskPlotConf[101009]={nID=101009,nIndex=10,nTaskId=101009,sTaskName="主线-长安风云",sTaskDescription="铁柱来接应",tTalk={{6,"我兄弟二人杀人如麻，身上案子多的是，不介意多你一条人命！",},{7,"大海兄，这种陈词滥调你还真是说不腻啊，老子都听几百遍了！",},{6,"卓一！平！你怎么来了，三番两次找我们兄弟的麻烦，还真以为我们怕你了？赶紧给老子让开！",},{0,"你是何人，是他们的帮手吗？",},{7,"哈哈，像我这种风流倜傥的帅哥，你觉得我会和他们同流合污吗？赶紧抓人吧！",},},sSeekNpc="铁柱",sBackground="caomiaocun",}
ctTaskPlotConf[101010]={nID=101010,nIndex=11,nTaskId=101010,sTaskName="主线-长安风云",sTaskDescription="擒拿铁柱兄弟",tTalk={{6,"你们聊得那么开心，简直不把我们两兄弟放在眼里，太嚣张了，拿命来",},{0,"呵，你这种无耻之辈怎么，还想我放在眼里，开什么国际玩笑，乖乖就束手就擒吧，嚯…",},},sSeekNpc="铁柱",sBackground="caomiaocun",}
ctTaskPlotConf[101011]={nID=101011,nIndex=12,nTaskId=101011,sTaskName="主线-长安风云",sTaskDescription="打架狂人卓不凡",tTalk={{7,"哈哈，爽，好久没有这么痛快过了。",},{7,"你这身本事，在怎么会在衙门当差？",},{0,"我是奉师命来衙门调查展捕快失踪案件的，不料遇到这俩二货...",},{7,"展捕快？前些日子他查案查到了境地那边，莫非与此有关？",},{0,"原来你们认识？既然如此，那我们去那边找找线索吧！",},{7,"不行，近来各地瘴气四起，实在蹊跷，我先带你去找个用毒的高人，到时也好救展兄呀！",},},sSeekNpc="卓不凡",sBackground="caomiaocun",}
ctTaskPlotConf[101012]={nID=101012,nIndex=13,nTaskId=101012,sTaskName="主线-境地疑云",sTaskDescription="秋田犬拦路",tTalk={{8,"谁..谁..？不许打扰仙子!",},{7,"秋田犬，每次我来找毒仙子你都拦我，但这次真有急事 ，就不与你打斗了，快放我进去!",},{8,"不行，没有仙子命令，哎哟，喂喂...别闯啊！",},{7,"别废话，我这边等着救人的。",},},sSeekNpc="秋田犬",sBackground="caomiaocun",}
ctTaskPlotConf[101013]={nID=101013,nIndex=14,nTaskId=101013,sTaskName="主线-境地疑云",sTaskDescription="初见毒仙子",tTalk={{0,"听闻姑娘医术独步天下，近来境地毒瘴四起，我们有位朋友深陷其中，想请姑娘施以援手!",},{10,"也就卓不凡敢带人来找我了，因为我救人也是用毒救的。。。",},{0,"呃，只要能救人，用什么方法不重要，还请姑娘...",},{10,"哈哈.你放心好啦，本姑娘还没失过手呢，只是...本姑娘的酒，被外面的的臭机关兽偷了去…",},{0,"那好，我现在去帮姑娘你抢回来，你暂且等候。",},},sSeekNpc="毒仙子",sBackground="caomiaocun",}
ctTaskPlotConf[101014]={nID=101014,nIndex=15,nTaskId=101014,sTaskName="主线-境地疑云",sTaskDescription="追讨毒仙子的酒",tTalk={{11,"毒仙子，又来抢酒喝，就不怕你们的师尊责罚吗？",},{0,"难道？？这酒不是姑娘的？？",},{10,"管它是谁的呢，人命重要还是一壶酒重要？",},{0,"好…我抢！",},},sSeekNpc="灵猴",sBackground="caomiaocun",}
ctTaskPlotConf[101015]={nID=101015,nIndex=16,nTaskId=101015,sTaskName="主线-境地疑云",sTaskDescription="把酒交给毒仙子",tTalk={{10,"(咕噜咕噜）呀，这酒真是好喝",},{0,"唉，我的良心备受煎熬！",},{7,"习惯就好啦，这毒仙子没了酒，那可是活不下去啊，哈哈哈！",},{0,"好吧，那咱们赶快出发，救人要紧!",},},sSeekNpc="毒仙子",sBackground="caomiaocun",}
ctTaskPlotConf[101016]={nID=101016,nIndex=17,nTaskId=101016,sTaskName="主线-境地疑云",sTaskDescription="前往境地",tTalk={{0,"（忽然听到哭声）小妹妹，你怎么一个人在这种地方，发生什么事了啊？",},{12,"前些日子醒来，发现姐姐不见了，小花循着气味到此处，只找到了一只狐妖，姐姐一定被她害死了，呜呜呜...",},{0,"难道少女失踪也是这狐狸干的好事？",},{0,"展捕快也一定在附近，哪怕这狐狸不是凶手，也不能留她祸害人间，小妹妹来，我们去帮你姐姐报仇！",},{12,"要是能帮小花的忙，我就跟着你们了！",},},sSeekNpc="小花",sBackground="caomiaocun",}
ctTaskPlotConf[101017]={nID=101017,nIndex=18,nTaskId=101017,sTaskName="主线-境地疑云",sTaskDescription="为狐妖所为",tTalk={{13,"看在同为妖的情分上放你一马，居然还敢回来送死？",},{12,"丑妖怪，还我姐姐！",},{0,"你这妖孽，长安城的少女是不是你掳走的！",},{13,"何止是少女，既然你们都来了，那我就一个不留的抓回去好了，左护法一定会很高兴的，哈哈…",},},sSeekNpc="狐妖",sBackground="caomiaocun",}
ctTaskPlotConf[101018]={nID=101018,nIndex=19,nTaskId=101018,sTaskName="主线-境地疑云",sTaskDescription="找到昏迷展捕快",tTalk={{7,"老展，老展…...看来中毒不轻，仙子，拜托你了",},{10,"这点小毒，还难不到姑奶奶，看我的！",},{10,"不过我救人时需要有人在一旁运气帮他逼毒，不然毒血倒灌，必死无疑！",},{0,"好，这个包在我身上！",},},sSeekNpc="展捕快",sBackground="caomiaocun",}
ctTaskPlotConf[101019]={nID=101019,nIndex=20,nTaskId=101019,sTaskName="主线-境地疑云",sTaskDescription="了解前因后果",tTalk={{7,"老展，你醒了？好点了吗？算你命大，这位少侠可是奉了师命千里迢迢来救你的。",},{9,"我这是...呃...感谢少侠救命之恩！",},{0,"展捕快言重了，既然狐妖已被我们除去，这案子也算结了，我们还是出去再说吧！",},{9,"那狐妖子不过是个抓人的喽啰，她背后还有更凶恶的妖怪！展某还不能离开。",},{0,"既然如此，索性一起为民除害，看看这罪魁祸首到底什么来头！",},},sSeekNpc="展捕快",sBackground="caomiaocun",}
ctTaskPlotConf[102020]={nID=102020,nIndex=21,nTaskId=102020,sTaskName="主线-境地疑云",sTaskDescription="狼狗拦路",tTalk={{14,"主人在作法，你们统统不能过去!",},{9,"小心，这是那妖怪唤来的狼狗。",},{0,"作法就作法，有什么见不得人的？还是说你们在作些伤天害理的邪法！再不让开，就让你到地府报到！",},},sSeekNpc="狼狗",sBackground="heyang",}
ctTaskPlotConf[102021]={nID=102021,nIndex=22,nTaskId=102021,sTaskName="主线-境地疑云",sTaskDescription="阻止神秘人作法",tTalk={{15,"何人敢在这放肆，不想活命了？你们到底是什么人？",},{0,"本小侠行事素来不留名！",},{15,"呵呵，那本座这里，也不在乎多一个无名之魂，哈哈！",},},sSeekNpc="神秘人",sBackground="heyang",}
ctTaskPlotConf[102022]={nID=102022,nIndex=23,nTaskId=102022,sTaskName="主线-境地疑云",sTaskDescription="听听神秘人说话",tTalk={{15,"哈哈哈，原来如此，没想到在这里也能碰到此等血脉之人。",},{0,"妖怪，你在说什么？",},{15,"小娃娃，咱们缘分未尽，早会相见的。",},{9,"妖怪--哪里跑！",},{0,"糟了，这妖怪要逃出出去去了！",},},sSeekNpc="神秘人",sBackground="heyang",}
ctTaskPlotConf[102023]={nID=102023,nIndex=24,nTaskId=102023,sTaskName="主线-巫族初现",sTaskDescription="遇见孙大圣",tTalk={{17,"站住！刚才老孙从天上望见境地妖气冲天，之后就看见你出现了，说，是不是你捣的鬼！",},{0,"你是..孙大圣？？现在是斗胜佛祖了吧？",},{17,"老孙是不是成佛，你说句假话试试看？",},{0,"大圣千万别误会！我们进入境地 ，就是在追查这个用巫术害人的妖怪 ，只可惜被他逃了去了。",},{17,"逃了？看你身上无邪气，老孙就信你一次，但为了以绝后患，那妖人不可不除！",},{0,"大圣放心！晚辈一定让那要人伏法！！",},},sSeekNpc="孙悟空",sBackground="heyang",}
ctTaskPlotConf[102024]={nID=102024,nIndex=25,nTaskId=102024,sTaskName="主线-巫族初现",sTaskDescription="向贵妇人打听消息                                                                                                                                                                                            ",tTalk={{0,"恩，这位大姐！",},{18,"说是你大姐呢，我很老吗！！",},{7,"小姐，别生气，我们是有事而来！",},{18,"你才是小姐，你全家都是小姐，哎，我说你们到底会不会说话的呀！",},{0,"好了好了，别吵了，我们只是想问下您，近来境地周围有什么可疑之人，",},{18,"不知道，我家小哈刚被隔壁家的小狐狸咬伤，正愁没有止血散呢，哪有闲心帮你想这些乱七八糟的事呀！",},{0,"我给你取来！",},},sSeekNpc="贵妇人",sBackground="heyang",}
ctTaskPlotConf[102025]={nID=102025,nIndex=26,nTaskId=102025,sTaskName="主线-巫族初现",sTaskDescription="帮贵妇人找止血散",tTalk={{18,"呀，止血散？给我家小哈的？？哇，现在药材这么紧缺，少侠还能弄到这些药，真是太有心了！",},{7,"（嘀咕一声）唉，这年头，真是人不如狗呀！",},{18,"（白了卓不凡一眼）刚才少侠是想问，这附近有没有可疑的人吧？",},{0,"正是如此！",},{18,"这两天呀，还真是看到过一个神秘女子经常出现，也不知道干嘛的…",},},sSeekNpc="贵妇人",sBackground="heyang",}
ctTaskPlotConf[102026]={nID=102026,nIndex=27,nTaskId=102026,sTaskName="主线-巫族初现",sTaskDescription="发现一名神秘女子",tTalk={{0,"（见到一神秘女子）大白天鬼鬼祟祟的，喂，是不是想搞事情呀！",},{16,"你是哪位？",},{0,"你的演技倒是十分逼真，最好快说出那妖人的下落，不然有你好果子吃！",},},sSeekNpc="神秘女子",sBackground="menpaidian",}
ctTaskPlotConf[102027]={nID=102027,nIndex=28,nTaskId=102027,sTaskName="主线-巫族初现",sTaskDescription="问神秘女子巫术之事",tTalk={{0,"说吧，你在这里鬼鬼祟祟的想干嘛？是不是想接应那施展巫术的妖人",},{16,"啥？妖人？巫术？什么乱起八糟的？",},{7,"唉，有些人就是不打不行，喜欢装蒜…",},{16,"（手一指)看，有只狗！",},{7,"（转过头）好啊，大白天的，想骗我，这样的伎俩...简直找打!",},{16,"嘿嘿，那你还回头？我闪了。",},{7,"别跑。。",},},sSeekNpc="神秘女子",sBackground="qingyunmen",}
ctTaskPlotConf[102028]={nID=102028,nIndex=29,nTaskId=102028,sTaskName="主线-巫族初现",sTaskDescription="追神秘女子至皇门外",tTalk={{19,"站住，什么人，前面就是皇城了，敢在这乱逛，不想要命啦？",},{0,"差大哥您别误会，我们是在捉一个行迹可疑的女子至此的，若被他混进皇城，那就麻烦了",},{19,"恩？？那是要我放你进去查一下啊？",},{0,"这位大哥真是深明大义呀！",},{19,"呸，这种几百年前就有人用的烂借口还敢摆出来，想混进皇城的是你吧——把他们给我拿下！",},},sSeekNpc="皇宫禁卫",sBackground="qingyunmen",}
ctTaskPlotConf[102029]={nID=102029,nIndex=30,nTaskId=102029,sTaskName="主线-巫族初现",sTaskDescription="兴奋的卓不凡",tTalk={{7,"老子今天把吃皇粮的揍了一顿，爽啊…",},{0,"唉，打什么呀，这下神秘女子连个鬼影都没了…咱们还是走吧！",},{7,"当朝宰相房玄龄大人曾密令我办过不少案子，咱们去找下他，或许有法子进皇城。",},{0,"你这贱客，不早说…",},{7,"因为我老卓为人低调啊！",},{0,"我看你就是想来闹事。",},},sSeekNpc="卓不凡",sBackground="qingyunmen",}
ctTaskPlotConf[102030]={nID=102030,nIndex=31,nTaskId=102030,sTaskName="主线-巫族初现",sTaskDescription="前往房府大院",tTalk={{7,"哎哎！那个谁...谁，我们来找房大人，快进去禀报！",},{20,"你哪位呀，这么大口气，我们老爷是你想见就能见得吗？有预约吗？",},{7,"嘿，还预约？我，你卓大爷，威名远播--你不认识我？",},{20,"不认识，怎么样？",},{0,"贱客呀，要是你的面子不好使，还是老方法吧。再唠叨追兵可要到了…",},{7,"行，你先让开点，嚯..我、弄、死、你！",},},sSeekNpc="房府家丁",sBackground="qingyunmen",}
ctTaskPlotConf[102031]={nID=102031,nIndex=32,nTaskId=102031,sTaskName="主线-巫族初现",sTaskDescription="拜见宰相房玄龄",tTalk={{24,"我说外面怎么那么吵，原来是卓大侠登门，看来本官又要换上几个门丁了…",},{0,"适才鲁莽了，请大人见谅！只是事出紧急，我们务必要见大人一面。",},{24,"长安城有什么风吹草动都少不了卓不凡，境地的事本官早有耳闻，不知二位有何事需要帮忙？",},{0,"境地的事我们已查到了些眉目，并寻到了一名神秘女子，只可惜，被她轻易混进了宫中。",},{24,"皇上近来久未早朝，此人对宫中如此熟悉的话，难道…不好，本官要马上进宫面圣！",},{0,"房大人，此事非同小可，能否带我们二人进宫，或许能帮上什么忙?",},{24,"如此甚好，那就劳烦几位了！",},},sSeekNpc="房玄龄",sBackground="qingyunmen",}
ctTaskPlotConf[102032]={nID=102032,nIndex=33,nTaskId=102032,sTaskName="主线-巫族初现",sTaskDescription="进宫面圣",tTalk={{0,"皇上久未早朝，事有蹊跷。正所谓事急从权，若皇上有什么闪失，你们担待得起吗？",},{21,"哼，皇上在宫里安安稳稳的，能有什么事，你是不是想搞事情！",},{0,"我看你才想搞事情吧，推来推去的，说，你是不是那妖人的同党，不然要你狗命。",},},sSeekNpc="禁卫统领",sBackground="qingyunshan",}
ctTaskPlotConf[102033]={nID=102033,nIndex=34,nTaskId=102033,sTaskName="主线-巫族初现",sTaskDescription="击败巫十三",tTalk={{21,"难怪境地的计划会被你们破坏，果然难缠!既然左护法收拾不了你们，就让我来了结吧！",},{24,"居然是巫族的人，没想到你们这些妖孽，竟还潜藏于世！",},{0,"怪不得我们觐见皇上，你们会推三阻四，原来有猫腻...妖人，受死吧！",},},sSeekNpc="巫十三",sBackground="qingyunshan",}
ctTaskPlotConf[102034]={nID=102034,nIndex=35,nTaskId=102034,sTaskName="主线-巫族初现",sTaskDescription="找房玄龄商量计策",tTalk={{24,"想不到皇上身边，竟有巫族潜伏，实在是令人后怕啊！",},{0,"房大人，现在妖人 虽死，但皇上仍未好转，这如何是好？",},{24,"本官也一筹莫展呀，方才看那妖人似乎很忌惮少侠，莫非少侠有什么辟邪祛凶的本事？",},{0,"这..在下就不知道了.",},{0,"不过，似乎自从师门出来之后，这些妖怪对我伤口流出来的血液，有些忌惮。",},},sSeekNpc="房玄龄",sBackground="qingyunshan",}
ctTaskPlotConf[102035]={nID=102035,nIndex=36,nTaskId=102035,sTaskName="主线-巫族初现",sTaskDescription="毒仙子有法子救皇上",tTalk={{10,"我有法子救皇上，房大人可否让我一试？",},{24,"这御医也束手无策，姑娘若是有方法，那可真是大功一件啊！",},{0,"喂，你该不会又要下毒救人吧？这次可是当今圣上！",},{10,"我又没说用毒…你有没想过，这些妖魔鬼怪为何忌惮于你？",},{0,"恩，你这一说，我还真没...想过!",},{10,"我怀疑你的血脉异于常人，或者有某种破邪的效果也说不定。",},{0,"我怎么觉得你想坑我呀，有什么法子能救皇上， 说来听听？",},{10,"我要三钱一点红做药引，然后放你一点点血来炼制解药，不会让你死的，放心！",},{0,"老实说，你的用词让我好怕怕…",},},sSeekNpc="毒仙子",sBackground="qingyunshan",}
ctTaskPlotConf[102036]={nID=102036,nIndex=37,nTaskId=102036,sTaskName="主线-巫族初现",sTaskDescription="买药又遇到神秘女子",tTalk={{16,"怎么又是你们，阴魂不散的！",},{0,"嘿嘿，踏破铁鞋无觅处，得来全不费工夫！你的同伙已经落网了，你还不快束手就擒！",},},sSeekNpc="神秘女子",sBackground="qingyunmen",}
ctTaskPlotConf[102038]={nID=102038,nIndex=38,nTaskId=102038,sTaskName="主线-巫族初现",sTaskDescription="又被神秘女子忽悠了",tTalk={{16,"哼，简直就是放屁，我现在要去办正经事，没空理你！",},{0,"想跑？没门！",},{16,"哎哟看快，有只飞天猪（手指向空中）",},{0,"哪？？可恶，又骗我，下次再让我遇到你看看，气死我了！！",},},sSeekNpc="神秘女子",sBackground="dazhulin",}
ctTaskPlotConf[102039]={nID=102039,nIndex=39,nTaskId=102039,sTaskName="主线-巫族初现",sTaskDescription="神秘女子道出真身份",tTalk={{28,"喂，你们不要太过分了哈，我只是查妖乱而已，哪知道你们什么破巫术，是不是有病呀？",},{28,"不对，我可是御史台的人，为何要怕你们，耽误我调查妖乱的事，你们吃不了兜着走！",},{7,"呀，如果她真是御史台的人，咱们可惹不起呀，这些人都是皇上手下的，杀人都是先斩后奏的哦！",},{28,"嘿！看暗器——！",},{7,"又想骗本大侠，我连看都不看，来啊来啊镖我啊！咦…人呢？小贼，又耍我！！！",},},sSeekNpc="简陌",sBackground="dazhulin",}
ctTaskPlotConf[102040]={nID=102040,nIndex=40,nTaskId=102040,sTaskName="主线-巫族初现",sTaskDescription="继续去买药",tTalk={{0,"唉，真晦气！掌柜的，快给我来点一点红，我有急用!",},{23,"客官不好意思，一点红刚刚被人全买走了。",},{0,"这么偏门的药材也那么抢手？能告知我是何人买走的吗？",},{23,"就是那边那位客官，要不您与她商量一下，让一些给你？",},{0,"咦？这人怎么那么眼熟。",},},sSeekNpc="孙百年",sBackground="dazhulin",}
ctTaskPlotConf[102041]={nID=102041,nIndex=41,nTaskId=102041,sTaskName="主线-巫族初现",sTaskDescription="击败抢药少女",tTalk={{0,"这位姑娘，能否让我一点红给我，我有急用，价钱好商量。",},{107,"竟然来找一点红，看来你们见过皇上了，哼，这次可不能再让你们坏了大事！",},{0,"原来是你这狐妖，没想到你还敢在长安出现，看我不把你打回原形！",},},sSeekNpc="抢药少女",sBackground="dazhulin",}
ctTaskPlotConf[102042]={nID=102042,nIndex=42,nTaskId=102042,sTaskName="主线-巫族初现",sTaskDescription="赶回去抽血配药",tTalk={{0,"（脸色苍白）给，我的血，还有这些一点红，够了吧？",},{10,"够了够了，你还真是玩命呀，这些血够配十次解药了。",},},sSeekNpc="毒仙子",sBackground="dazhulin",}
ctTaskPlotConf[102043]={nID=102043,nIndex=42,nTaskId=102043,sTaskName="主线-巫族初现",sTaskDescription="突然有人出现",tTalk={{0,"谁？在那躲着干嘛，快出来！",},},sSeekNpc="毒仙子",sBackground="dazhulin",}
ctTaskPlotConf[102044]={nID=102044,nIndex=43,nTaskId=102044,sTaskName="主线-巫族初现",sTaskDescription="出来阻止配药神秘人",tTalk={{15,"呵呵呵…巫十三那家伙，真是沉不住气，现在你气血不足，看你怎么逃出我的手掌心！",},},sSeekNpc="神秘人",sBackground="dazhulin",}
ctTaskPlotConf[102045]={nID=102045,nIndex=44,nTaskId=102045,sTaskName="主线-巫族初现",sTaskDescription="击败神秘人",tTalk={{0,"你这妖人竟然还敢来，仙子，你先配药救人，我来拦他！",},{15,"看你有没那本事，哼！",},},sSeekNpc="神秘人",sBackground="dazhulin",}
ctTaskPlotConf[102046]={nID=102046,nIndex=45,nTaskId=102046,sTaskName="主线-巫族初现",sTaskDescription="拷问神秘人",tTalk={{7,"说！你究竟是什么人，谁指使你们加害皇上的，有何阴谋？",},{15,"呵呵呵呵...呃...噗..",},{7,"呀呀，喂，你别死呀，打个架还提前服毒，万一你赢了岂不白死…",},},sSeekNpc="神秘人",sBackground="dazhulin",}
ctTaskPlotConf[102047]={nID=102047,nIndex=46,nTaskId=102047,sTaskName="主线-巫族初现",sTaskDescription="向房大人问话",tTalk={{0,"房大人，妖人已伏法，可惜没有问出线索，不知皇上龙体如何？",},{24,"皇上服药后，渐渐恢复了神智，但仍有些虚弱，虽然本官再三劝诫，但圣上还是坚持要见少侠一面！",},{0,"既然如此， 那在下就冒昧面圣了！",},},sSeekNpc="房玄龄",sBackground="dazhulin",}
ctTaskPlotConf[102048]={nID=102048,nIndex=47,nTaskId=102048,sTaskName="主线-巫族初现",sTaskDescription="觐见太宗李世民",tTalk={{25,"果然是英雄年少、意气风发呀，此番大劫，若非少侠，朕大限将至矣！",},{0,"蒙皇上鸿恩！圣上乃人中之龙，自有天佑，绵薄之力，何足挂齿！",},{25,"居功而不自傲，真乃果之栋梁呀，若能报效我大唐，平定四方妖乱，何愁天下不兴呀？只是说到这妖乱…",},{0,"不知圣上为何事担忧，可否说与小民一听？",},{25,"近日境地异相不断，朕派御史台调查此事，却苦无进展。少侠身手不凡，可否助她一臂之力，解救黎民？",},{0,"御史台？？..呵呵，真是冤家路窄。",},},sSeekNpc="李世民",sBackground="dazhulin",}
ctTaskPlotConf[102049]={nID=102049,nIndex=48,nTaskId=102049,sTaskName="主线-查探消息",sTaskDescription="找简陌了解情况",tTalk={{0,"咳咳，辣个，之前误会姑娘了。请问，境地发生什么怪异的事？",},{28,"哼，还好意思问，近来出现僵尸四处伤人，本姑娘正烦着呢！",},{0,"哈？？僵尸?",},{28,"本来我已追踪到一个叫沫熙的僵尸了，谁知被你吓跑了，你说让我怎么查？",},{0,"呃...既然如此，我便去给你查清楚。",},{28,"一言为定，我暂且留在长安城继续追踪沫熙行踪。",},},sSeekNpc="简陌",sBackground="dazhulin",}
ctTaskPlotConf[102050]={nID=102050,nIndex=49,nTaskId=102050,sTaskName="主线-查探消息",sTaskDescription="士兵的检查",tTalk={{26,"来人止步，境地全面封锁，闲杂人等不得出入！",},{7,"我们像闲杂人等吗？我们是奉命来调查僵尸一案的。",},{0,"令牌？什么令牌？",},{26,"呀，没有令牌还想混进来，敢戏弄兵爷，兄弟们给我打！",},{7,"哎呀，觉得我卓某人好欺负是吧？",},{0,"他们要打便打，速战速决，赶紧离开。",},},sSeekNpc="国境士兵",sBackground="dazhulin",}
ctTaskPlotConf[102051]={nID=102051,nIndex=50,nTaskId=102051,sTaskName="主线-查探消息",sTaskDescription="惊动程将军",tTalk={{27,"谁在闹事，置我大唐君威何在？",},{0,"这位将军，误会呀，我们是奉命来调查僵尸事件的，只是来得急，尚未拿到通行令牌而已！",},{27,"没有通行令就无法证明你们的身份！",},{0,"好，那我们回长安城拿令牌！",},{27,"想走？来人，把这些搞事的给我抓起来！",},},sSeekNpc="程将军",sBackground="qingyunmen",}
ctTaskPlotConf[102052]={nID=102052,nIndex=51,nTaskId=102052,sTaskName="主线-查探消息",sTaskDescription="与程将军对话",tTalk={{0,"将军这是何意？",},{27,"擅闯封锁线，又出手伤人，还想轻易离开，我君岂不是让人笑话！",},{0,"只怕拦我，你们的脸丢的更大！",},},sSeekNpc="程将军",sBackground="qingyunmen",}
ctTaskPlotConf[102053]={nID=102053,nIndex=52,nTaskId=102053,sTaskName="主线-查探消息",sTaskDescription="简陌的消息",tTalk={{28,"将军停手，这位少侠真是来调查僵尸事件的，令牌我已带来了！",},{27,"看你们有令牌的份上，这次就放过你们了！",},{0,"简陌姑娘来此，难道是找到沫熙了？",},{28,"在长安并没有找到她，我怀疑她在境地。",},{0,"既然如此，我们就分开行动，各自打探消息，届时相互通知！",},},sSeekNpc="简陌",sBackground="qingyunmen",}
ctTaskPlotConf[102054]={nID=102054,nIndex=53,nTaskId=102054,sTaskName="主线-查探消息",sTaskDescription="无法砍柴的樵夫",tTalk={{29,"（自言自语）唉，唯一的谋生工具丢了，又要提防被僵尸咬，这地方是没法待了！",},{0,"僵尸？这位大哥，僵尸何在？或许我能帮上忙。",},{29,"僵尸呀，就在…可是俺的斧头不见了。",},{0,"那我帮你找回斧头，你告知我僵尸在哪如何？",},{29,"好，多谢少侠！",},},sSeekNpc="樵夫",sBackground="qingyunmen",}
ctTaskPlotConf[102056]={nID=102056,nIndex=54,nTaskId=102056,sTaskName="主线-查探消息",sTaskDescription="归还斧头给樵夫",tTalk={{29,"我心爱的斧头终于回来了，太感动了。",},{0,"你还真是用情至深呀..现在该告诉我僵尸在哪了吧？",},{29,"啊，当然！之前老林的儿子被一个女僵尸咬了，现在可能变成僵尸了呢!",},{0,"女僵尸？难道是沫熙？看来要去问清楚了。",},},sSeekNpc="樵夫",sBackground="qingyunmen",}
ctTaskPlotConf[102057]={nID=102057,nIndex=55,nTaskId=102057,sTaskName="主线-僵尸内幕",sTaskDescription="寻找老林",tTalk={{0,"老林，听说你儿子被僵尸咬了，可否…",},{30,"来福！旺！财！",},{22,"汪汪汪...",},{0,"哎，你听我把话说完！",},},sSeekNpc="老林",sBackground="qingyunmen",}
ctTaskPlotConf[102058]={nID=102058,nIndex=56,nTaskId=102058,sTaskName="主线-僵尸内幕",sTaskDescription="击退来福",tTalk={{0,"我为僵尸之事而来，并无恶意！",},{30,"来福！旺！财！，咬他。",},{22,"汪汪汪...",},},sSeekNpc="来福",sBackground="qingyunmen",}
ctTaskPlotConf[102059]={nID=102059,nIndex=57,nTaskId=102059,sTaskName="主线-僵尸内幕",sTaskDescription="老林儿子林沐生",tTalk={{0,"老林，你别紧张呀，我们是来帮你的，你儿子说不定能治好呢！",},{30,"真的？ 刚才得罪了！",},{0,"你先和我说说你儿子的情况。",},{30,"唉。我家沐白被一个女子迷得神魂颠倒，没想到这女的是个僵尸，还咬了沐白，这不，现在神智不清了。",},{30,"但我发现我儿子只要能吸血就会恢复正常，家里的牲口都被他吸尽血液了，现在我只能把他关起来。",},{0,"果然和地府的僵尸不一样，咱们去看看这个小刘吧！",},{30,"我儿子现在神志不清，会攻击看到的东西，各位小心呀！",},},sSeekNpc="老林",sBackground="qingyunmen",}
ctTaskPlotConf[102060]={nID=102060,nIndex=58,nTaskId=102060,sTaskName="主线-僵尸内幕",sTaskDescription="制伏发狂的林沐生",tTalk={{31,"血？血！哈哈哈，是血，快给我！快给我血！！",},{0,"果然理智全无，看来要让你冷静冷静了，得罪！",},},sSeekNpc="林沐生",sBackground="qingyunmen",}
ctTaskPlotConf[102061]={nID=102061,nIndex=59,nTaskId=102061,sTaskName="主线-僵尸内幕",sTaskDescription="毒仙子有法子了",tTalk={{10,"传统的僵尸是死人的怨气而变，身躯虽然硬但是可以消灭的，而且一般的僵尸是没有神智的。",},{0,"说重点！",},{10,"别急嘛，林沐生这个是活人中了尸毒，至于为何吸血后能维持理智这让我很费解，并且还杀不死，这点和沫熙很像。",},{0,"你还直接告诉我如何救小刘比较好。",},{10,"恩恩，找些琉璃草来给我炼药我有把握解除这尸毒，当然也少不了你的血液。",},{0,"你笑得那么贼，肯定还有别的方法代替我的血液对不对？",},{10,"唔，今天天气不错!",},},sSeekNpc="毒仙子",sBackground="qingyunmen",}
ctTaskPlotConf[102062]={nID=102062,nIndex=60,nTaskId=102062,sTaskName="主线-僵尸内幕",sTaskDescription="跳出一个女贼",tTalk={{0,"你是谁，干嘛拦路？",},},sSeekNpc="女贼",sBackground="qingyunmen",}
ctTaskPlotConf[102063]={nID=102063,nIndex=61,nTaskId=102063,sTaskName="主线-僵尸内幕",sTaskDescription="教训女贼",tTalk={{32,"你怎么摘我的草药？",},{0,"这草药明明是野生的，怎么就成你的了？？",},{32,"少废话，这里的所以东西偶是我的，包括你身上的值钱的。",},{0,"喔，有本事你就来拿呗！",},},sSeekNpc="女贼",sBackground="qingyunmen",}
ctTaskPlotConf[102064]={nID=102064,nIndex=62,nTaskId=102064,sTaskName="主线-僵尸内幕",sTaskDescription="毒仙子配制解药",tTalk={{10,"喏，把这个给小刘服下就好啦。",},{0,"怎么就一颗解药？我明明看你炼了很多颗的呀，你是不是克扣出来了。",},{10,"唔，今天天气不错。",},},sSeekNpc="毒仙子",sBackground="qingyunmen",}
ctTaskPlotConf[102065]={nID=102065,nIndex=63,nTaskId=102065,sTaskName="主线-僵尸内幕",sTaskDescription="林沐生服下解药",tTalk={{31,"多谢恩公的解药，把我从生不如死的状态中解救出来。",},{0,"你怎么知道是我给你吃解药的？",},{31,"那时我虽神志不清，但还是有点记忆的。",},{0,"那你还你记得你是在哪里被谁咬的吗？",},{31,"就在西边，一个叫沫熙的女子咬了我。话说恩公你的药烈性好呀，我的身体好虚弱啊！",},{10,"那个，你再调理一下身体就好啦，用血液调制的，药性的确烈了点，哈哈！",},{0,"果然是有东西可以代替我的血液，你坑我！！",},{10,"唔，今天天气不错。",},},sSeekNpc="林沐生",sBackground="qingyunmen",}
ctTaskPlotConf[102066]={nID=102066,nIndex=64,nTaskId=102066,sTaskName="主线-僵尸内幕",sTaskDescription="击退拦路的僵尸",tTalk={{7,"走，去西边了解下僵尸的情况的。",},{7,"快看，好多僵尸啊，怕是我的银剑不够用了。。。。",},},sSeekNpc="僵尸",sBackground="qingyunmen",}
ctTaskPlotConf[102067]={nID=102067,nIndex=65,nTaskId=102067,sTaskName="主线-僵尸内幕",sTaskDescription="心疼卓不凡",tTalk={{7,"我的天，真的没完没了，怎么办呀？我的剑都快砍坏了，心疼！",},{0,"那就换一把呀，真是猪一样的队友！",},{7,"啊，我身上只剩一把银剑了啊！",},{0,"淫贱？哈哈，很适合你用！",},{7,"我…",},{28,"快过来这边，不要被僵尸围堵了。",},},sSeekNpc="卓不凡",sBackground="qingyunmen",}
ctTaskPlotConf[102068]={nID=102068,nIndex=66,nTaskId=102068,sTaskName="主线-异变",sTaskDescription="简陌调查到真相了",tTalk={{28,"我查到了，僵尸都是沫熙咬了后转化而成的，普通僵尸只会吸血，不会把人转化成僵尸的。",},{0,"这么说，沫熙是罪魁祸首咯？",},{28,"完全没错，所以要解决这件事必须得从沫熙身上下手。",},{0,"可是我们现在不知道沫熙在哪呀，而且也不能放任这些普通僵尸不管呀。",},{28,"这样，我先去找沫熙的踪迹，你去找程将军帮忙，让他派兵前来隔离这些僵尸。",},},sSeekNpc="简陌",sBackground="qingyunmen",}
ctTaskPlotConf[102069]={nID=102069,nIndex=67,nTaskId=102069,sTaskName="主线-异变",sTaskDescription="寻找程将军的的帮忙",tTalk={{27,"你们的计划简陌已经告诉我了，不过我的兵力都安排在剿匪上了，暂时还腾不出人手来。",},{7,"（小声）这家伙不会是在公报私仇吧？",},{0,"（小声）有可能。",},{27,"哼，本将军岂是这种公私不分之人，不信你随本将军一同剿匪，事后我自然派兵助你们隔离僵尸。",},{0,"一言为定。",},},sSeekNpc="土匪",sBackground="qingyunmen",}
ctTaskPlotConf[102070]={nID=102070,nIndex=68,nTaskId=102070,sTaskName="主线-异变",sTaskDescription="镇压土匪归来",tTalk={{27,"这群土匪让我头疼了很久，有你们帮忙，果然轻松解决，哈哈哈。",},{0,"你故意激怒我们帮你剿匪的",},{27,"咳咳，我们这是互相帮忙嘛，我这就派兵去隔离僵尸。",},{10,"慢，将军可以用琉璃草和马钱子煮水，让那些隔离的僵尸服下，自然变回普通人了。",},{0,"果然有代替我血液的东西，这下你还不承认。",},{10,"嘻嘻，这血液炼丹可是好东西呀，能坑则坑呗。",},{0,"你…",},{27,"简陌传来消息，说有事找你，赶紧去吧！",},},sSeekNpc="程将军",sBackground="qingyunmen ",}
ctTaskPlotConf[102071]={nID=102071,nIndex=69,nTaskId=102071,sTaskName="主线-异变",sTaskDescription="简陌找你有事",tTalk={{0,"简陌姑娘这么急找我，莫非是追查到了沫熙的行踪？",},{28,"当然，大唐御史台第一大美女出马 有什么事情能难到我的？",},{0,"说重点！！",},{28,"我查到沫熙现在就在西边的亭子外。",},},sSeekNpc="简陌",sBackground="qingyunmen",}
ctTaskPlotConf[102072]={nID=102072,nIndex=70,nTaskId=102072,sTaskName="主线-异变",sTaskDescription="找到沫熙",tTalk={{33,"怎么又是你们？混蛋，计划都让你们搅和了。",},{0,"混蛋说谁呢？",},{33,"混蛋说你！",},{0,"喔，那算是被狗咬好了！",},{33,"啊，你竟敢戏弄本吸血鬼，我杀了你！",},{0,"吸血鬼是什么鬼！！",},},sSeekNpc="沫熙",sBackground="qingyunmen",}
ctTaskPlotConf[103070]={nID=103070,nIndex=71,nTaskId=103070,sTaskName="主线-异变",sTaskDescription="击败沫熙",tTalk={{33,"你们对力量一无所知!",},},sSeekNpc="沫熙",sBackground="qingyunmen",}
ctTaskPlotConf[103071]={nID=103071,nIndex=72,nTaskId=103071,sTaskName="主线-异变",sTaskDescription="质问简陌",tTalk={{0,"你干什么？为什么要杀了她，她都要招供了！",},{28,"我们御史台的办事方式还轮不到你来管！",},{0,"我看你是心理有鬼吧，沫熙临死前可是叫出了你的名字。",},{28,"我简陌鼎鼎大名，被人知晓有什么好奇怪的。懒得跟你废话，我回去向皇上复命了。",},},sSeekNpc="简陌",sBackground="qingyunshan",}
ctTaskPlotConf[103072]={nID=103072,nIndex=73,nTaskId=103072,sTaskName="主线-异变",sTaskDescription="毒仙子的忧心",tTalk={{0,"仙子为何心事重重？",},{10,"长安和境地发生的事都与简陌跟沫熙有关，我觉得简陌是在杀人灭口!",},{7,"那还等什么，我们去抓简陌呀！",},{0,"不妥，这只是我们的猜测，还是先回去禀报皇上吧！",},},sSeekNpc="毒仙子",sBackground="qingyunshan",}
ctTaskPlotConf[103073]={nID=103073,nIndex=74,nTaskId=103073,sTaskName="主线-异变",sTaskDescription="向李世民复命",tTalk={{25,"唉，想不到朕迷糊的这段时间发生了这么多事，如今总算是解决一件了！",},{0,"若是皇上神智清醒，那妖邪定不敢作乱。",},{25,"唉，真是祸事不断，现在边境有麻烦…",},},sSeekNpc="李世民",sBackground="qingyunmen",}
ctTaskPlotConf[103074]={nID=103074,nIndex=75,nTaskId=103074,sTaskName="主线-战事告急",sTaskDescription="主动请缨",tTalk={{0,"皇上有天策府在，边境能遇到什么麻烦？",},{25,"扶桑勾结波斯入侵我大唐西部。他们的人能像巫族那般使用妖法。",},{0,"肯定是有巫族的人混在扶桑和波斯人中。皇上，既然此事牵扯到巫族，我愿前往一探究竟。",},{25,"如此正好，秦琼将军正负责此事，你听他的安排即可。",},{0,"这是自然！",},},sSeekNpc="李世民",sBackground="qingyunmen",}
ctTaskPlotConf[103075]={nID=103075,nIndex=76,nTaskId=103075,sTaskName="主线-战事告急",sTaskDescription="秦琼的疑惑",tTalk={{0,"秦将军，我奉皇上之命来查探巫族之事。",},{34,"上战场是有危险的，况且战场上如若混有巫族，没有点真本事可是…若你能打败我手下的亲卫，我再做安排。",},{0,"没问题，请！",},},sSeekNpc="秦琼亲卫",sBackground="qingyunmen",}
ctTaskPlotConf[103076]={nID=103076,nIndex=77,nTaskId=103076,sTaskName="主线-战事告急",sTaskDescription="秦琼的赏识",tTalk={{34,"少侠果然好本事呀，看来我们这次退敌大有希望了。战场上若有巫族，就请少侠除去，若无，你便帮我军突袭敌方的主将。",},{0,"万军之中取敌将首级，听起来就很刺激呀！",},{34,"只要敌将一死群龙无首，后面的小兵小将就容易对付了，请少侠尽快前往啊。",},{0,"好，那在下告辞！",},},sSeekNpc="秦琼",sBackground="qingyunmen",}
ctTaskPlotConf[103077]={nID=103077,nIndex=78,nTaskId=103077,sTaskName="主线-战事告急",sTaskDescription="前往报到",tTalk={{35,"来者何人，不得擅闯我军大营！",},{0,"这位军爷，秦将军派我前来查探战争中有巫族作祟事件。",},{35,"京城来人？最近很多奸细企图混进我军大营，你先去杀几个扶桑兵我看看",},{7,"有没搞错，这样还要证明？看来你们比我们江湖人还流氓呀！",},},sSeekNpc="边境军官",sBackground="qingyunmen",}
ctTaskPlotConf[103078]={nID=103078,nIndex=79,nTaskId=103078,sTaskName="主线-波斯阴谋",sTaskDescription="击败扶桑军",tTalk={{0,"走吧，不说那么多，先去杀几个扶桑兵。",},{7,"来，一起战个痛快!",},},sSeekNpc="扶桑军",sBackground="qingyunmen",}
ctTaskPlotConf[103079]={nID=103079,nIndex=80,nTaskId=103079,sTaskName="主线-波斯阴谋",sTaskDescription="搜到一封扶桑密信",tTalk={{0,"咦，这有一封信，原来真有秘密…",},{7,"果然有巫族，还潜伏到大营旁布阵，我们直接杀过去，把他们的布阵破坏！",},{0,"不行，能不能别蛮干想点可靠的办法呀！",},{10,"要不我们假扮成扶桑军去送信，先探下内幕再动手？",},{0,"这主意不错，我们先搞清楚巫族的阴谋！",},{7,"到头还不是要动手…",},},sSeekNpc="卓不凡",sBackground="qingyunmen",}
ctTaskPlotConf[103080]={nID=103080,nIndex=81,nTaskId=103080,sTaskName="主线-波斯阴谋",sTaskDescription="与波斯巫师的对话",tTalk={{0,"巫师，将军命我前来看看阵法布置得如何了！",},{36,"扶桑人就这么不相信我们大波斯吗？请回复川吉将军，阵法已布置妥当了.",},{0,"好的。巫师，这阵法好像很厉害的样子呀，有什么作用呀？",},{36,"赐阵法能让战死的人短暂地复活一段时间，根据阵法指引冲击大唐大营！",},{36,"不对，川吉将军的人怎会不知阵法作用，你们是谁？难道是大唐的人？？",},{0,"答对啦，你这歹人，在此祸害人间，看我们不收拾你。",},},sSeekNpc="波斯巫师",sBackground="qingyunmen",}
ctTaskPlotConf[103081]={nID=103081,nIndex=82,nTaskId=103081,sTaskName="主线-波斯阴谋",sTaskDescription="击败波斯巫师",tTalk={{7,"能动手就别吵吵。",},},sSeekNpc="波斯巫师",sBackground="qingyunmen",}
ctTaskPlotConf[103082]={nID=103082,nIndex=83,nTaskId=103082,sTaskName="主线-波斯阴谋",sTaskDescription="毒仙子发话",tTalk={{10,"穷寇莫追，扶桑的主将是叫川吉，巫族是混在波斯这边的，我们当务之急是破阵，根据灵气走向，大阵应该是在那边！",},{0,"你好像什么都知道！",},{10,"要是有壶好酒的话，我会知道的更多。",},},sSeekNpc="毒仙子",sBackground="qingyunmen",}
ctTaskPlotConf[103083]={nID=103083,nIndex=84,nTaskId=103083,sTaskName="主线-波斯阴谋",sTaskDescription="破坏阵基",tTalk={{0,"阵基就在这里，交给我吧！",},},sSeekNpc="巫师守卫",sBackground="qingyunmen",}
ctTaskPlotConf[103084]={nID=103084,nIndex=85,nTaskId=103084,sTaskName="主线-波斯阴谋",sTaskDescription="返回禀报军官",tTalk={{35,"什么！波斯居然被巫族控制了？",},{0,"我等已将他们的邪阵摧毁了，但是不知他们还有什么动作。",},{35,"事关重大，几位还是直接和我们的将军说明吧！",},},sSeekNpc="边境军官",sBackground="qingyunmen",}
ctTaskPlotConf[103085]={nID=103085,nIndex=86,nTaskId=103085,sTaskName="主线-波斯阴谋",sTaskDescription="面见李靖",tTalk={{0,"将军，波斯…（把情况如实相告）。",},{38,"巫族居然能复活死人，简直是邪术。现在要趁他们阵法被破，打他们个措手不及才好。",},{0,"那将军打算如何处理？",},{38,"我让大军在战场上牵制住扶桑联军，少侠去偷袭敌营进行斩首行动如何？",},{0,"将军果然是高啊！这个任务就放心交给我吧！",},},sSeekNpc="李靖",sBackground="qingyunmen",}
ctTaskPlotConf[103086]={nID=103086,nIndex=87,nTaskId=103086,sTaskName="主线-波斯之乱",sTaskDescription="埋伏的川吉将军",tTalk={{39,"坏我好事，我要将你碎尸万段！",},{0,"正找你们呢，还送上门来，那就先斩你这个扶桑将军，再灭主将暴怒。",},},sSeekNpc="川吉将军",sBackground="qingyunmen",}
ctTaskPlotConf[103087]={nID=103087,nIndex=88,nTaskId=103087,sTaskName="主线-波斯之乱",sTaskDescription="卓不凡的计划",tTalk={{7,"现在该去找那波斯老儿了，但大军正赶往战场，我们走大路的话太容易被对方发现了。",},{0,"你说的有道理，但是现在我们也没别的选择了。",},{7,"嘿嘿，我们可以抄小路啊，行脚商人经常游离边境，可以向他们打听打听。",},{0,"哇塞！剑客这次那么醒目，不错哟！",},{7,"必须的，毕竟碰到大军也蛮干不过他们嘛，嘻嘻！",},},sSeekNpc="卓不凡",sBackground="qingyunmen",}
ctTaskPlotConf[103088]={nID=103088,nIndex=89,nTaskId=103088,sTaskName="主线-波斯之乱",sTaskDescription="行脚商人的麻烦",tTalk={{40,"（这个王大牛老是欺负我，太可恶了!）",},{0,"打扰了，请问你知道有什么小路可以直达波斯大营吗？",},{40,"这边塞的地形路径我最清楚不过了。但是我心情不好，不想说话！",},{40,"除非...你帮我教训王大牛，他敲诈了我五两银子呢！",},{0,"好，一言为定，我去帮你教训这个王大牛。",},},sSeekNpc="行脚商人",sBackground="qingyunmen",}
ctTaskPlotConf[103089]={nID=103089,nIndex=90,nTaskId=103089,sTaskName="主线-波斯之乱",sTaskDescription="教训王大牛",tTalk={{41,"又是行脚商人找来的帮手！兄弟们，给我狠狠的打！",},{0,"果然蛮横，我还没说开打呢！就算行脚商人不说，我也不能放纵你！",},},sSeekNpc="王大牛",sBackground="qingyunmen",}
ctTaskPlotConf[103090]={nID=103090,nIndex=91,nTaskId=103090,sTaskName="主线-波斯之乱",sTaskDescription="获得小路消息",tTalk={{40,"多谢几位，这王大牛平日欺凌乡亲们终于得到报应了。只要你们一路向东，很快就能到达波斯大营了。",},{0,"多谢相告，我们告辞了！",},},sSeekNpc="行脚商人",sBackground="qingyunmen",}
ctTaskPlotConf[103091]={nID=103091,nIndex=92,nTaskId=103091,sTaskName="主线-波斯之乱",sTaskDescription="卓不凡的想法",tTalk={{7,"走小路果然没碰到军队呀，波斯大营就在眼前了，我有个好想法。",},{0,"你上次主意不错，这次又有什么好想法呀！",},{7,"我们既然要进这个波斯大营，索性就扮成扶桑使者，这样就不容易被发现啦！",},{0,"老卓，可以呀，这招不错，佩服！",},},sSeekNpc="卓不凡",sBackground="qingyunmen",}
ctTaskPlotConf[103092]={nID=103092,nIndex=93,nTaskId=103092,sTaskName="主线-波斯之乱",sTaskDescription="击败巫族侍卫",tTalk={{0,"带我去见你们将军，川吉将军有要事让我转告主将暴怒。",},{42,"既然是扶桑来人，请说暗号！",},{0,"暗号？暗号是…动手！",},},sSeekNpc="巫族侍卫",sBackground="qingyunmen",}
ctTaskPlotConf[103093]={nID=103093,nIndex=94,nTaskId=103093,sTaskName="主线-波斯之乱",sTaskDescription="被掳的士兵",tTalk={{43,"救命呀！怪物！都是怪物！啊！救命啊！",},{0,"你还好吧？什么怪物？",},{43,"怪物！怪物！救命啊！！",},{44,"不用看了，他已经被吓疯了，要不是留着他作为怨气的容器，早就杀了！",},},sSeekNpc="被俘士兵",sBackground="qingyunmen",}
ctTaskPlotConf[104094]={nID=104094,nIndex=95,nTaskId=104094,sTaskName="主线-波斯之乱",sTaskDescription="波斯将军现身",tTalk={{0,"果然整个大营弥漫着巫族的气息！你是谁，什么怨气的容器？",},{44,"哈哈哈，我就是你们口中的波斯将军，战争的胜负对我来说无关紧要，只要战争爆发，就有无数的死亡怨气被我转移到这个疯子身上，哈哈！",},{0,"你才是个疯子，我不会让你得逞的。",},},sSeekNpc="主将暴怒",sBackground="heyang",}
ctTaskPlotConf[104095]={nID=104095,nIndex=96,nTaskId=104095,sTaskName="主线-波斯之乱",sTaskDescription="狂傲波斯将军暴怒",tTalk={{44,"哈哈哈，不自量力的家伙！等下我收了你的怨气再走！",},},sSeekNpc="主将暴怒",sBackground="heyang",}
ctTaskPlotConf[104096]={nID=104096,nIndex=97,nTaskId=104096,sTaskName="主线-波斯之乱",sTaskDescription="魅魔的出现",tTalk={{45,"暴怒，别忘记了自己的任务，快带怨气容器走，我帮你挡一下。",},{44,"哼，你们吸血鬼也管得太多了吧。也罢，那这里就交给你，我先走了。",},{0,"原来巫族和吸血鬼有勾结呀!休想跑，放下那个士兵！",},{45,"你当我不存在呀？我们的计划没有人能够阻止！",},},sSeekNpc="魅魔",sBackground="heyang",}
ctTaskPlotConf[104097]={nID=104097,nIndex=98,nTaskId=104097,sTaskName="主线-波斯之乱",sTaskDescription="卓不凡的银剑",tTalk={{7,"看到没有？这吸血鬼在我的银剑下简直弱爆了，嘻嘻！",},{0,"你的剑是很神勇，但是线索也被你砍断了！",},{7,"呃，那咋办呀？",},{0,"还是边境安全最为重要，也不知道那边的战况如何了，我们先回去找李将军吧！",},},sSeekNpc="卓不凡",sBackground="wanfudong",}
ctTaskPlotConf[104098]={nID=104098,nIndex=99,nTaskId=104098,sTaskName="主线-波斯之乱",sTaskDescription="向李靖汇报",tTalk={{38,"多亏少侠偷袭成功，波斯军群龙无首，我军才能这么轻松地平定中原呀！",},{0,"暴怒抓走了一个被掳的士兵，说是收集到足够的怨气了。",},{38,"收集怨气绝非什么好事，请少侠火速回京将此事禀告皇上呀！",},{7,"我们才回来，好歹也让我们喝杯茶嘛！",},{0,"（白了卓不凡一眼）那事不宜迟，我们这就出发。",},},sSeekNpc="李靖",sBackground="wanfudong",}
ctTaskPlotConf[104099]={nID=104099,nIndex=100,nTaskId=104099,sTaskName="主线-波斯之乱",sTaskDescription="回京禀报",tTalk={{0,"禀皇上，这次边境…（讲述事情的经过）",},{25,"巫族和吸血鬼的事已非我们凡人能解决。刚好玄奘法师取经成佛已归，少侠可前去问问佛家是否有办法。",},{0,"哇，传奇人物哟...我现在就去。",},},sSeekNpc="李世民",sBackground="wanfudong",}
ctTaskPlotConf[104100]={nID=104100,nIndex=101,nTaskId=104100,sTaskName="主线-佛缘",sTaskDescription="和尚不为拦住去路",tTalk={{46,"玄奘大师现在可是佛，施主若想见他，还要看看你有没有佛缘",},{0,"啊？那如何看我有木有佛缘？",},{46,"是指只要击败我便可",},{7,"呃..原来是想打一架，来吧！",},},sSeekNpc="和尚不为",sBackground="wanfudong",}
ctTaskPlotConf[104101]={nID=104101,nIndex=102,nTaskId=104101,sTaskName="主线-佛缘",sTaskDescription="寻找唐三藏",tTalk={{0,"大师，最近大唐发现一些巫族的踪迹，他们有复活死人的方法，不知您是否有方法对付",},{48,"阿弥陀佛，巫族乃是九黎遗族，他们擅长利用怨气使用各种邪法，只要没怨气就好了。",},{0,"那天下太平，巫族收不到怨气就不可怕了？",},{48,"正是，所以巫族才会对皇上下巫术，让皇上神志不清难以管理朝政，然后乘风作浪收集怨气。",},{0,"那现在天下太平，没有战争，就没我什么事了，就先走了！",},{48,"施主留步！",},},sSeekNpc="唐三藏",sBackground="wanfudong",}
ctTaskPlotConf[104102]={nID=104102,nIndex=103,nTaskId=104102,sTaskName="主线-佛缘",sTaskDescription="与玄奘的对话",tTalk={{48,"贫僧听皇上说施主为神祖血脉，方才也确实能破不为的邪念。",},{0,"大师能明说吗？",},{48,"神祖血脉为破邪体质，通过血脉传承，而道家最熟悉血脉传承，事关施主身世，你可去找太上老君。",},{0,"这，多谢大师提点！",},},sSeekNpc="唐三藏",sBackground="wanfudong",}
ctTaskPlotConf[104103]={nID=104103,nIndex=104,nTaskId=104103,sTaskName="主线-神祖血脉",sTaskDescription="莫名其妙的增长天王",tTalk={{49,"来着何人，是否有天庭之人引荐而来？",},{0,"引荐？是玄奘法师让我来找太上老君的。",},{49,"难道是因神祖血脉而来？",},{0,"正是，天王可让我见太上老君？",},{49,"太上老君吩咐，须斩下我的心魔方可验证！",},{0,"又验证？那就来吧！",},},sSeekNpc="增长天王",sBackground="wanfudong",}
ctTaskPlotConf[104104]={nID=104104,nIndex=105,nTaskId=104104,sTaskName="主线-神祖血脉",sTaskDescription="战胜增长天王",tTalk={{0,"得罪了！",},},sSeekNpc="增长天王的心魔",sBackground="wanfudong",}
ctTaskPlotConf[104105]={nID=104105,nIndex=106,nTaskId=104105,sTaskName="主线-神祖血脉",sTaskDescription="与太上老君的对话",tTalk={{47,"能助别人斩心魔，血液还具有破邪之力。若是老道没猜错的话，少侠一定身具神祖血脉！",},{0,"神祖血脉？这就是我的身世？",},{47,"伏羲的神祖血脉终于有传人出现了，巫族将得以消灭，三界之幸呀！",},{0,"等会，我之事来求问身世的，其他的是我就不参与...",},{47,"眼看着苍生受苦，少侠于心何忍呀？",},{0,"哦，好吧，那到底有哪些事用到我的。",},},sSeekNpc="太上老君",sBackground="wanfudong",}
ctTaskPlotConf[104106]={nID=104106,nIndex=107,nTaskId=104106,sTaskName="主线-神祖血脉",sTaskDescription="继续与太上老君对话",tTalk={{47,"三界中出现巫妖，乃是巫族人用生命为代价，用怨气凝聚怨丹而复活的。",},{47,"而怨丹是无法破坏的，除非用神祖血脉的血液破坏怨丹，否则巫族将生生不息呀！",},{0,"说半天，这又是放我的血呀！",},{47,"其实不用每次都放血啦，只要把血液附魔到武器上，一样有破邪的效果。",},{0,"那样还好，不然我可要血尽人亡...",},{47,"哪吒在乌斯藏境地追踪巫族，少侠附魔成功后可去找他。",},},sSeekNpc="附魔成功",sBackground="wanfudong",}
ctTaskPlotConf[104107]={nID=104107,nIndex=108,nTaskId=104107,sTaskName="主线-追踪巫族",sTaskDescription="协战仙子",tTalk={{0,"仙子，我来助你一臂之力。",},},sSeekNpc="哪吒",sBackground="wanfudong",}
ctTaskPlotConf[104108]={nID=104108,nIndex=109,nTaskId=104108,sTaskName="主线-追踪巫族",sTaskDescription="击退野狐",tTalk={{51,"吼~",},},sSeekNpc="野狐",sBackground="wanfudong",}
ctTaskPlotConf[104109]={nID=104109,nIndex=110,nTaskId=104109,sTaskName="主线-追踪巫族",sTaskDescription="哪吒受伤",tTalk={{0,"仙子，你怎么受了重伤，还被那么多妖怪围堵？",},{50,"我刚和巫妖大战一场。被巫妖所伤，可惜只能毁它形体，让他的怨丹遁走了。",},{50,"正想追之时，就被这些妖怪围住了，恐怕不久这巫妖又要重生了。",},{0,"仙子你且休息，太上老君让我来对付巫妖的。",},{50,"那就拜托了，我先回天庭疗伤，你可以向土地公打探一下巫妖的行踪。",},},sSeekNpc="哪吒",sBackground="wanfudong",}
ctTaskPlotConf[104110]={nID=104110,nIndex=111,nTaskId=104110,sTaskName="主线-追踪巫族",sTaskDescription="与土地公对话",tTalk={{0,"土地爷爷，你知道刚逃跑的巫妖在哪吗？",},{52,"巫妖怨丹刚刚遁走，我还没找到它呢，除非它已经重生了。",},{116,"果然，留着土地公就是个麻烦。为了巫妖大人的计划，去死吧！",},},sSeekNpc="土地公",sBackground="wanfudong",}
ctTaskPlotConf[104111]={nID=104111,nIndex=112,nTaskId=104111,sTaskName="主线-追踪巫族",sTaskDescription="击败熊怪",tTalk={{116,"拿命来！",},},sSeekNpc="熊怪",sBackground="wanfudong",}
ctTaskPlotConf[104112]={nID=104112,nIndex=113,nTaskId=104112,sTaskName="主线-追踪巫族",sTaskDescription="拷问熊怪",tTalk={{0,"就你这三脚猫还敢来杀人灭口，看来今晚我们可以吃烤虎肉了。",},{116,"不要啊大人，您千万不要吃我，我的肉不好吃啊。",},{0,"那你告诉我巫妖在哪？",},{116,"这个…",},{0,"算了，还是开始今晚的虎肉大餐吧！",},{116,"我说我说，巫妖大人就在前头，马上就重生了。",},{0,"哼，算你识相。",},},sSeekNpc="熊怪",sBackground="wanfudong",}
ctTaskPlotConf[104113]={nID=104113,nIndex=114,nTaskId=104113,sTaskName="主线-追踪巫族",sTaskDescription="巫妖是熊怪",tTalk={{116,"哼，杀我一次又如何，我还不照样可以重生，哈哈哈！",},{0,"咦？你就是巫妖？熊怪的恶念？？",},{116,"虎爷拍死你，没有人可以阻止我的计划，我要灭了这人界。",},{0,"哼，区区一头老虎你还想翻了天呀。",},},sSeekNpc="熊怪",sBackground="wanfudong",}
ctTaskPlotConf[104114]={nID=104114,nIndex=115,nTaskId=104114,sTaskName="主线-追踪巫族",sTaskDescription="击杀巫妖",tTalk={{53,"就先灭了你，再灭整个人间界!",},},sSeekNpc="巫妖",sBackground="wanfudong",}
ctTaskPlotConf[104115]={nID=104115,nIndex=116,nTaskId=104115,sTaskName="主线-追踪巫族",sTaskDescription="嫦娥仙子降临",tTalk={{54,"不错，不愧是拥有神祖血脉之人。",},{0,"嫦娥仙子怎么来了呢？",},{54,"我是奉观音菩萨之命来净化这野狐的执念的，免得巫族再次利用复活。",},{54,"这位姑娘有琉璃之相，可否愿意入我仙门？",},{10,"什么相我不懂，我已有师门，况且不能喝酒的地方我是不考虑的！",},{54,"既然如此，那就不强求！",},{10,"（奇怪，为何我拒绝得这么坚决）",},{0,"（咦，嫦娥仙子的眼神有点不对，难道这其中有隐情？）",},},sSeekNpc="嫦娥仙子",sBackground="wanfudong",}
ctTaskPlotConf[104116]={nID=104116,nIndex=117,nTaskId=104116,sTaskName="主线-追踪巫族",sTaskDescription="与嫦娥仙子的对话",tTalk={{54,"好了，熊怪的执念已被我净化了，只是这里现在的怨气很重，怕是还有别的巫妖在那潜伏，还请少侠再查探一二。",},{0,"没问题，这地方风光无限，就当到处游玩好啦。",},{54,"毒仙子，我传你清心咒，日后你们灭了巫妖，你也可以净化这执念。",},{10,"谨遵法旨。（咦，我咋回答那么顺溜）",},},sSeekNpc="嫦娥仙子",sBackground="wanfudong",}
ctTaskPlotConf[104117]={nID=104117,nIndex=118,nTaskId=104117,sTaskName="主线-童子事件",sTaskDescription="村长的苦恼",tTalk={{55,"哎，我可伶的孙子孙女，这可怎么办呢？",},{0,"老村长，你怎么唉声叹气的呀？",},{55,"唉，告诉你也没要用啊，还是别害了你。",},{56,"老头，今天的童男童女准备好了没有。",},{55,"（发抖）没有呢，求求你放过我们林家村吧！",},{56,"那我只好杀了你，再到你林家村抢人。",},{0,"哪来的妖怪欺负一个老人家，村长你放心，我来帮你。",},},sSeekNpc="村长",sBackground="wanfudong",}
ctTaskPlotConf[104118]={nID=104118,nIndex=119,nTaskId=104118,sTaskName="主线-童子事件",sTaskDescription="击杀机关兽",tTalk={{0,"何方妖孽，在此作怪？",},},sSeekNpc="机关兽",sBackground="wanfudong",}
ctTaskPlotConf[104119]={nID=104119,nIndex=120,nTaskId=104119,sTaskName="主线-童子事件",sTaskDescription="林村长很激动",tTalk={{0,"好奇怪，这妖怪为何要抓童男童女！",},{0,"咦，村长，你怎么这么看着我，难道是有事和我说？",},{55,"这位神仙，求你救救我林家村，救救的我孙儿们呀！(噗通一声跪下了）",},{0,"老人家快快请起，说下到底发生了什么事。",},{55,"前天有个妖怪突然出现，让我们林家村每天交出一双童男童女。现在我是迫不得已先交我自己的孙子孙女呀！（抹了一把泪）",},{0,"每天？何人这么猖狂？村长你放心，我一定帮你把人救回来。",},},sSeekNpc="村长",sBackground="wanfudong",}
ctTaskPlotConf[104120]={nID=104120,nIndex=121,nTaskId=104120,sTaskName="主线-童子事件",sTaskDescription="找土地公问问",tTalk={{0,"土地公公，你可知道这附近有什么妖怪在抢童男童女吗？",},{52,"的确有个熊怪在作祟，不过它身边妖怪可多着呢！",},{0,"无妨，你只要告诉我它在哪就行。",},{52,"妖怪就潜伏在桥那边.",},},sSeekNpc="土地公",sBackground="wanfudong",}
ctTaskPlotConf[104121]={nID=104121,nIndex=122,nTaskId=104121,sTaskName="主线-童子事件",sTaskDescription="出现一头熊怪",tTalk={{57,"什么人？可是来给我家大王送孩童的？",},{0,"看来是这里没错了，你这个妖孽，还敢说孩童！快叫你们大王滚出来！",},},sSeekNpc="熊怪",sBackground="wanfudong",}
ctTaskPlotConf[104122]={nID=104122,nIndex=123,nTaskId=104122,sTaskName="主线-童子事件",sTaskDescription="与灵感大王的对话",tTalk={{58,"谁敢在本王的地盘撒野？",},{10,"咦？这不是灵感大王吗？难道…",},{0,"灵感大王，难道你也是巫妖吗？",},{58,"巫妖是什么，本王不知道你在说什么！",},{0,"装疯卖傻是吧，上！",},},sSeekNpc="灵感大王",sBackground="wanfudong",}
ctTaskPlotConf[104123]={nID=104123,nIndex=124,nTaskId=104123,sTaskName="主线-童子事件",sTaskDescription="激战灵感大王",tTalk={{58,"你要找死，本王成全你！！",},},sSeekNpc="灵感大王",sBackground="wanfudong",}
ctTaskPlotConf[104124]={nID=104124,nIndex=125,nTaskId=104124,sTaskName="主线-童子事件",sTaskDescription="找到林贝儿",tTalk={{59,"呜呜，你不要过来，啊！！！",},{0,"别害怕，林村长让我来找他孙儿的。",},{59,"爷爷？我是林村长的孙女，求求你救救我哥哥，他被一个坏人抓走了。",},{0,"好，你放心，我去打探一下。",},},sSeekNpc="林贝儿",sBackground="wanfudong",}
ctTaskPlotConf[104125]={nID=104125,nIndex=126,nTaskId=104125,sTaskName="主线-童子事件",sTaskDescription="与土地公对话",tTalk={{0,"土地公公，贝儿有个哥哥被人抓走了，你可有发现？",},{52,"确实，老朽刚刚看到有个黑袍人抱着一个小男孩往东边去了。",},},sSeekNpc="土地公",sBackground="wanfudong",}
ctTaskPlotConf[104126]={nID=104126,nIndex=127,nTaskId=104126,sTaskName="主线-童子事件",sTaskDescription="莫愁的愤怒",tTalk={{0,"美女你好呀，有没有看到一个带着小男孩的黑袍人呀？",},{60,"咦？姐姐的怨气在你身上？是你毁了我姐姐的！",},{7,"你啥时候毁了个女子的？",},{0," 姑娘，你可别乱说呀，我怎么知道你姐姐是谁？",},{60,"沫熙就是我姐，这下你有印象了吧！",},{0,"嘿，原来你也是吸血鬼，那就一起解决了吧！",},},sSeekNpc="莫愁",sBackground="wanfudong",}
ctTaskPlotConf[105127]={nID=105127,nIndex=128,nTaskId=105127,sTaskName="主线-童子事件",sTaskDescription="打败莫愁",tTalk={{60,"都给我死。",},},sSeekNpc="莫愁",sBackground="jingcheng",}
ctTaskPlotConf[105128]={nID=105128,nIndex=129,nTaskId=105128,sTaskName="主线-童子事件",sTaskDescription="找到黑袍人",tTalk={{61,"你是谁，莫愁没过来吗？",},{0,"莫愁忙着和她姐姐相聚呢，你快把林村长的孙子交出来，",},{61,"喔？那就看你有没有这个本事了。",},{0,"既然你皮痒，我就不客气了!",},},sSeekNpc="黑袍人",sBackground="liuboshan",}
ctTaskPlotConf[105129]={nID=105129,nIndex=130,nTaskId=105129,sTaskName="主线-童子事件",sTaskDescription="打败黑袍人",tTalk={{61,"那我就恩赐你死亡吧!",},},sSeekNpc="黑袍人",sBackground="liuboshan",}
ctTaskPlotConf[105130]={nID=105130,nIndex=131,nTaskId=105130,sTaskName="主线-童子事件",sTaskDescription="傻笑的林宝儿",tTalk={{0,"宝儿，你个黑袍人有没有对你做什么？",},{62,"嘿嘿嘿。",},{0,"宝儿，你怎么了？没事吧？",},{62,"嘿嘿嘿。",},{10,"别叫了，这小家伙怕是被吓傻了。",},{0,"啊？那这怎么办呀！",},{63,"你们是谁？在这嚷嚷什么！！",},},sSeekNpc="林宝儿",sBackground="liuboshan",}
ctTaskPlotConf[105131]={nID=105131,nIndex=132,nTaskId=105131,sTaskName="主线-童子事件",sTaskDescription="出现不明美男",tTalk={{63,"这个男童分明不认识你们，咦，这个男童的气息怎么怪怪的？",},{0,"怪什么了？你休想对他不利。",},{63,"哼，村里近来总有童男童女失踪，我看就是你们捣的鬼。",},{0,"你这妖人，胡说八道的，我看你是讨打。",},},sSeekNpc="美男八戒",sBackground="liuboshan ",}
ctTaskPlotConf[105132]={nID=105132,nIndex=133,nTaskId=105132,sTaskName="主线-童子事件",sTaskDescription="击败美男",tTalk={{63,"好贼子，打就打，俺老猪还怕你？",},},sSeekNpc="美男八戒",sBackground="liuboshan",}
ctTaskPlotConf[105133]={nID=105133,nIndex=134,nTaskId=105133,sTaskName="主线-童子事件",sTaskDescription="嫦娥仙子道明真相",tTalk={{54,"你怎么和净坛使者打起来了？",},{0,"净坛使者？他是猪八戒？猪八戒有那么帅吗？况且是他先挑起事端的。",},{63,"我不是怕吓到人嘛，就变了个美男咯。",},{63,"这段时间那么多孩童失踪，你又带着个气息诡异的男童到处晃悠，不怀疑你怀疑谁呀！",},{54,"好了，少侠乃神祖血脉，绝非妖邪之人！",},{0,"这宝儿是受到惊吓而已，仙子您一定有办法恢复他对吧？",},{54,"这男童的气息的确有问题，像是被附身了，你拿我的带来甘露洗涤一下他身上的污秽吧！",},},sSeekNpc="嫦娥仙子",sBackground="liuboshan",}
ctTaskPlotConf[105134]={nID=105134,nIndex=135,nTaskId=105134,sTaskName="主线-童子事件",sTaskDescription="使用甘露",tTalk={{0,"谢谢仙子！",},},sSeekNpc="嫦娥仙子",sBackground="liuboshan",}
ctTaskPlotConf[105135]={nID=105135,nIndex=136,nTaskId=105135,sTaskName="主线-童子事件",sTaskDescription="镇压寄生虫",tTalk={{0,"哇，从宝儿身上冒出这么大一个东西。",},{54,"就是这家伙了，诡异的气息就是从它身上发出的。",},{54,"镇压住它，我带回去研究研究！",},{0,"没问题！",},},sSeekNpc="嫦娥仙子",sBackground="liuboshan",}
ctTaskPlotConf[105136]={nID=105136,nIndex=137,nTaskId=105136,sTaskName="主线-童子事件",sTaskDescription="嫦娥仙子的来意",tTalk={{54,"三界中已发现大量巫妖捉童男童女送到乌斯藏境地，巫妖的事情比我们想象的更为严重。",},{54,"三界中的各路神仙都在各地对付巫妖，这里就只能交给你了。",},{0,"慢着？大量巫妖？那我岂不是很危险呀？",},{54,"我相信你是可以的，好了，我现在要回去研究寄生虫了，你好好加油哟！",},},sSeekNpc="嫦娥仙子",sBackground="liuboshan",}
ctTaskPlotConf[105137]={nID=105137,nIndex=138,nTaskId=105137,sTaskName="主线-童子事件",sTaskDescription="美男八戒有话说",tTalk={{63,"不打不相识，老猪我也是在调查童子事件，不如一起行动吧！",},{0,"啧啧，散伙王居然这么有正义感呀！",},{63,"咳咳，怎么说也是佛门中人嘛，嘻嘻！",},},sSeekNpc="美男八戒",sBackground="liuboshan",}
ctTaskPlotConf[105138]={nID=105138,nIndex=139,nTaskId=105138,sTaskName="主线-童子事件",sTaskDescription="九尾狐狸拦路",tTalk={{64,"此路是我开，哎不对！留下男童，你可以走了。",},{0,"老猪，这你能忍？",},{63,"跟牠废什么话，捉牠问个清楚。",},},sSeekNpc="九尾狐狸",sBackground="liuboshan",}
ctTaskPlotConf[105139]={nID=105139,nIndex=140,nTaskId=105139,sTaskName="主线-童子事件",sTaskDescription="击败九尾狐狸",tTalk={{0,"开打！",},},sSeekNpc="九尾狐狸",sBackground="liuboshan",}
ctTaskPlotConf[105140]={nID=105140,nIndex=141,nTaskId=105140,sTaskName="主线-童子事件",sTaskDescription="美男八戒的妙招",tTalk={{0,"你拦我干嘛？不是要抓他吗，还不行动？",},{63,"别急，我已经在他身上做记号了，找他很容易的。我们先安顿好宝儿。",},{0,"你说的有道理，我们赶紧送宝儿回家。",},},sSeekNpc="美男八戒",sBackground="liuboshan",}
ctTaskPlotConf[105141]={nID=105141,nIndex=142,nTaskId=105141,sTaskName="主线-童子事件",sTaskDescription="宝儿回家",tTalk={{55,"宝儿，我的好孩子，你终于回来了。",},{62,"爷爷，呜呜，宝儿好怕，呜呜…",},{55,"感谢上仙救我孙儿，大恩大德无以为报呀，日后必定日夜供奉上仙。",},{0,"供奉？不用不用，老人家您太客气了。",},},sSeekNpc="村长",sBackground="liuboshan",}
ctTaskPlotConf[105142]={nID=105142,nIndex=143,nTaskId=105142,sTaskName="主线-童子事件",sTaskDescription="追击九尾狐狸",tTalk={{0,"识相的就赶紧告诉我你抢童男做什么，不然就打得你永世不得超生。",},{64,"饶命啊，我说，都是被白鹿精逼的。",},{0,"白鹿精？看来又是个巫妖，它要童子干嘛？",},{64,"白鹿精好像要童男童女的心肝来炼制什么。",},{0,"快带我去见白鹿精，我要灭了它。",},{64,"可是它那里有很多妖怪把守的，你们这样去肯定不行的。",},{63,"这个简单，我变成童子，你假装捉我过去就好。",},},sSeekNpc="九尾狐狸",sBackground="liuboshan",}
ctTaskPlotConf[105143]={nID=105143,nIndex=144,nTaskId=105143,sTaskName="主线-真相浮现",sTaskDescription="避寒大王",tTalk={{65,"白面，你这次的童男交得实在太慢了！",},{64,"村子里有碍事的人，就费了些时日。",},{65,"咦，这不是童男呀，白面，你怎么办事的。这都分辨不出来。",},{63,"既然被你看穿了，就送你下地狱吧！",},},sSeekNpc="避寒大王",sBackground="liuboshan",}
ctTaskPlotConf[105144]={nID=105144,nIndex=145,nTaskId=105144,sTaskName="主线-真相浮现",sTaskDescription="击败避寒大王",tTalk={{63,"既然被你看穿了，就送你下地狱吧！",},},sSeekNpc="避寒大王",sBackground="liuboshan",}
ctTaskPlotConf[105145]={nID=105145,nIndex=146,nTaskId=105145,sTaskName="主线-真相浮现",sTaskDescription="九尾狐狸的担忧",tTalk={{64,"完了完了，他们居然能认出你来了。",},{0,"慌什么，赶紧带路。",},},sSeekNpc="九尾狐狸",sBackground="liuboshan",}
ctTaskPlotConf[105146]={nID=105146,nIndex=147,nTaskId=105146,sTaskName="主线-真相浮现",sTaskDescription="白面的分析",tTalk={{64,"他们妖多势众，怕是不好对付啊，越前越多妖怪。",},},sSeekNpc="九尾狐狸",sBackground="liulboshan",}
ctTaskPlotConf[105147]={nID=105147,nIndex=148,nTaskId=105147,sTaskName="主线-真相浮现",sTaskDescription="继续往前",tTalk={{0,"别废话，实在不行就直接开打，你赶紧带路。",},},sSeekNpc="九尾狐狸",sBackground="liuboshan",}
ctTaskPlotConf[106148]={nID=106148,nIndex=149,nTaskId=106148,sTaskName="主线-真相浮现",sTaskDescription="毒仙子有话说",tTalk={{10,"奇怪，刚刚那个巫妖我净化不了，这个地方有问题。",},{0,"这里四周有聚阴阵，对怨气有保护作用，所以你现在净化不了。",},{10,"既然这样，我们先找白鹿精吧。",},{0,"九尾狐狸，你怎么那么配合？",},{64,"我只想保住我的小命…",},},sSeekNpc="毒仙子",sBackground="jingcheng",}
ctTaskPlotConf[106149]={nID=106149,nIndex=150,nTaskId=106149,sTaskName="主线-真相浮现",sTaskDescription="遭遇避暑大王",tTalk={{67,"白面，你好大的胆子，既然串通外人来谋害我哥哥。",},{0,"那么多巫妖，真是浪费我时间找白鹿精。",},{67,"白鹿大人的炼制已经到了最后时刻，我是不会让你们打扰到大人的。",},},sSeekNpc="避暑大王",sBackground="qingyunmen",}
ctTaskPlotConf[106150]={nID=106150,nIndex=151,nTaskId=106150,sTaskName="主线-真相浮现",sTaskDescription="击败避暑大王",tTalk={{67,"拿你们给我哥哥偿命！",},},sSeekNpc="避暑大王",sBackground="qingyunmen",}
ctTaskPlotConf[106151]={nID=106151,nIndex=152,nTaskId=106151,sTaskName="主线-真相浮现",sTaskDescription="美男八戒的提醒",tTalk={{63,"不好，我们中了陷阱，刚才巫妖死后没有怨丹，应该是由阵法幻化的。",},{0,"什么，你是说这妖怪是假的？可这个幻化也太真实了吧。",},{63,"是九尾狐狸，可恶，一定是这个狡诈的狐狸因我们入阵的。",},},sSeekNpc="美男八戒",sBackground="qingyunmen",}
ctTaskPlotConf[106152]={nID=106152,nIndex=153,nTaskId=106152,sTaskName="主线-真相浮现",sTaskDescription="九尾狐狸的狞笑",tTalk={{64,"哈哈哈，白鹿精只是个幌子，真正要炼制童心丹的是我！",},{0,"好狡猾的狐狸，如此残忍留你不得！",},{64," 如此海口，之前你打败的是我的分身，现在我真是在此，不知道谁灭谁呢！",},{0,"恩，你的遗言我记下了。",},},sSeekNpc="九尾狐狸",sBackground="qingyunmen",}
ctTaskPlotConf[106153]={nID=106153,nIndex=154,nTaskId=106153,sTaskName="主线-真相浮现",sTaskDescription="击败九尾狐狸",tTalk={{64,"恰好童心丹已炼成，我就陪你玩玩!",},},sSeekNpc="九尾狐狸",sBackground="qingyunmen",}
ctTaskPlotConf[106154]={nID=106154,nIndex=155,nTaskId=106154,sTaskName="主线-真相浮现",sTaskDescription="净化九尾狐狸",tTalk={{0,"哪里跑。",},{64,"饶命呀，是我错了。",},{0,"说，你到底炼制的什么邪物，快交出来！",},{64,"我只是奉命行事的，百眼魔君让我炼制一颗童心丹，刚已经被他带走了。",},{0,"百眼魔君是谁，他去哪了",},{64,"我也不知道呀，一直都是他找我的，求您放过我吧！",},{10,"哼，放你祸害人间，就让我来净化你吧！",},{64,"啊！！…",},},sSeekNpc="九尾狐狸",sBackground="qingyunmen",}
ctTaskPlotConf[106155]={nID=106155,nIndex=156,nTaskId=106155,sTaskName="主线-真相浮现",sTaskDescription="美男八戒的建议",tTalk={{63,"听说这个百眼魔君隐匿方面很是擅长，找他不容易呀。",},{0,"唉，我还你为你有办法找到他呢。",},{63,"别急。我知道昆仑山有高人能占卜人的下落，我们前去看看。",},{0,"那事不宜迟。",},},sSeekNpc="美男八戒",sBackground="qingyunmen",}
ctTaskPlotConf[106156]={nID=106156,nIndex=157,nTaskId=106156,sTaskName="主线-昆仑危机",sTaskDescription="避尘大王拦住去路",tTalk={{0,"又有一个过来送死的小妖！",},},sSeekNpc="避尘大王",sBackground="qingyunmen",}
ctTaskPlotConf[106157]={nID=106157,nIndex=158,nTaskId=106157,sTaskName="主线-昆仑危机",sTaskDescription="天雷道人",tTalk={{63,"天雷，我老猪呀，好久不见呀，这次要麻烦你卜算一卦了。",},{68,"原来是净坛使者，可我现在无法分身，山中无缘无故来了些巫妖作乱。",},{10,"巫妖?不如让我们也帮帮忙，我能净化巫妖。",},{68,"那实在是太好了，之前抓得巫妖都消灭不了，只能分神镇压它。",},},sSeekNpc="天雷道人",sBackground="qingyunmen",}
ctTaskPlotConf[106158]={nID=106158,nIndex=159,nTaskId=106158,sTaskName="主线-昆仑危机",sTaskDescription="解开镇魔符",tTalk={{0,"让我解开镇魔符消灭这些巫妖！",},},sSeekNpc="镇压巫妖",sBackground="qingyunmen",}
ctTaskPlotConf[106159]={nID=106159,nIndex=160,nTaskId=106159,sTaskName="主线-昆仑危机",sTaskDescription="净化巫妖",tTalk={{0,"仙子，这些就拜托你了。",},{10,"恩，这个就交给我吧！",},},sSeekNpc="毒仙子",sBackground="qingyunmen",}
ctTaskPlotConf[106160]={nID=106160,nIndex=161,nTaskId=106160,sTaskName="主线-昆仑危机",sTaskDescription="分头行事",tTalk={{0,"已经搞掂了，现在有时间帮我们卜算了吧！",},{68,"我暂时还抽不出空，昆仑山被巫妖召来妖怪围住了，我还要解决。",},{0,"呃…这么多妖怪的杀到什么时候呀！",},{68,"可恶!!!到底是谁!",},{68,"难道是小火龙出事了？劳烦少侠前去查探一二。",},},sSeekNpc="天雷道人",sBackground="qingyunmen",}
ctTaskPlotConf[106161]={nID=106161,nIndex=162,nTaskId=106161,sTaskName="主线-昆仑危机",sTaskDescription="暴怒的小火龙",tTalk={{0,"这是怎么回事？",},},sSeekNpc="小火龙",sBackground="qingyunmen",}
ctTaskPlotConf[107162]={nID=107162,nIndex=163,nTaskId=107162,sTaskName="主线-昆仑危机",sTaskDescription="安抚小火龙",tTalk={{0,"小火龙，你先冷静一下，要是我偷了什么了也不会回来找你了呀！",},{69,"也对，可是昆仑山很久没有外人进来了呀。",},{0,"你到底不见什么了？",},{69,"有人趁我熟睡偷走了我的千年宝珠。",},{0,"…那还有什么方法找的回来吗？",},{69,"宝珠上有我的气息，你拿着我的精血去追踪，肯定能抓到小偷的。拜托了！",},{0,"放心，就交给我了。",},},sSeekNpc="小火龙",sBackground="heyang",}
ctTaskPlotConf[107163]={nID=107163,nIndex=164,nTaskId=107163,sTaskName="主线-昆仑危机",sTaskDescription="追踪小偷",tTalk={{0,"小火龙的气息到这里就断了，看来是你偷了宝珠？",},{70,"什么宝珠，我表示什么也不知道！",},{63,"呱呱兽，原来是你这个贼子。",},{70,"我还以为是谁，这不是蠢猴子身边的猪嘛！",},{63,"哼，让你瞧瞧你猪爷爷的厉害。",},},sSeekNpc="呱呱兽",sBackground="heyang",}
ctTaskPlotConf[107164]={nID=107164,nIndex=165,nTaskId=107164,sTaskName="主线-昆仑危机",sTaskDescription="击杀呱呱兽",tTalk={{0,"虽然不知道你们啥恩怨，打打打打！",},},sSeekNpc="呱呱兽甲",sBackground="heyang",}
ctTaskPlotConf[107165]={nID=107165,nIndex=166,nTaskId=107165,sTaskName="主线-昆仑危机",sTaskDescription="老猪的猜测",tTalk={{0,"奇怪，为何这厮死了偏偏没有留下尸体呢？",},{63,"不奇怪，我之前和猴哥杀过他一次，也是没有尸体留下的。",},{63,"这个说不定就是他的化身。",},{0,"不管了，反正宝珠拿回来了。",},},sSeekNpc="美男八戒",sBackground="heyang",}
ctTaskPlotConf[107166]={nID=107166,nIndex=167,nTaskId=107166,sTaskName="主线-昆仑危机",sTaskDescription="交还宝珠",tTalk={{69,"谢谢你帮我找回宝珠。",},{0,"不客气，我也是有求于天雷道人的，哈哈哈！",},{69,"恩，这宝珠，啊！！！！",},{0,"怎么了？",},{71,"小心！！！",},},sSeekNpc="小火龙",sBackground="lianxuetang",}
ctTaskPlotConf[107167]={nID=107167,nIndex=168,nTaskId=107167,sTaskName="主线-昆仑危机",sTaskDescription="天风道人",tTalk={{71,"少侠当心！小火龙有不妥！",},{0,"天雷道人？？你怎么来了？小火龙刚才还好好的。",},{71,"我是天风道人，可能是小火龙的宝珠出问题了，它体内有奇怪的气息，心神俱失。",},{0,"宝珠？看起来没什么问题呀！",},{71,"不行，小火龙发狂了，肯定出问题了，我们要帮它镇压住。",},},sSeekNpc="天风道人",sBackground="lianxuetang",}
ctTaskPlotConf[107168]={nID=107168,nIndex=169,nTaskId=107168,sTaskName="主线-昆仑危机",sTaskDescription="压制小火龙",tTalk={{69,"吼！吼！",},},sSeekNpc="小火龙",sBackground="lianxuetang",}
ctTaskPlotConf[107169]={nID=107169,nIndex=170,nTaskId=107169,sTaskName="主线-昆仑危机",sTaskDescription="天风道人的办法",tTalk={{71,"小火龙身上的神力都被刚才的呱呱兽吸走了，恐怕有性命之危！",},{7,"可恶，让那个畜生跑了怎么办。",},{71,"现在必须马上给小火龙传功治疗。",},{0,"可它现在这么虚弱，能承受得住吗？",},{71,"现在恐怕不行了，你帮我取些万年玄晶来，但其附近有守护精灵，你要多加小心",},},sSeekNpc="天风道人",sBackground="lianxuetang",}
ctTaskPlotConf[107170]={nID=107170,nIndex=171,nTaskId=107170,sTaskName="主线-昆仑危机",sTaskDescription="万年玄晶",tTalk={{0,"我这就去取来！",},},sSeekNpc="天风道人",sBackground="tiandibaoku",}
ctTaskPlotConf[107171]={nID=107171,nIndex=172,nTaskId=107171,sTaskName="主线-昆仑危机",sTaskDescription="守护精灵现身",tTalk={{72,"偷万年玄晶者，死！",},{0,"剑客，你动作小声点呀！",},{7,"怕什么，无非就是一战！",},},sSeekNpc="守护精灵",sBackground="tiandibaoku",}
ctTaskPlotConf[107172]={nID=107172,nIndex=173,nTaskId=107172,sTaskName="主线-昆仑危机",sTaskDescription="击败守护精灵",tTalk={{0,"守护精灵出现了，上！",},},sSeekNpc="守护精灵",sBackground="tiandibaoku",}
ctTaskPlotConf[107173]={nID=107173,nIndex=174,nTaskId=107173,sTaskName="主线-昆仑危机",sTaskDescription="治疗小火龙",tTalk={{0,"道长，万年玄晶已取来。没啥事我去找天雷道人卜算了。",},{71,"（偷笑）好，那先去吧，小火龙的事就交给我了。",},{0,"道长你怎么偷笑，好不对劲呀！",},{71,"没事，拿去吧，这里你放心好了！",},},sSeekNpc="天风道人",sBackground="tiandibaoku",}
ctTaskPlotConf[107174]={nID=107174,nIndex=175,nTaskId=107174,sTaskName="主线-昆仑危机",sTaskDescription="天雷道人的麻烦",tTalk={{0,"天雷道长，小火龙的事情已经解决了，你是时候给我卜算了吧！",},{68,"哎呀少侠，你看我昆仑山被困成这样，老夫哪有空呀！",},{0,"哇，你这套路还真是满满的，难道接下来要去帮你清理围山的小妖吗？",},{68,"那当然不是，这些围山小妖我能解决的，只是这巫妖…你看？",},{0,"行行行，我去给你砍了它行了吧！",},},sSeekNpc="天雷道人",sBackground="tiandibaoku",}
ctTaskPlotConf[107175]={nID=107175,nIndex=176,nTaskId=107175,sTaskName="主线-昆仑危机",sTaskDescription="击杀万圣大王",tTalk={{112,"敢挡本大王的路？找死！",},{0,"谁死还说不定呢!",},},sSeekNpc="万圣大王",sBackground="xuanhuotan",}
ctTaskPlotConf[107176]={nID=107176,nIndex=177,nTaskId=107176,sTaskName="主线-昆仑危机",sTaskDescription="天雷道人再有麻烦",tTalk={{0,"道长！巫妖也清除了，围山的小妖也散了，这下总可以卜算了吧！",},{68,"少侠帮了我昆仑山大忙，卜算之事当然可以啦！唔…… 突然之间好晕呀！",},{0,"喂，能不能有点仙家风范！别装死呀！",},{68,"少侠，非也！我刚刚感应到是神霄道人那边出事了，有人试图在夺取神霄的灵魂。",},{0,"呃...这你都能感应到，厉害了！不对，怎么又来一个神霄道长？",},{68,"少侠，求你去救救神霄道长吧，我昆仑山的传承可不能断在我手里呀！",},{0,"说得那么严重，那我就走一趟吧！",},},sSeekNpc="天雷道人",sBackground="xuanhuotan",}
ctTaskPlotConf[107177]={nID=107177,nIndex=178,nTaskId=107177,sTaskName="主线-昆仑危机",sTaskDescription="万圣公主的邀请",tTalk={{73,"这位少侠急急忙忙的想去哪里？不如留下来，奴家为你煮一杯酒谈谈人生？",},{0,"哎，你别过来。大老远就闻到你身上浓浓的巫妖味道。赶紧让开！",},{73,"哎，少侠难道舍得对一个弱女子动手吗？我被复活成巫妖也并非我的意愿呀！",},{63,"一看她就是拖延时间的，打！",},{0," 哇，老猪你居然能忍受她的魅惑，我对你刮目相看了！ ",},},sSeekNpc="万圣公主",sBackground="xuanhuotan",}
ctTaskPlotConf[107178]={nID=107178,nIndex=179,nTaskId=107178,sTaskName="主线-昆仑危机",sTaskDescription="击杀万圣公主",tTalk={{73,"难道真的要对奴家动手吗？",},},sSeekNpc="万圣公主",sBackground="xuanhuotan",}
ctTaskPlotConf[107179]={nID=107179,nIndex=180,nTaskId=107179,sTaskName="主线-昆仑危机",sTaskDescription="美男八戒有话要说",tTalk={{63,"慢着！神霄道长看起来很不正常，他的表现和之前小火龙很像。",},{0,"你是说，神霄道长也被控制了？",},{63," 很有可能，我们小心点。",},},sSeekNpc="美男八戒",sBackground="heyang",}
ctTaskPlotConf[107180]={nID=107180,nIndex=181,nTaskId=107180,sTaskName="主线-昆仑危机",sTaskDescription="击败神宵道长心魔",tTalk={{0,"既然是那呱呱兽作怪，那就留不得了！",},},sSeekNpc="击败神宵道长心魔",sBackground="nanjiang",}
ctTaskPlotConf[107181]={nID=107181,nIndex=182,nTaskId=107181,sTaskName="主线-昆仑危机",sTaskDescription="道长突变的缘由",tTalk={{0,"神霄道长你怎么会被控制了？",},{74,"多谢少侠的帮助，没想到那厮如此阴险，居然趁我一气化三清的时候偷袭我。",},{0,"到底是谁偷袭了你？",},{75,"难怪在你最虚弱的时候都控制不住你，原来是有外人相助。",},},sSeekNpc="神宵道长",sBackground="guiwangzong",}
ctTaskPlotConf[107182]={nID=107182,nIndex=183,nTaskId=107182,sTaskName="主线-昆仑危机",sTaskDescription="呱呱兽又出现",tTalk={{74,"贼子！没想到你还敢回来，看来你这个分身都不想要了！",},{75," 生有何惧死又何妨，你杀了万圣公主，我便都没有活下去的动力！",},{75," 更何况你破坏了我的计划，回去我也难以交差！既然得不到，神霄道长，那我便与你玉石俱焚！",},{0," 敬你是个性情中人，那我就给你一个痛快！",},},sSeekNpc="神宵道长",sBackground="guiwangzong",}
ctTaskPlotConf[107183]={nID=107183,nIndex=184,nTaskId=107183,sTaskName="主线-昆仑危机",sTaskDescription="击败呱呱兽乙",tTalk={{75,"好不容易复活万圣公主，现在她死了",},},sSeekNpc="呱呱兽乙",sBackground="nanjiang",}
ctTaskPlotConf[107184]={nID=107184,nIndex=185,nTaskId=107184,sTaskName="主线-昆仑危机",sTaskDescription="神宵道长的演算",tTalk={{0,"道长可曾帮我卜算到百眼魔君的下落？",},{74,"唔，百眼魔君前不久已身死道消了。",},{0,"那查探巫族阴谋的线索岂不是又断了？",},{74,"莫慌！百眼魔君乃为我斩杀的！巫族的阴谋我已知晓，你且听我道来... ",},},sSeekNpc="神宵道长",sBackground="nanjiang",}
ctTaskPlotConf[107185]={nID=107185,nIndex=186,nTaskId=107185,sTaskName="主线-巫族阴谋",sTaskDescription="老君忽降临昆仑山",tTalk={{0,"巫族的阴谋到底是什么？",},{132,"巫族打算以上古四大神兽精血为引唤醒蚩尤魔躯，再以童心丹复苏心脏，最后复活蚩尤！",},{0,"复活蚩尤？这…",},{132,"巫族的手段诡异，不可不防！童心丹已被巫族所得，接下来他们肯定会针对四神兽！",},{132," 如今小火龙已被袭击，怕是其他神兽也会遭受次劫。",},{0,"那我们岂非误打误撞的保护了小火龙。那其他神兽在哪呢？",},{74," 关于四大神兽，贫道倒是知道青龙如今在何处。",},},sSeekNpc="太上老君",sBackground="nanjiang",}
ctTaskPlotConf[107186]={nID=107186,nIndex=187,nTaskId=107186,sTaskName="主线-解救青龙",sTaskDescription="青龙的行踪",tTalk={{132,"倒是忘了，道长精通演算之道。不知道长演算出青龙所在何处呢？",},{74,"神兽已超脱六道轮回和因果循环，是演算不出行踪的。贫道也是听小火龙说过，青龙此前一直在兽王寨，那斗战佛也去了那里，问题应该不大。",},{132,"既然这样，我看我们就走上一趟瞧瞧吧。",},},sSeekNpc="神宵道长",sBackground="nanjiang",}
ctTaskPlotConf[107187]={nID=107187,nIndex=188,nTaskId=107187,sTaskName="主线-解救青龙",sTaskDescription="前往了解情况",tTalk={{0,"那就一起，前去查看查看！",},},sSeekNpc="神宵道长",sBackground="nanjiang",}
ctTaskPlotConf[107188]={nID=107188,nIndex=189,nTaskId=107188,sTaskName="主线-解救青龙",sTaskDescription="山中出现妖踪",tTalk={{76,"喂，你！说你呢！来此地干嘛，是哪个部队手下的？",},{0,"哈？什么鬼？此处怎么到处都是厮杀？",},{76,"你是外来者！大王说要统统杀掉外来者，以防控制山寨的消息走漏。",},{0,"控制？来之前也没看到什么异象呀？",},{132,"有阵法封禁了这山寨，外面是看不出的端倪。你且杀了这小妖，我去看看是何种封禁大阵！",},},sSeekNpc="封山小妖",sBackground="nanjiang",}
ctTaskPlotConf[107189]={nID=107189,nIndex=190,nTaskId=107189,sTaskName="主线-解救青龙",sTaskDescription="击杀封山小妖",tTalk={{0,"放心，这等小妖就交给我吧!",},},sSeekNpc="封山小妖",sBackground="guiwangzong",}
ctTaskPlotConf[107190]={nID=107190,nIndex=191,nTaskId=107190,sTaskName="主线-解救青龙",sTaskDescription="封山大阵的解法",tTalk={{133,"封禁山寨的是三尸阵，此阵只能进不能出。",},{0,"那岂非我们也出不去？老君可有什么破阵之法？",},{47,"要破此阵倒也简单只要少侠收了三个阵灵即可。",},{0,"老君，我不懂什么是阵灵。",},{47,"莫慌，领我灵符去使用，阵灵自会出现。 ",},},sSeekNpc="太上老君",sBackground="sanfuzhen",}
