ctTaskPlotConf[107197]={nID=107197,nIndex=198,nTaskId=107197,sTaskName="主线-解救青龙",sTaskDescription="解除危机",tTalk={{0,"老君，封山大阵已经解除了。到底是何人要控制这山，有何用处啊？",},{132,"是巫族，他们的目的是青龙。此事情急，回去天庭搬救兵已来不及。",},{0,"老君，封山大阵已经解除了，我们何不向离这最近的东海求援。",},{132,"少侠此法妙极，我有传音符，可唤来东海的巡游水兵。 ",},{0,"事不宜迟，那我们赶紧去向东海求援。",},},sSeekNpc="太上老君",sBackground="caomiaocun",}
