ctTaskPlotConf[107199]={nID=107199,nIndex=200,nTaskId=107199,sTaskName="主线-解救青龙",sTaskDescription="求援东海",tTalk={{77,"谁呀？咋的啦？要没啥事，大爷可是会发火的！",},{133," 好大的威风，东海水族都如此目中无人吗？",},{77," 太…太上老君！小…小的罪该万死！",},{0," 大事为重！老君何必跟一个小兵计较呢？",},{133,"告诉龙海龙王，兽王寨被巫妖围攻！请东海水族前来协助围剿巫妖！",},{77," 是是是！小的这就去转达龙王！",},{78," 找死，你们竟然还敢找援兵！",},},sSeekNpc="巡海水兵",sBackground="qingyunshan",}
