ctTaskPlotConf[107201]={nID=107201,nIndex=202,nTaskId=107201,sTaskName="主线-解救青龙",sTaskDescription="山寨情况",tTalk={{0,"我们是来帮你们的，到底是哪个巫妖想控制这山寨啊？",},{79," 太感激少侠的援助了！我们有救了！",},{0,"情况那么严重吗？大圣之前不是来了山寨吗，怎么还有歹人敢来此作怪啊？",},{79,"攻打我们的是牛魔王一家子。大圣被牛魔王拖住，我们可挡不住巫妖。",},{0,"放宽心一切有我！",},{80," 好大的口气！就是你破了封山大阵吗？",},},sSeekNpc="蹦将军",sBackground="heyang",}
ctTaskPlotConf[107202]={nID=107202,nIndex=203,nTaskId=107202,sTaskName="主线-解救青龙",sTaskDescription="红孩儿来袭",tTalk={{0,"哈哈哈，是我又如何？你们好大的胆子，斗战胜佛在此也敢来捣乱",},{80,"哼，孙猴子毁了我们一家，我们就是来寻仇的！",},{0,"哎哟，说得好像真的一样。你们还不是为了青龙而来！",},{80,"你！你怎么知道我们的目的？ ",},{0,"主要是你拖延时间的手段实在太低下了！",},},sSeekNpc="红孩儿",sBackground="nanjiang",}
ctTaskPlotConf[107203]={nID=107203,nIndex=204,nTaskId=107203,sTaskName="主线-解救青龙",sTaskDescription="斩杀红孩儿",tTalk={{80,"哈哈哈，你是阻止不了我们控制青龙的！",},{0,"别废话，打过再说！",},},sSeekNpc="红孩儿",sBackground="nanjiang",}
ctTaskPlotConf[107204]={nID=107204,nIndex=205,nTaskId=107204,sTaskName="主线-解救青龙",sTaskDescription="东海援兵到",tTalk={{81,"兽王寨居然遭受围攻，本王没来迟吧？",},{0,"不迟不迟！龙王来的正是时候！ 劳驾龙王调动水族包围山寨，务求不放过任何一个巫妖。",},{81," 好说好说！本网现在就派镇海将军调遣虾兵蟹将包围整个山寨，保准不会放过任何一个巫妖的！",},{0," 既然如此，那我就放心去解教青龙了！",},},sSeekNpc="东海龙王",sBackground="nanjiang",}
ctTaskPlotConf[107205]={nID=107205,nIndex=206,nTaskId=107205,sTaskName="主线-解救青龙",sTaskDescription="铁扇公主的执念",tTalk={{83,"哈哈哈，你来晚了！青龙已经被控制了！",},{81,"妖言惑众！此山已被包围了，你们谁也别想逃！",},{83,"就凭那些虾兵蟹将？等完全控制好青龙，今天就是那弼马温也得死！",},{0,"斗战胜佛到如今尚未出现，你们施展了什么阴谋诡计！",},{83," 死人不配知道这些！",},},sSeekNpc="铁扇公主",sBackground="nanjiang",}
ctTaskPlotConf[107206]={nID=107206,nIndex=207,nTaskId=107206,sTaskName="主线-解救青龙",sTaskDescription="斩杀铁扇公主",tTalk={{83,"我要杀了你给我儿子报仇!",},},sSeekNpc="铁扇公主",sBackground="nanjiang",}
ctTaskPlotConf[107207]={nID=107207,nIndex=208,nTaskId=107207,sTaskName="主线-解救青龙",sTaskDescription="解救青龙",tTalk={{0,"青龙看来是真的被控制了，这下麻烦了！",},{133,"无妨，小火龙此前也是如此！我有办法让青龙恢复正常！",},},sSeekNpc="太上老君",sBackground="nanjiang",}
ctTaskPlotConf[107208]={nID=107208,nIndex=209,nTaskId=107208,sTaskName="主线-解救青龙",sTaskDescription="青龙嘱托",tTalk={{84,"多谢阁下相救，否则就不是损失精血那么简单了，青龙欠你一条命！ ",},{0,"青龙言重了，不知斗战胜佛何在？ ",},{84,"这次是我连累孙老弟了，他被牛魔王偷袭了，此刻还在苦战中！",},{0," 那我去助斗战胜佛，把这次来犯的巫妖统统消灭！",},},sSeekNpc="青龙",sBackground="nanjiang",}
ctTaskPlotConf[108209]={nID=108209,nIndex=210,nTaskId=108209,sTaskName="主线-解救青龙",sTaskDescription="再见孙悟空",tTalk={{0,"斗战胜佛你没事吧？兽王寨和青龙的危机已经解除了！",},{17,"我们又见面了",},{113,"哈哈哈，你这个弼马温！以为来了援兵就能救下你吗？",},{17,"哼，你这巫妖！若非偷袭俺老孙，老孙一棒就能让你灰飞烟灭！",},{0," 胜佛暂且养伤，让我来会会这个牛魔王！",},{17,"少侠小心，这厮成了巫妖后，实力提升了很多。",},},sSeekNpc="孙悟空",sBackground="heyang",}
ctTaskPlotConf[108210]={nID=108210,nIndex=211,nTaskId=108210,sTaskName="主线-解救青龙",sTaskDescription="斩杀牛魔王",tTalk={{113,"无知小儿，敢来犯我神威！",},},sSeekNpc="牛魔王",sBackground="caomiaocun",}
ctTaskPlotConf[108211]={nID=108211,nIndex=212,nTaskId=108211,sTaskName="主线-解救青龙",sTaskDescription="太上老君的忧虑",tTalk={{0,"老君你怎么看起来忧心忡忡的，青龙虽然损失了点精血，但好歹没出事。",},{133,"天庭传来消息，朱雀已经被巫族控制住了。 ",},{0,"老君莫叹气，当务之急是保护好青龙和小火龙！",},{0,"我们去搜索麒麟的下落，希望能在巫族之前找到麒麟。",},{133,"我立刻赶回天庭求援，麒麟之事就全靠少侠了。",},{81,"麒麟的行踪，我倒是有点猜测。",},},sSeekNpc="太上老君",sBackground="caomiaocun",}
ctTaskPlotConf[108212]={nID=108212,nIndex=213,nTaskId=108212,sTaskName="主线-东海传说",sTaskDescription="关于麒麟龙王有话说",tTalk={{81,"在东海一直有个传说…当地人能平安出海全靠麒麟庇佑！",},{0,"有古怪，邻近东海的渔民不信奉龙王反倒信奉麒麟…",},{81," 传言麒麟最后一次现身，便是在东海显灵。",},{0," 或许东海和麒麟之间有什么联系，只能先去找当地人打探一二。 ",},},sSeekNpc="东海龙王",sBackground="caomiaocun",}
ctTaskPlotConf[108213]={nID=108213,nIndex=214,nTaskId=108213,sTaskName="主线-东海传说",sTaskDescription="钱夫人的怒火",tTalk={{85,"少侠来东海是要买海鲜吗？保证新鲜的很！ ",},{0,"实不相瞒，我正在追寻麒麟的行踪。听闻东海的人都信奉麒麟，不知夫人是否知道…",},{85,"不知道不知道！赶紧离开不要妨碍我做生意！",},{0,"这女人真的是翻脸比翻书都快！",},{86," 救命呀！义母救我！ ",},{85,"玉儿… ",},},sSeekNpc="钱夫人",sBackground="caomiaocun",}
ctTaskPlotConf[201001]={nID=201001,nIndex=216,nTaskId=201001,sTaskName="支线-剑客的剑",sTaskDescription="看看剑客为何发呆",tTalk={{0,"贱客，你又对着这把铁剑发呆。一把破剑而已，有什么好执着的?",},{7,"剑，是剑客的生命！你懂什么！",},{0,"不对，这里面有情况！快从实招来！",},{7,"快看，有妖怪！前面的妖怪站住——再不站住，我要拔剑了！！",},},sSeekNpc="卓不凡",sBackground="qingyunmen",}
ctTaskPlotConf[201002]={nID=201002,nIndex=217,nTaskId=201002,sTaskName="支线-剑客的剑",sTaskDescription="拦住前面的小妖",tTalk={{200,"干、干啥呀，一言不合就拔剑……俺只是个路过的……",},},sSeekNpc="过路小兽",sBackground="qingyunmen",}
ctTaskPlotConf[201003]={nID=201003,nIndex=218,nTaskId=201003,sTaskName="支线-剑客的剑",sTaskDescription="继续追问剑客",tTalk={{0,"哎哎哎，这只是个涉世未深的小妖，饶牠去吧!",},{0,"倒是你，肯定在隐瞒什么，别想蒙混过关！",},{7,"啧……你当真想知道？",},{0,"我当然想知道！",},{7,"好，你随我来。",},},sSeekNpc="卓不凡",sBackground="qingyunmen",}
ctTaskPlotConf[201004]={nID=201004,nIndex=219,nTaskId=201004,sTaskName="支线-剑客的剑",sTaskDescription="前往天策府门外",tTalk={{121,"没想到又回来了。十五年前，三界会武，三山六派七十二洞府精锐尽出。我踏出这府门，就再没回来过……",},{0,"哎哟不错哦！想不到你还是大天策的高徒哟！",},{121,"嘘",},{201,"什么人？！在我天策府左近徘徊，所为何事？",},{121,"快打晕他们！作为曾经的门派第一人，我的出现会引起轰动的！",},{0,"呃……",},},sSeekNpc="天策府护卫",sBackground="qingyunmen",}
ctTaskPlotConf[201005]={nID=201005,nIndex=220,nTaskId=201005,sTaskName="支线-剑客的剑",sTaskDescription="打晕天策府护卫",tTalk={{0,"剑客让我动手的。",},},sSeekNpc="天策府护卫",sBackground="qingyunmen",}
ctTaskPlotConf[201006]={nID=201006,nIndex=221,nTaskId=201006,sTaskName="支线-剑客的剑",sTaskDescription="原来剑客又在吹牛",tTalk={{0,"你不是自称一代剑圣吗，怎么...？",},{7,"呃，不要在意这些细节。",},{202,"原来是卓师兄，当年你为了一个女弟子与大师兄反目，在三界会武中丢尽我派颜面，如今还想再来生事？",},},sSeekNpc="沈御风",sBackground="qingyunmen ",}
ctTaskPlotConf[201007]={nID=201007,nIndex=222,nTaskId=201007,sTaskName="支线-剑客的剑",sTaskDescription="问出大师兄的下落",tTalk={{121,"我不是来搞事的，是有急事找大师兄，烦请告知一声。",},{202,"既然这样，大师兄已隐居长安四平巷，二位请便吧！",},{0,"行啊贱客，没想到，你还真的有猛料啊……快走快走！",},{121,"走去哪里？",},{0,"会你的老情敌去啊！你不是要找他打架吗？",},{121,"我什么时候……喂，你等等！",},},sSeekNpc="沈御风",sBackground="qingyunmen",}
ctTaskPlotConf[201008]={nID=201008,nIndex=223,nTaskId=201008,sTaskName="支线-剑客的剑",sTaskDescription="与剑客前往四平巷",tTalk={{0,"进去啊！都到门口了，发什么愣啊！你怕了？",},{121,"笑话，咱怕过谁！就……就是有点小紧张。",},{0,"噢！我明白了，你怕见到老情人！怕什么，就说是见见老朋友嘛！",},{121,"不成不成，你先进去看看再说，万一里面根本没人呢！",},},sSeekNpc="卓不凡",sBackground="xiaochizhen",}
ctTaskPlotConf[201009]={nID=201009,nIndex=224,nTaskId=201009,sTaskName="支线-剑客的剑",sTaskDescription="几名捕快围了过来",tTalk={{203,"什么人，私闯他人宅邸！知道这家主人姓什么吗，说出来吓死你！",},{0,"难不成……这家主人姓鬼？",},{203,"好油滑的小贼！多说无益，抓回去再说！",},{0,"这就抓人？太草率了吧！你们到底是官家还是劫匪啊！",},},sSeekNpc="王捕快",sBackground="xiaochizhen",}
ctTaskPlotConf[201010]={nID=201010,nIndex=225,nTaskId=201010,sTaskName="支线-剑客的剑",sTaskDescription="打退王捕快",tTalk={{0,"这么喜欢干架是吧，来来来！",},},sSeekNpc="王捕快",sBackground="xiaochizhen",}
ctTaskPlotConf[201011]={nID=201011,nIndex=226,nTaskId=201011,sTaskName="支线-剑客的剑",sTaskDescription="王捕快求饶",tTalk={{203,"大侠饶命啊，呀，痛！痛！痛",},},sSeekNpc="王捕快",sBackground="xiaochizhen",}
ctTaskPlotConf[201012]={nID=201012,nIndex=227,nTaskId=201012,sTaskName="支线-剑客的剑",sTaskDescription="训话王捕快",tTalk={{0,"还乱不乱抓人？",},{203,"不了不了，请大侠高抬贵手！",},{0,"以后睁大你的狗眼办事，滚！",},{203,"是是是，这就滚！这就滚！",},},sSeekNpc="王捕快",sBackground="qingyunmen",}
ctTaskPlotConf[201013]={nID=201013,nIndex=229,nTaskId=201013,sTaskName="支线-剑客的剑",sTaskDescription="出去喊剑客进来",tTalk={{0,"（一拍肩膀）走，贱客，咱们……",},{204,"哪里来的乡野村夫，敢挡我们庞少爷的路？不想活了？",},{7,"嘿！这都被你发现了！",},{204,"你们这些外乡人，不要总想搞个大新闻嘛！对不对？我们向来以德服人——给我打！",},{121,"好好好，先干上一仗压压惊！",},{0," 唉，真是无可救药……",},},sSeekNpc="庞府家丁",sBackground="heyang",}
ctTaskPlotConf[201014]={nID=201014,nIndex=230,nTaskId=201014,sTaskName="支线-剑客的剑",sTaskDescription="打退庞府家丁",tTalk={{121,"正好让小爷练练手！",},},sSeekNpc="庞府家丁",sBackground="tianyinsi",}
ctTaskPlotConf[201015]={nID=201015,nIndex=231,nTaskId=201015,sTaskName="支线-剑客的剑",sTaskDescription="说服剑客跟你进去",tTalk={{0,"痛快了吧？痛快了就跟我进去。",},{121,"这个这个…我看就没有这个必要了吧？",},{0,"你心中这块石头放不下，以后还怎么帮我打…呃…怎么帮我除魔卫道？",},{121,"我……",},},sSeekNpc="卓不凡",sBackground="tianyinsi",}
ctTaskPlotConf[201016]={nID=201016,nIndex=232,nTaskId=201016,sTaskName="支线-剑客的剑",sTaskDescription="带剑客进入庭院",tTalk={{205,"二位是……？",},{121,"洛桦，你不记得我了吗？我是不凡啊！",},{121,"当年，我与师兄在三界会武中一决雌雄，身受重伤，你悉心救治于我，还赠我这把铁剑，你忘了吗？",},{205," 你是卓……师弟？ ",},{206,"哼哼！卓师弟，多年不见，你果然还是剑气逼人啊！ ",},{121,"好说！陆师兄，你我师出同门，彼此彼此！",},},sSeekNpc="洛桦",sBackground="tianyinsi",}
ctTaskPlotConf[201017]={nID=201017,nIndex=233,nTaskId=201017,sTaskName="支线-剑客的剑",sTaskDescription="大师兄要过上两招",tTalk={{206,"既然来了，咱们就按老规矩，先走上两招，如何？ ",},{121,"哼，求之不得。 ",},},sSeekNpc="陆师兄",sBackground="nanjiang",}
ctTaskPlotConf[201018]={nID=201018,nIndex=234,nTaskId=201018,sTaskName="支线-剑客的剑",sTaskDescription="到庭院大战",tTalk={{206,"哼，此间促狭，难以施展。你我二人院外战如何！ ",},{121,"好，奉陪到底！",},{205," 哎，你们两个……  ",},},sSeekNpc="陆师兄",sBackground="caomiaocun",}
ctTaskPlotConf[201019]={nID=201019,nIndex=235,nTaskId=201019,sTaskName="支线-剑客的剑",sTaskDescription="向陆夫人问些事情",tTalk={{0,"陆夫人，我心中有些许疑问，不知当讲不当讲。",},{205," 远来是客，少侠但讲无妨。 ",},{0,"从您的反应，我想剑客当年可能……误会了一些事。但十多年前，您为什么要赠剑给他呢？",},{205," 当年卓师弟以一柄银制细剑闻名于天下，我赠他铁剑，其实是说我二人并不相配…… ",},{0,"那他和陆师兄比剑负伤，你为何救他而不救陆师兄呢？",},{205,"因为剑一当时根本就毫发无伤啊……",},},sSeekNpc="洛桦",sBackground="caomiaocun",}
ctTaskPlotConf[201020]={nID=201020,nIndex=236,nTaskId=201020,sTaskName="支线-剑客的剑",sTaskDescription="与剑客并肩一战",tTalk={{206,"拜你所赐，我夫妻二人蛰居此地多年。如今你还来纠缠，当真不把我这个大师兄放在眼里吗？ ",},{121,"（鼻青脸肿）放马过来吧！我皱一下眉头就算不得好汉！",},{206," 好！那我可就不客气了！",},{0," 等等！陆师兄，你艺成在先，怎能以大欺小？",},{0,"我虽资辈尚浅，愿为卓兄助拳！ ",},{206,"哼！何必多说，一起上吧！",},},sSeekNpc="陆师兄",sBackground="caomiaocun",}
ctTaskPlotConf[201021]={nID=201021,nIndex=237,nTaskId=201021,sTaskName="支线-剑客的剑",sTaskDescription="带剑客离开这里",tTalk={{205,"你们快住手，不要再打了！！ ",},{0,"贱客，咱们还是走吧，其实…… ",},{121,"（摆手）呼……呼……等等…… 姓陆的，今天看在洛桦的面子上，我不伤你。",},{121,"日后再见，可没什么情面好讲了！ （气喘吁吁）呼……呼……我、等、着、你！",},},sSeekNpc="卓不凡",sBackground="caomiaocun",}
ctTaskPlotConf[201022]={nID=201022,nIndex=228,nTaskId=201022,sTaskName="支线-剑客的剑",sTaskDescription="查看剑客的伤势",tTalk={{121,"哎哟……哎哟喂，哎哟我的妈哎……",},{0,"别叫了，现在就是亲娘都未必认得出你。刚才放狠话的时候，你不是挺精神的吗？",},{121,"哼——！ ",},{0,"唉，你先在这等着，我去买点止血散给你疗伤。 ",},},sSeekNpc="卓不凡",sBackground="caomiaocun",}
ctTaskPlotConf[201023]={nID=201023,nIndex=238,nTaskId=201023,sTaskName="支线-剑客的剑",sTaskDescription="为剑客买些止血散",tTalk={{0,"咦？人呢？",},},sSeekNpc="孙百年",sBackground="caomiaocun",}
ctTaskPlotConf[201024]={nID=201024,nIndex=239,nTaskId=201024,sTaskName="支线-剑客的剑",sTaskDescription="剑客不知去了哪里",tTalk={{0,"小弟弟，刚才这里有个奇怪的大叔，你看到他去哪里了吗？ ",},{207,"娘说了，小羽不能跟陌生人讲话！",},{0," 看，我这里有糖噢！",},{207," 小羽看到了，小羽看到了！怪大叔去了百花姐姐那里，百花姐姐医术最好了！ ",},{0,"好好好，小羽乖。糖给你，我走啦！",},},sSeekNpc="小羽",sBackground="heyang",}
ctTaskPlotConf[201025]={nID=201025,nIndex=240,nTaskId=201025,sTaskName="支线-剑客的剑",sTaskDescription="向仙子问剑客下落",tTalk={{0,"这位仙子，刚才街口有个受伤的剑客，不知仙子可曾看到他去了何处？ ",},{208,"年糕，年糕……小花想吃年糕了",},},sSeekNpc="花仙子",sBackground="heyang",}
ctTaskPlotConf[201026]={nID=201026,nIndex=241,nTaskId=201026,sTaskName="支线-剑客的剑",sTaskDescription="去醉仙楼买年糕",tTalk={{209,"刚出锅的年糕哎，各位客官请了！ ",},{0,"小二，快给我来一块，我有急用！ （眼珠一转）",},{209,"好嘞！年糕一块，纹银十两！您是扫码还是现金啊？",},{0," 扫马？我还扫驴呢！十两银子一块的年糕，你怎么不去抢！",},{209,"抢？偶卖糕的，咱可不敢干那违法乱纪的勾当啊！",},},sSeekNpc="店小二",sBackground="heyang",}
ctTaskPlotConf[201027]={nID=201027,nIndex=242,nTaskId=201027,sTaskName="支线-剑客的剑",sTaskDescription="年糕引发的血案",tTalk={{209,"干啥啊？！切了我们的糕，又不想要了是不是？",},{0," 你要是想送给我，我倒是不介意。",},{209," 啥？弟兄们，给我往死里削！ ",},},sSeekNpc="店小二",sBackground="heyang",}
ctTaskPlotConf[201028]={nID=201028,nIndex=243,nTaskId=201028,sTaskName="支线-剑客的剑",sTaskDescription="将年糕带给花仙子",tTalk={{0,"给，你的年糕。这下可以告诉我剑客的下落了吧？",},{208,"唔……真甜！你那朋友，真不识好歹，我好心想帮他医治，他却头也不回的走了。 我还没跟他要年糕呢！真是的…… ",},{0,"那他到底去了哪里啊？",},{208," （伸手一指）喏，那边那边，出城去啦——那边可经常有人跳河哦！",},{0," 哈？那你还有闲心跟我要年糕？你们这都是啥人啊！！！",},},sSeekNpc="花仙子",sBackground="heyang",}
ctTaskPlotConf[201029]={nID=201029,nIndex=244,nTaskId=201029,sTaskName="支线-剑客的剑",sTaskDescription="寻找剑客",tTalk={{0,"冷静！冷静！贱客，你可别想不开，不值得啊！ ",},{7,"呸！这么浅的水能淹死我？我得有多矮？",},{0," 呃……那你……",},{7," 算了，既然你来了，就帮我个忙吧。",},{0," 什么忙？",},{7," 趁我没后悔，帮我把这把铁剑丢到河里去，越远越好！",},},sSeekNpc="卓不凡",sBackground="heyang",}
ctTaskPlotConf[201030]={nID=201030,nIndex=245,nTaskId=201030,sTaskName="支线-剑客的剑",sTaskDescription="将铁剑抛入河中",tTalk={{0,"真无奈！",},},sSeekNpc="卓不凡",sBackground="qingyunmen",}
ctTaskPlotConf[201031]={nID=201031,nIndex=246,nTaskId=201031,sTaskName="支线-剑客的剑",sTaskDescription="回到剑客那里",tTalk={{0,"已经丢掉了……那个，你不想知道当年是怎么一回事吗？",},{7,"知道如何，不知道又如何？世上的事情，又何必分得那么清楚明白？",},{7,"走吧，咱们可还有好多架要打呢！",},{0," 哟呵，认识你这么久，今天第一次觉得你像个剑客。",},{7,"哪个“剑”？",},{0," 剑客的剑。",},},sSeekNpc="卓不凡",sBackground="qingyunmen ",}
ctTaskPlotConf[201032]={nID=201032,nIndex=247,nTaskId=201032,sTaskName="支线-龙女托书",sTaskDescription="路遇一位落榜书生",tTalk={{300,"唉，唉！时不我与，日月逝矣！ ",},{0,"公子可是因功名之事而烦闷？今年若是未中，来年再考便是了，何必唉声叹气呢？ ",},{300,"少侠有所不知，前十三次上京，小生都是这样想的！ ",},{0,"（那还不回家种地！）这……公子宽心，想必是时运不济吧！",},},sSeekNpc="柳咏",sBackground="qingyunmen",}
ctTaskPlotConf[201033]={nID=201033,nIndex=248,nTaskId=201033,sTaskName="支线-龙女托书",sTaskDescription="继续和书生聊聊",tTalk={{300,"不知公子之后作何打算？ 我有旧友居于泾川一带，小生想先去探望一番，再回洞庭老家刻苦攻读。",},{0," 巧了，在下正要去泾川查探妖乱的缘由，咱们可以结伴而行。 ",},{300,"这…那边现在可不太平啊…",},{0," 放心吧，有我在，可保公子无恙！",},},sSeekNpc="柳咏",sBackground="qingyunmen",}
ctTaskPlotConf[201034]={nID=201034,nIndex=249,nTaskId=201034,sTaskName="支线-龙女托书",sTaskDescription="和书生一同商量",tTalk={{300,"真是劳烦少侠了！",},{0,"公子不必客气，咱们走吧！",},},sSeekNpc="柳咏",sBackground="qingyunmen",}
ctTaskPlotConf[201035]={nID=201035,nIndex=250,nTaskId=201035,sTaskName="支线-龙女托书",sTaskDescription="同书生前往泾川",tTalk={{302,"站住！前面是泾川龙君统辖的地界，闲杂人等不得前行了！！",},{0,"龙君乃水族之主，怎么连地上也成了你们的地盘了？我要是偏要过去呢？",},{300,"别别别，大家都是斯文人，讲道理嘛，对不对？正所谓君子动口不动手…… ",},{302,"动你妹夫！能动手就憋吵吵，众水军听令——动他！",},{0,"哎你怎么骂人呢……哎呀，要死要死！",},},sSeekNpc="虾兵蟹将",sBackground="qingyunmen",}
ctTaskPlotConf[201036]={nID=201036,nIndex=251,nTaskId=201036,sTaskName="支线-龙女托书",sTaskDescription="书生似乎有些担忧",tTalk={{300,"少侠，咱们这样莽撞，会不会冒犯仙家呀？",},{0," 这些下界散仙，和山野小妖没什么分别，而且他们身上邪气很重，想必不是善类。",},{300," 呼……但愿如此。 ",},{0,"咦？你看前面，怎么会有个姑娘在这种地方？",},},sSeekNpc="柳咏",sBackground="qingyunmen",}
ctTaskPlotConf[201037]={nID=201037,nIndex=252,nTaskId=201037,sTaskName="支线-龙女托书",sTaskDescription="问问发生了什么事",tTalk={{0,"这位姑娘，这荒郊野岭，又不太平。你怎么会只身一人在这种地方呢？",},{ 301,"我……我…… ",},{0,"我看姑娘神色凄然、面有淤青，想必有什么难言之隐吧？",},{301," 不瞒二位，我本是洞庭龙君的小女儿，因错嫁泾川龙君次子，才落得如此下场……",},{301," 我那丈夫近来性情大变，稍不顺心便将我毒打，公婆又溺爱于他，我这才…… ",},{300,"我从未见过如此厚颜无耻之人！姑娘放心，小生与姑娘算是同乡，定会为你讨回公道！ ",},{301,"此事怎敢劳烦二位……糟了，他们来了！",},},sSeekNpc="洞庭龙女",sBackground="qingyunmen",}
ctTaskPlotConf[201038]={nID=201038,nIndex=253,nTaskId=201038,sTaskName="支线-龙女托书",sTaskDescription="虾兵喊来镇海将军",tTalk={{303,"你们是何人，竟敢闯入我泾川之地？",},{302,"就是他们！刚才就是他们两人闯了进来，现在又与这婆娘窃窃私语，不知在密谋什么！ ",},{303,"龙君说了，大业将成，此时擅闯泾川者，格杀勿论！",},{0," 大业将成？倒要看看你们在搞什么鬼！",},},sSeekNpc="镇海将军",sBackground="qingyunmen",}
ctTaskPlotConf[201039]={nID=201039,nIndex=254,nTaskId=201039,sTaskName="支线-龙女托书",sTaskDescription="龙女托你们传信",tTalk={{301,"此地不宜久留，二位还是快走吧。只是…… ",},{0,"姑娘有事，但讲无妨！ 小女子有家书一封，想请二位转交家父。",},{301,"还有这块玉佩，您将它挂在洞庭南岸的大树上，自会有人迎接。",},{0," 好！姑娘放心，我们一定将信带到！",},{301," 大恩不言谢，小女子这条性命，就交托给二位了！",},{7,"南岸的大树……应该就在前面了。",},{0," 快把那块玉佩挂在树上吧！",},},sSeekNpc="洞庭龙女",sBackground="heyang",}
ctTaskPlotConf[201040]={nID=201040,nIndex=255,nTaskId=201040,sTaskName="支线-龙女托书",sTaskDescription="找洞庭南岸的大树",tTalk={{0,"南岸的大树……应该就在前面了。",},{300," 快把那块玉佩挂在树上吧！",},},sSeekNpc="柳咏",sBackground="heyang ",}
ctTaskPlotConf[201041]={nID=201041,nIndex=256,nTaskId=201041,sTaskName="支线-龙女托书",sTaskDescription="将玉佩挂在树上",tTalk={{0,"恩，相信很快有人出来接应我们的！",},},sSeekNpc="柳咏",sBackground="heyang",}
ctTaskPlotConf[201042]={nID=201042,nIndex=257,nTaskId=201042,sTaskName="支线-龙女托书",sTaskDescription="洞庭水兵误会你们",tTalk={{304,"什么人？在我洞庭沿岸鬼鬼祟祟，所为何事？！ ",},{0,"呃，这位仙将…… ",},{304,"来虾，把他们拿下！",},{0," 小心！",},},sSeekNpc="洞庭水兵",sBackground="heyang",}
ctTaskPlotConf[201043]={nID=201043,nIndex=258,nTaskId=201043,sTaskName="支线-龙女托书",sTaskDescription="书生赶忙表明来意",tTalk={{300,"几位仙将确实误会了，我二人是受了龙君之女所托，前来报信的，您看树上那块玉佩……",},{304," 是公主的玉佩？！哎呀，我们有眼无珠，惊扰了贵客，真是该死！ ",},{304,"最近天下不太平，您身旁这位少侠又不似凡人，这才让我们误会了。",},{300," 无妨，正事要紧，还是快带我们进去面见龙君吧！ ",},{304,"是是是，我来带路，二位请随我来。     ",},},sSeekNpc="柳咏",sBackground="heyang",}
ctTaskPlotConf[201044]={nID=201044,nIndex=259,nTaskId=201044,sTaskName="支线-龙女托书",sTaskDescription="将此事禀明龙君",tTalk={{305,"吾乃洞庭之主，不知二位由凡界来此，所为何事？ ",},{300,"龙君有所不知，令嫒在泾川受尽夫君和公婆的欺凌，如今更是被逼出宫，流落于荒野！",},{305," 什么……竟有此事！！哎呀，都怪本王识人不清，竟将爱女推入火坑！",},{300," 这是令嫒托小生转交的书信，请龙君过目。",},{306,"地动山摇）哇呀呀呀呀！是谁，竟敢欺侮我宝贝侄女，气死我也！！吼…… ",},{305,"糟了，吾弟钱塘来了！ ",},},sSeekNpc="洞庭君",sBackground="heyang",}
ctTaskPlotConf[201045]={nID=201045,nIndex=260,nTaskId=201045,sTaskName="支线-龙女托书",sTaskDescription="天上出现巨龙身影",tTalk={{0,"天怎么黑了……谁，谁啊？",},{306,"宝贝侄女在泾川受人欺辱，此话当真？若有半句虚言，老夫定将你二人生吞活剥！",},{0,"我等不远千里，前来报信。堂堂洞庭龙宫，就是如此款待来客的吗？",},{306,"还敢嘴硬！小娃娃，你看你是活腻了！",},},sSeekNpc="钱塘君",sBackground="heyang",}
ctTaskPlotConf[201046]={nID=201046,nIndex=261,nTaskId=201046,sTaskName="支线-龙女托书",sTaskDescription="钱塘君仍然傲慢",tTalk={{306,"你是何人，竟能抵挡老夫一怒之威？",},{0," 我是何人，无关紧要……可您的宝贝侄女，现在还在泾川受苦呢！",},{305,"舍弟钱塘，因触犯天条在此思过，方才惊扰二位了！",},{305,"二弟，还不赔礼？",},{306," 哼！两个凡俗之辈，何必与他们客气！兄长，我要飞往泾川，去救宝贝侄女啦！！",},{305," 你现下是戴罪之身，万万不可……",},},sSeekNpc="钱塘君",sBackground="heyang",}
ctTaskPlotConf[201047]={nID=201047,nIndex=262,nTaskId=201047,sTaskName="支线-龙女托书",sTaskDescription="龙君似乎有话要讲",tTalk={{305,"这下要出大事了！我这兄弟虽非恶徒，但性情也实在太过粗莽了！ 还望二位能不计前嫌，替老夫去泾川劝诫吾弟，切莫让他闯下大祸！",},{300," 小、小生在外许久，家中甚是想念。",},{0,"既然信已送到…… 龙君放心！我们在泾川还有事要办，此事就包在我们二人身上，是吧书生？",},{300,"（关我什么事啊，我不想死啊！）呃，是是是，我、我等义不容辞……",},},sSeekNpc="洞庭君",sBackground="nanjiang",}
ctTaskPlotConf[201048]={nID=201048,nIndex=263,nTaskId=201048,sTaskName="支线-龙女托书",sTaskDescription="与书生回到泾川",tTalk={{301,"你们可回来了！叔父已经气势汹汹的杀进泾河了，这可如何是好？",},{300,"(一秒变脸）你尽管放心，有我在，一定不会有事的！",},{301,"叔父的脾气我最清楚不过，要是这次犯下大错，可就难以回头了呀！",},{0," 在这里干着急也没用。事不宜迟，我们赶过去看看再说！  ",},},sSeekNpc="洞庭龙女",sBackground="nanjiang",}
ctTaskPlotConf[201049]={nID=201049,nIndex=264,nTaskId=201049,sTaskName="支线-龙女托书",sTaskDescription="赶往泾川龙宫",tTalk={{303,"钱塘君，莫要冲动啊！",},{303," 钱……钱塘君！快，快来人呐！ ",},{306,"挡我者死！！",},{303,"我这条小命哟，今天是要归位啦。",},{0,"（灵机一动）钱塘君威名远播啊！好，我也来凑凑热闹！",},},sSeekNpc="镇海将军",sBackground="nanjiang",}
ctTaskPlotConf[201050]={nID=201050,nIndex=265,nTaskId=201050,sTaskName="支线-龙女托书",sTaskDescription="劝阻钱塘君",tTalk={{306,"哼，又是你们两个，难道想要阻拦老夫？",},{300,"小、小、小生不敢。只是那啥，那个那个……就那啥……",},{306," 哼，不知所谓！想当年老夫一怒，令唐尧之世连发九年洪水，一条泾川野龙，算得了什么？不必多言！",},{0," 钱塘君勇毅果决，名不虚传！我等愿在两旁策应，为您掠阵！ ",},{300,"（低声）喂喂，咱们不是来劝架的吗？你怎么开始火上浇油了？ ",},{0,"(低声）你忘了我来泾川是干嘛的啦？不搅他个地覆天翻，怎么查出泾川妖乱的缘由？ ",},{300," 你……",},},sSeekNpc="钱塘君",sBackground="nanjiang",}
ctTaskPlotConf[201051]={nID=201051,nIndex=266,nTaskId=201051,sTaskName="支线-龙女托书",sTaskDescription="眼前便是龙女夫君",tTalk={{307,"呵！我当是谁，原来是叔父大驾光临。久疏拜候，不知您这把老骨头，可还安好啊？",},{306,"小畜生，我洞庭龙女下嫁于你，你非但不感恩戴德，竟还赶她出宫！今天不抽了你的龙筋，算你没见过真龙！ ",},{307,"哦？抽我龙筋？噫哈哈哈……这话放在几百年前，还可一听。而现在，简直是个笑话…… ",},{300,"哎有话好好说，都是一家人，莫要伤了和气！少侠，你快劝劝…… ",},},sSeekNpc="泾川龙子",sBackground="nanjiang",}
ctTaskPlotConf[201052]={nID=201052,nIndex=267,nTaskId=201052,sTaskName="支线-龙女托书",sTaskDescription="在两人间煽风点火",tTalk={{0,"哟呵！这小子竟敢瞧不起我大钱塘君，真是不知死活！是吧，钱塘君？",},{306,"哼！一会儿看这小畜生还笑不笑得出！",},},sSeekNpc="泾川龙子",sBackground="nanjiang",}
ctTaskPlotConf[201053]={nID=201053,nIndex=268,nTaskId=201053,sTaskName="支线-龙女托书",sTaskDescription="听听泾阳君说什么",tTalk={{307,"（奄奄一息）你们以为我会……这么容易……就死在这里？呵，呵呵呵呵…… ",},{307,"我父王……还有那位异域大神，不会放过你们的……！",},{ 308,"竟敢害我儿性命，今日让你们死无葬身之地！！ ",},{306,"泾川龙王，你纵容儿子胡作非为，老夫正要找你，你却自己送上门来！",},},sSeekNpc="泾川龙子",sBackground="nanjiang",}
ctTaskPlotConf[201054]={nID=201054,nIndex=269,nTaskId=201054,sTaskName="支线-龙女托书",sTaskDescription="终于见到泾川龙君",tTalk={{0,"二位且慢，在下有事想问。听闻泾川流域，本是支脉末流，在三江四海中也算不得丰饶…… ",},{0,"可是近来，龙君不觉得泾川一代，气势太盛了吗？",},{308," 天下人都觉得我泾川池小鱼稀，难道我们就要任人鱼肉、坐以待毙吗？那只是大神显圣之前的事了！ ",},{0,"你果然与人勾结，祸害一方水土！难道不怕玉帝调遣天兵天将，平了你的泾川龙宫吗？ ",},{308,"小娃娃，你口中的玉帝，此时知道的未必比你多……而且你觉得，今天本王还会放你走么？！",},},sSeekNpc="泾川龙王",sBackground="nanjiang",}
ctTaskPlotConf[201055]={nID=201055,nIndex=270,nTaskId=201055,sTaskName="支线-龙女托书",sTaskDescription="劝钱塘君刀下留人",tTalk={{0,"钱塘君",},{306,"如此大逆不道的野龙，竟敢藐视天帝，怎可留他？",},{0,"您只是想公报私仇吧……",},{308,"尔等人多势众，本王先行一步。下次再见之时，便是你们的死期！",},{0,"糟了！",},},sSeekNpc="泾川龙王",sBackground="nanjiang",}
ctTaskPlotConf[201056]={nID=201056,nIndex=271,nTaskId=201056,sTaskName="支线-龙女托书",sTaskDescription="到头来白忙一场",tTalk={{306,"都怪你这娃娃横加阻拦，让这孽龙跑了！！",},{300,"少侠，这下竹篮打水一场空了吧？人算不如天算哟！",},{0," 好你个小秀才，我白忙一场，你还说风凉话！",},{306," 哼！你若想查，大可留在此地，老夫可要打道回府了。",},{306,"书呆子，你可愿随我叔侄二人，一同回洞庭去？ ",},{300,"小生愿意，小生愿意！",},{300,"少侠，相见即是有缘，日后一定要来洞庭看我哦！ ",},{0," 呀呀呀！气死我了！",},},sSeekNpc="柳咏",sBackground="nanjiang",}
ctTaskPlotConf[201057]={nID=201057,nIndex=272,nTaskId=201057,sTaskName="支线-先天秘谱",sTaskDescription="回到师门拜见师父",tTalk={{0,"师父在上，小徒在外游历，久疏拜候。在这给您行礼了！ ",},{1,"好好好！徒儿在外劳苦，罢了罢了！ 你久离山门，不知有没有好好修习门中所学啊？",},{0," 弟子不敢懈怠！",},{1," 好，口说无凭。你与首席弟子切磋一番，让为师看看！",},},sSeekNpc="师傅",sBackground="nanjiang",}
ctTaskPlotConf[201058]={nID=201058,nIndex=273,nTaskId=201058,sTaskName="支线-先天秘谱",sTaskDescription="与首席弟子切磋",tTalk={{401,"回来了？在外许久，肯定吃了不少苦吧？",},{0," 唉，现在三界不宁，世间动荡，真不知何时是个尽头。",},{401," 能出门游历四方，你还抱怨……总比我整天在这被新人吊打要好吧？",},{0,"其实我这次来，就是为了…… ",},{401,"又要打是吧？来吧来吧，反正我都习惯了。",},},sSeekNpc="首席弟子",sBackground="heyang",}
ctTaskPlotConf[201059]={nID=201059,nIndex=274,nTaskId=201059,sTaskName="支线-先天秘谱",sTaskDescription="回见师父",tTalk={{1,"嗯！不错不错，几日不见，徒儿的修为又精进了不少。",},{0,"多亏师父教导，只是……徒儿最近觉得进境迟滞，似乎满身的修为渐渐施展不出了……",},{1,"哦？我门中绝技，已经跟不上你的修为了么？ ",},{0,"这……弟子失言了！ ",},{1,"不必慌张，为师并未怪你……倒不如说，为师很是欣慰。",},},sSeekNpc="师傅",sBackground="heyang",}
ctTaskPlotConf[201060]={nID=201060,nIndex=275,nTaskId=201060,sTaskName="支线-先天秘谱",sTaskDescription="继续听师父训话",tTalk={{1,"我门中绝学，千百年来，无人能一窥全境。而你只用了短短十几年，就已触及瓶颈…… ",},{0," 师父是不是有什么压箱底的绝招，打算传给弟子啊？",},{1," 并没有。不过为师可以指给你一条明路，至于能否得遇机缘，就看你的造化了。",},{1,"有位精研三教的菩提祖师，其人妙法通神十分了得。现在他在药王谷的道观中修行，你可潜心求访，如能得他指点，修为更进一步。",},{0," 原来如此，多谢师父！徒儿这就动身！ ",},},sSeekNpc="师傅",sBackground="wanfudong",}
ctTaskPlotConf[201061]={nID=201061,nIndex=276,nTaskId=201061,sTaskName="支线-先天秘谱",sTaskDescription="求访菩提老祖",tTalk={{0,"这位大哥，听闻有一位老神仙再次修行，神通广大，您知道他在哪里吗？",},{402," 噢，你是来找老祖的吧！可前日老祖已出门，不在观内啊。",},{0," 那我岂不白跑一趟…… ",},{402,"少侠别急，他老人家已即仙位，你只要在这的东南角焚香三拜，诚心祷祝，老祖一定会显圣的！",},},sSeekNpc="方寸山路人",sBackground="wanfudong",}
