ctTaskPlotConf[201123]={nID=201123,nIndex=338,nTaskId=201123,sTaskName="支线-人间美味",sTaskDescription="找到第3颗九孔螺",tTalk={{0,"请赐我九孔螺吧！",},},sSeekNpc="高太公",sBackground="nanjiang",}
ctTaskPlotConf[201124]={nID=201124,nIndex=339,nTaskId=201124,sTaskName="支线-人间美味",sTaskDescription="忽然跳出一个夜叉",tTalk={{418,"站住！你是哪来的，敢到我们这里来挖宝？",},{0,"您这可就误会了，我只是来寻几只九孔螺，用来做菜而已。",},{418,"胡说！我看你鬼鬼祟祟，几步一探。肯定又是哪个龟孙到处乱发藏宝图！",},{418,"再说了，挖到所有东西都要上交国家，你不知道吗？这海上的东西，自然归我们东海龙王所有！ ",},{0,"哎呀，我真的没骗人，不信你看，就是这些螺。",},{418,"哼，螺也不行！都是我的！ ",},},sSeekNpc="夜叉",sBackground="nanjiang",}
ctTaskPlotConf[201125]={nID=201125,nIndex=340,nTaskId=201125,sTaskName="支线-人间美味",sTaskDescription="带九孔螺回高老庄",tTalk={{0,"老太公，这些九孔螺，您看可还合意？",},{416," 嗯，不错不错！肉厚肚圆，肥嫩已极！如今材料已经备齐，老朽这就起火开坛！",},{416," 佛跳墙烹煮起来十分耗时，小女翠兰准备了一些茶点，少侠还是稍坐片刻吧！",},{0," 既然如此，那就辛苦您老了！",},},sSeekNpc="高太公",sBackground="nanjiang",}
ctTaskPlotConf[201126]={nID=201126,nIndex=341,nTaskId=201126,sTaskName="支线-人间美味",sTaskDescription="舌尖上的高老庄",tTalk={{419,"少侠先耐心等候吧，这佛跳墙不比寻常菜肴，火候时辰可是一点都马虎不得！",},{0,"敢问姑娘，这佛跳墙究竟有何妙处，能取个这么大口气的名字！",},{419,"自古以来，佛跳墙这种佳肴都必须用绍兴酒坛盛装，并用上品绍酒与佐料一同调合。",},{419,"待各类主料齐备，密封于一坛，再用上好炭火慢慢煨足三个时辰，期间香气绝不可外泄……",},{419,"几十种材料煨于一坛，既有共同的荤香之气，又有各类食材自身的鲜香，入口软糯滑嫩，味厚而不腻。",},{419,"可说是味中有味，回味无穷！",},{0,"原来这佛跳墙的制法还有这么多讲究，想必味道自然极其鲜美。",},{0,"难怪净坛使者时至今日，仍然念念不忘…… ",},{419,"唉，这冤家……当年寄住我家，也不知是为了我，还是为了这坛佛跳墙呢？",},{420,"好香的味道！是谁在庄中烹制美味，快端上来给本大王尝尝！",},},sSeekNpc="翠兰",sBackground="nanjaing",}
ctTaskPlotConf[201127]={nID=201127,nIndex=342,nTaskId=201127,sTaskName="支线-人间美味",sTaskDescription="乱入的炒饭大魔王",tTalk={{0,"你们是哪里来的妖怪，竟想打家劫舍？",},{420," 你都知道我们是妖怪了，还不许我们打家劫舍？是不是让我们吃人才开心啊？",},{0,"啧啧啧，真是不讲理！ ",},{0,"嗯？似乎也有点道理…… ",},{420,"你看，理亏了吧？我们当然不用守你们凡人的规矩啦！快把东西交出来吧！ ",},{0,"可我们人界还有一条规矩，你听过没有？ 啥规矩？ 打人是不对的，但打妖怪可就不同了！   ",},},sSeekNpc="炒饭大魔王",sBackground="nanjiang",}
ctTaskPlotConf[201128]={nID=201128,nIndex=343,nTaskId=201128,sTaskName="支线-人间美味",sTaskDescription="佛跳墙终于做好了",tTalk={{416,"来，这坛佛跳墙大功告成，快趁热送去吧！",},{0," 实在有劳高太公和翠兰小姐了，晚辈代净坛使者谢过二位！ ",},{416,"相识一场，即是有缘。若是少侠不嫌老朽手艺不精，日后还请常来啊！ ",},{0,"多谢老太公！晚辈告辞！ ",},},sSeekNpc="高太公",sBackground="nanjiang",}
ctTaskPlotConf[201129]={nID=201129,nIndex=344,nTaskId=201129,sTaskName="支线-人间美味",sTaskDescription="饥渴难耐的八戒",tTalk={{0,"我回来啦！你看…… ",},{63,"哇……快、快开坛，老猪我都等不及啦！ 别急别急，可别打翻了美味佳肴！",},{17,"哼！不知你们得了什么好宝贝，给俺老孙看看可好？",},{63,"我是不是饿出幻觉了？怎么好像听到那遭瘟猴子的声音了？ ",},{0,"大……大圣？",},},sSeekNpc="美男八戒",sBackground="nanjiang",}
ctTaskPlotConf[201130]={nID=201130,nIndex=345,nTaskId=201130,sTaskName="支线-人间美味",sTaskDescription="赶忙向孙大圣解释",tTalk={{0,"大圣消消气，晚辈只是看净坛使者闷闷不乐，心中不忍，才想帮他打打牙祭的。",},{63," 我…滴个妈哎！真是猴子，你，你你……",},{63,"呃哼！你这人，怎么能拿这些犯戒的东西，引诱俺老猪呢！这样会让俺犯错误的！",},{0," 我…… ",},{17,"好你个呆子！自己六根不净，还要倒打一耙，我看你是皮痒了！ ",},{63,"别别别！殴打宗教神职人员可是大罪，你现在是有身份的人，可不能动粗哇！",},{17,"佛祖我都打过，还不敢动你这猪头？！我看你往哪里跑！",},},sSeekNpc="孙悟空",sBackground="nanjiang",}
ctTaskPlotConf[201131]={nID=201131,nIndex=346,nTaskId=201131,sTaskName="支线-人间美味",sTaskDescription="阻止大圣出手",tTalk={{0," 别别别，大圣息怒啊！有话好好说！",},{17,"你别拦着我，真是气死俺老孙了，哼！",},},sSeekNpc="孙悟空",sBackground="nanjiang",}
ctTaskPlotConf[201132]={nID=201132,nIndex=347,nTaskId=201132,sTaskName="支线-人间美味",sTaskDescription="劝孙大圣消消气",tTalk={{17,"苦修十载盼正果，一朝成圣又思凡。如此这般，何苦当初呢？",},{0,"话虽如此，可是……大圣成佛之时，难道心中就没有丝毫挂碍，或些许遗憾吗？",},{17,"渺渺天地，进退苦海，又有什么分别？",},{17,"怎么样也好，既然选了，就不该回头——不是么？ ",},{0,"难道这世间，就没有两全之法么？ ",},{17,"老孙早已不在乎了，而你，或许有机会找到这个答案。",},{17,"另外，别再叫我大圣了，我是——斗战胜佛。",},{0,"……是，晚辈记下了。",},{63,"哎！！猴，猴哥，别走啊——你等等我啊！",},},sSeekNpc="孙悟空",sBackground="nanjiang",}
ctTaskPlotConf[201133]={nID=201133,nIndex=348,nTaskId=201133,sTaskName="支线-钱塘逼婚",sTaskDescription="去洞庭看望柳咏",tTalk={{0,"书生，好久不见啦！近来可好啊？",},{300," 呀，原来是少侠，有失远迎，有失远迎……",},{0," 咦，本以为你看到我会满心欢喜，怎么看起来并不高兴的样子？",},{300," 唉！上次分别后，我与龙女叔侄二人同回洞庭，本来和和气气的，没想到钱塘君突然就要我娶龙女为妻！",},{0," 这是好事儿啊，为什么还愁眉苦脸的？ ",},{300,"婚姻大事，理当父母之命、媒妁之言，怎可如此草率？",},{300,"况且我救下龙女，完全是出于道义，并非是想趁人之危啊！",},{306,"无知小儿！本龙君念你救过宝贝侄女，将她下嫁于你，你却推三阻四！娶我洞庭龙女难道委屈你了不成？",},},sSeekNpc="柳咏",sBackground="nanjiang",}
ctTaskPlotConf[201134]={nID=201134,nIndex=349,nTaskId=201134,sTaskName="支线-钱塘逼婚",sTaskDescription="钱塘君来逼婚",tTalk={{0,"钱塘君身为长辈，又贵为一方江河之主，如此逼迫一个晚辈，怕是不妥吧？",},{306," 又是你？我龙宫之女下嫁这穷秀才，是他修来的福气，你不要多管闲事！",},{0," 话不能这样说，我与柳兄也算患难之交，婚姻大事轻率不得，我又怎能坐视不理？ ",},{306,"哼，几日不见，看来你这娃娃翅膀硬了许多？本龙君倒要看看，你又得了什么好本事！ ",},{300," 哎两位，有话好好说，别动手啊！ ",},},sSeekNpc="钱塘君",sBackground="nanjiang",}
ctTaskPlotConf[201135]={nID=201135,nIndex=350,nTaskId=201135,sTaskName="支线-钱塘逼婚",sTaskDescription="钱塘君不依不饶",tTalk={{0,"承让了，龙君！",},{306," 哼，算你有两下子！但是这秀才如此不给我龙族面子，此事可不能就这么算了！",},{0," 龙君如果还是不愿放过柳兄，那咱们只有去一趟洞庭龙宫，找令兄评评理了！ ",},},sSeekNpc="钱塘君",sBackground="nanjiang",}
ctTaskPlotConf[201136]={nID=201136,nIndex=351,nTaskId=201136,sTaskName="支线-钱塘逼婚",sTaskDescription="去洞庭龙宫讨说法",tTalk={{0,"拜见龙君，晚辈有礼了！",},{305," 原来是少侠！上次为了小女之事，真是让少侠费心了！",},{0," 龙君言重了，其实此次前来，仍是为了公主的事情。",},{305," 少侠说的是柳公子与小女的婚事吧？确实，此事舍弟是有些鲁莽，但是…… ",},{301,"父亲，叔父！我已是再嫁之人，柳公子不愿屈就是理所当然的。你们若是再苦苦相逼，我便再不回这龙宫来了！",},{300," 我没有这个意思啊，你去哪里啊公主，喂……",},},sSeekNpc="洞庭君",sBackground="guiwangzong",}
ctTaskPlotConf[201137]={nID=201137,nIndex=352,nTaskId=201137,sTaskName="支线-钱塘逼婚",sTaskDescription="带上柳咏去追龙女",tTalk={{300,"这可如何是好，少侠，咱们要不要去追啊？ ",},{0,"这还用问？唉，说句实话，我劝你还是珍惜这段缘分吧…… ",},{300,"少侠何出此言？ 就你这榆木脑袋，错过这次，基本上孤独一生了。",},{300,"呃……",},{0,"还不快追？！",},},sSeekNpc="柳咏",sBackground="guiwangzong",}
ctTaskPlotConf[201138]={nID=201138,nIndex=353,nTaskId=201138,sTaskName="支线-钱塘逼婚",sTaskDescription="与柳咏商量对策",tTalk={{0,"龙族之气到这里就断了，也不知公主究竟跑到哪去了。 ",},{300,"奇怪，刚才明明看到她从这边上岸的…… ",},{301,"柳公子，救我……！",},{0,"糟了，公主出事了！",},},sSeekNpc="柳咏",sBackground="guiwangzong",}
ctTaskPlotConf[201139]={nID=201139,nIndex=354,nTaskId=201139,sTaskName="支线-钱塘逼婚",sTaskDescription="追上掳走公主的人",tTalk={{0,"你们是什么人，连洞庭龙女都敢抓？",},{ 421,"哎呀呀，被发现了，你们带她先走，我来挡下他们！",},{0," 就您这小身板儿，还是先顾好自己吧！",},},sSeekNpc="鬼妖",sBackground="guiwangzong",}
ctTaskPlotConf[201140]={nID=201140,nIndex=355,nTaskId=201140,sTaskName="支线-钱塘逼婚",sTaskDescription="似曾相识的玉佩",tTalk={{300,"少侠你看，这块东西…好像是三公主那块玉佩！",},{0,"应该就是这边了！ ",},{421,"哈哈哈哈！丢了泾河老龙这颗废棋，却抓到了洞庭老龙的宝贝女儿…… ",},{300,"放、开、她！ ",},{421,"那边那个，不是那神祖后人吗？这次可真是没白来！！还有，刚才鬼叫的是谁，把他给我弄死！ ",},{300,"噢！原来你们认识，那我就不打扰了，告辞告辞！",},{0,"别跑了，咱们早就被包围了…… ",},},sSeekNpc="鬼妖",sBackground="wanfudong",}
ctTaskPlotConf[201141]={nID=201141,nIndex=356,nTaskId=201141,sTaskName="支线-钱塘逼婚",sTaskDescription="两人被围在垓心",tTalk={{421,"哼哼哼哼，想跑？幸好这次带了不少人，不然可抓不了这么多肥羊！",},{300," 可恶，这些妖人怎么杀都杀不完！ 唉，想不到我柳咏风华正茂，今天就要命丧于此，",},{300,"可怜我孑然一身，连个老婆都没有！",},{0," 你这书呆子，早干嘛去了？还不都是你婆婆妈妈…",},{306," 连我洞庭龙女都敢动，你们这是自寻死路！！",},{300,"钱塘君？太好了，这下有救了",},},sSeekNpc="鬼妖",sBackground="wanfudong",}
ctTaskPlotConf[201142]={nID=201142,nIndex=357,nTaskId=201142,sTaskName="支线-钱塘逼婚",sTaskDescription="钱塘君带水族赶来",tTalk={{0,"龙君！您来得正是时候！",},{306,"哼，小娃娃，你不是挺有本事的么？ ",},{0,"唉，双拳难敌四手啊！龙君还是快快号令水族，救出公主吧！",},{306,"还用你说？龙女乃我洞庭明珠，任谁都不能碰她一根毫毛！把这些妖人给我拿下！",},},sSeekNpc="钱塘君",sBackground="wanfudong",}
ctTaskPlotConf[201143]={nID=201143,nIndex=358,nTaskId=201143,sTaskName="支线-钱塘逼婚",sTaskDescription="击败鬼妖",tTalk={{304,"得令！杀——呀呀呀！",},},sSeekNpc="鬼妖",sBackground="wanfudong",}
ctTaskPlotConf[201144]={nID=201144,nIndex=359,nTaskId=201144,sTaskName="支线-钱塘逼婚",sTaskDescription="此事竟与地府有关",tTalk={{421,"区区江中小龙，也敢开罪于我们地府阴司？",},{0,"地府？难道你们不是万鬼门的人？",},{421,"哼哼！万鬼门早已江河日下，靠他们，我们大王何时能有出头之日？",},{0," 难怪大圣说万鬼门与地府叛逆勾结，原来就是你们！",},{421,"你们知道的太晚了，阴司十殿，已被我们占据大半，到时连天庭都不是我们的对手。",},{421,"钱塘老鬼，回去告诉你兄长，趁早投诚。不然到时无论钱塘洞庭，都将变成一潭死水！走！ ",},{306,"你说什么？",},},sSeekNpc="鬼妖",sBackground="wanfudong",}
ctTaskPlotConf[201145]={nID=201145,nIndex=360,nTaskId=201145,sTaskName="支线-钱塘逼婚",sTaskDescription="告别众人前往地府",tTalk={{0,"呼！好在这次有惊无险……公主，你没什么大碍吧？ ",},{300,"…… ",},{0,"喂！书呆子，你倒是说点什么啊！ ",},{300,"我…我绝没有嫌弃公主的意思！",},{301,"我要回去了……！ ",},{300,"哎，你别走啊三公主！我…… ",},{0,"书呆子，今天只能帮你到这里了。我还要去地府走上一遭，你要抓住机会哦！",},{300,"啊？哦哦哦！少侠——千万要小心啊！",},},sSeekNpc="柳咏",sBackground="wanfudong",}
ctTaskPlotConf[201146]={nID=201146,nIndex=361,nTaskId=201146,sTaskName="支线-前尘旧事",sTaskDescription="首席弟子将你拦下",tTalk={{0,"大师兄，你怎么在这里？我正要回去见师傅，向他老人家禀告一件大事呢！",},{401,"不成，现在师门那边，你可回不去了！",},{0," 此话怎讲？ ",},{401,"不知为何，地府派人上告天庭，说你与孙大圣联手大闹地府。玉帝拿大圣没有办法，就只能拿你开刀了啊！ ",},{401,"现在师门被众多天兵天将把持，师父算到你必经此地，让我来告诉你，千万别回去！",},{0,"我正要上天庭，通禀地府勾结巫族造反的事，现在可如何是好？",},{401,"如今看来，天庭你是去不成了。还是先避避风头，待师父想想办法再说。 ",},},sSeekNpc="首席弟子",sBackground="wanfudong",}
ctTaskPlotConf[201147]={nID=201147,nIndex=362,nTaskId=201147,sTaskName="支线-前尘旧事",sTaskDescription="一队天兵拦住去路",tTalk={{422,"站住！你可是与那孙猴子一同大闹地府的逆贼？",},{0,"这位上仙，我是被人诬告的！如今三界浩劫近在眼前，我有要事禀告玉帝呀！",},{422,"想见玉帝也可以，我们将你缉拿归案，让玉帝发落便是！只是不知，那时你还能否有命狡辩！",},{ 306,"且慢！这小娃娃虽然处处让人不悦，但还不至于不分是非，我看当中一定有人从中作梗！",},},sSeekNpc="天兵",sBackground="wanfudong",}
ctTaskPlotConf[201148]={nID=201148,nIndex=363,nTaskId=201148,sTaskName="支线-前尘旧事",sTaskDescription="有人来相助",tTalk={{300,"哈哈哈，少侠别怕，我们来帮你啦！ ",},{0,"书呆子？",},},sSeekNpc="天兵",sBackground="qingyunmen",}
ctTaskPlotConf[201149]={nID=201149,nIndex=364,nTaskId=201149,sTaskName="支线-前尘旧事",sTaskDescription="原来是柳咏一行人",tTalk={{0,"原来是你们啊！",},{300,"天庭要捉你，到处都传遍啦。本来我们也不知怎样帮你，但你也知道钱塘君的暴脾气，说走就走。我们就急忙跟着赶来了呀！ ",},{0,"你们带洞庭水族来帮我，若被天庭知道了，岂不会迁罪于洞庭龙宫？",},{301,"少侠，别想那么多了。叔父他凭戴罪之身来救你，你若不走，岂不辜负他一片好意？",},{0," 我…… ",},{300,"快走吧！等你想到办法伸冤，我们自然不会有事啦！ ",},},sSeekNpc="柳咏",sBackground="qingyunmen",}
ctTaskPlotConf[201150]={nID=201150,nIndex=365,nTaskId=201150,sTaskName="支线-前尘旧事",sTaskDescription="又遇哪吒",tTalk={{0," 哪吒？？",},{50," 你是谁啊？我认识你么？ ",},{0,"我是……",},{422," 仙子，末将怎么觉得这人就是画像上的逆贼？",},{50," 胡说！那人我见过，难道本仙子会认错人不成？",},{50,"喂，你这凡人，这里没你的事了，还不快走？",},{0," (原来是在帮我……)好好好，在下打扰了，这就告退！",},},sSeekNpc="哪吒",sBackground="qingyunmen",}
ctTaskPlotConf[201151]={nID=201151,nIndex=366,nTaskId=201151,sTaskName="支线-前尘旧事",sTaskDescription="一路来到兽王山",tTalk={{50,"我早就发现画像上的贼人，就是你了！",},{422,"哪吒不拿你，本将可不会放过你！",},{0,"既然如此，何必多言！你们一起上吧！ 喂！",},{63,"前面的人快让开！俺老猪刹不住啦！哇……",},},sSeekNpc="天兵",sBackground="qingyunmen",}
ctTaskPlotConf[201152]={nID=201152,nIndex=367,nTaskId=201152,sTaskName="支线-前尘旧事",sTaskDescription="击退天兵天将",tTalk={{0,"？？戒哥？",},{63,"俺老猪来也！",},},sSeekNpc="美男八戒",sBackground="qingyunmen",}
ctTaskPlotConf[201153]={nID=201153,nIndex=368,nTaskId=201153,sTaskName="支线-前尘旧事",sTaskDescription="原来是八戒和沙僧",tTalk={{0,"戒哥？怎么是你们？",},{63,"(揉了揉屁股)哼！俺老猪正要去找小白龙蹭饭呢，结果半道上被师兄拦住了，让俺来保你安危，这都哪跟哪儿啊！",},{422,"二位，虽说释道两家殊途同归，但我等领大天尊法旨前来缉拿此人。你们横加阻拦，怕是不太合适吧？ ",},{50,"呃哼！这位小施主善缘不浅，释尊命我们带他回灵山一叙，哥儿几个还是行个方便吧！",},{0," 几位，别因为我的事，伤了两家和气呀！ ",},{703,"小施主，这里有我们，你还是快走吧！留得青山在，不怕没柴烧啊！ ",},{50,"你是不是傻？还不快跑？！ ",},},sSeekNpc="美男八戒",sBackground="qingyunmen",}
ctTaskPlotConf[201154]={nID=201154,nIndex=369,nTaskId=201154,sTaskName="支线-前尘旧事",sTaskDescription="无奈只能速速离开",tTalk={{106,"站住！这里方圆十里已被我们团团围住，你是插翅难逃了！ ",},{0,"到此为止了……么？好，我跟你们回去就是。 ",},},sSeekNpc="菩提老祖",sBackground="qingyunmen",}
ctTaskPlotConf[201155]={nID=201155,nIndex=370,nTaskId=201155,sTaskName="支线-前尘旧事",sTaskDescription="老祖再次降临",tTalk={{408,"无量天尊！几位仙将，给老道一个面子可好？",},{0," 菩…菩提祖师？！ ",},},sSeekNpc="菩提老祖",sBackground="qingyunmen",}
ctTaskPlotConf[201156]={nID=201156,nIndex=371,nTaskId=201156,sTaskName="支线-前尘旧事",sTaskDescription="菩提祖师叱退天将",tTalk={{106,"祖师，这人是玉帝点名要拿的，您老这是让我们为难啊！",},{408," 呵呵呵，玉帝要拿，拿那猴儿岂不更好？何苦为难一个孩子呢？",},{106,"这个…这个…… ",},{408,"小友，我们走。",},{0," 啊？哦，弟子遵命！ ",},{106,"哎，祖师，您留步啊…… ",},},sSeekNpc="菩提老祖",sBackground="qingyunmen",}
ctTaskPlotConf[201157]={nID=201157,nIndex=372,nTaskId=201157,sTaskName="支线-前尘旧事",sTaskDescription="与祖师离开此处",tTalk={{408,"还记得这里么？ ",},{0,"这里是……？ 六百年前，你追查九黎”兵主”到此，为了阻止那魔神复生而身受重伤。",},{408,"为防你枉送性命，老道才封存了你的记忆，将你带到大唐…… 这也是不得已而为之。",},{408,"老道深知彼时法力，只能再封印那魔神十个甲子……如今，封印再次松动，也正当是你觉醒之时！",},{0,"哈？！这样说，难道…难道我有几百岁了？不是吧？！",},{408,"不记得也无妨，老道先来试试你的本领，看你修为是否尽数恢复，又是否更进一步。 ",},{408,"若这六百年来，你仍未有所突破。那么，想不起来，对你也是件好事…… ",},},sSeekNpc="菩提老祖",sBackground="qingyunmen",}
ctTaskPlotConf[201158]={nID=201158,nIndex=373,nTaskId=201158,sTaskName="支线-前尘旧事",sTaskDescription="接受的试炼",tTalk={{408,"来，让老道看看。如今的你，是否准备好面对一切了！",},},sSeekNpc="菩提老祖",sBackground="qingyunmen",}
ctTaskPlotConf[201159]={nID=201159,nIndex=374,nTaskId=201159,sTaskName="支线-前尘旧事",sTaskDescription="祖师点明一切因果",tTalk={{408,"想不到，如今的你，已然脱胎换骨……好，现在你屏息凝神，老道将你封存的记忆解开，你就能明白一切了！",},{0," 啊！！头好疼……我…我是……？太昊…伏羲氏……后人？",},{408," 不错！之所以你百邪不侵，又容颜不老，正因为你是神祖之后。",},{408,"而所谓”巫族”，则是那九黎族的末裔。 昔年神祖助黄帝封印”兵主”蚩尤，如今数千年过去，魔神再度临世。",},{408,"除魔卫道，当是你的宿命……你可明白？",},{0,"我…我…… ",},{408,"无妨，你刚刚找回记忆，还需要冷静一下。待你重新鼓起勇气，就去找那猴儿——他会助你破解这三界大劫！",},{0," 猴儿……大圣？难道祖师与大圣相熟？ ",},{408,"呵呵呵，一面之缘而已。说起来，你们两人应当是平辈论交，哈哈哈哈哈……你去吧！",},},sSeekNpc="菩提老祖",sBackground="qingyunmen",}
ctTaskPlotConf[201160]={nID=201160,nIndex=375,nTaskId=201160,sTaskName="支线-妖兽攻山",sTaskDescription="妖兽颇为狼狈",tTalk={{53,"了不起，了不起！",},{5002,"你若此等降服",},{53,"这样一柄凶戾无上的神剑，又加上下面青山灵气，你居然能支撑到现在也未见颓势，果然非常人可比。",},{5001,"你此话何意？",},{53,"我劝你早早弃剑才是，否则将来剑灵反噬，你下场只怕要比我更惨。",},{53,"妖魔外道，哪懂得我道家仁心，妖孽，受死吧！",},{53,"好，今日便让你见识一下我南疆巫术的厉害！",},},sSeekNpc="道通真人",sBackground="qingyunmen",}
ctTaskPlotConf[201161]={nID=201161,nIndex=376,nTaskId=201161,sTaskName="支线-兽王攻山",sTaskDescription="兽王现出真身",tTalk={{53,"待我先将你活剥了，再将脚下这些废物一个个剖腹挖心，送他们与你地狱一会，如何！",},},sSeekNpc="兽王",sBackground="caomiaocun",}
ctTaskPlotConf[201162]={nID=201162,nIndex=377,nTaskId=201162,sTaskName="支线-兽王攻山",sTaskDescription="兽王不敌斩妖剑",tTalk={{53,"（斩妖剑插在兽王胸口，他惨然一笑）了不起……了……不起！（突然发力向身后倒飞而去）",},{5002,"（道玄真人突觉斩妖剑上传来一股巨力，他大叫一声，手中斩妖剑一松，从云端摔了下去）",},{0,"（你连忙接住道玄真人）真人，真人你没事吧！",},{ 5001,"我没事……兽王已经被我击退，你们快……快把斩妖剑寻来……",},},sSeekNpc="道通真人",sBackground="heyang",}
ctTaskPlotConf[201163]={nID=201163,nIndex=378,nTaskId=201163,sTaskName="支线-尘事",sTaskDescription="听闻苏茹已殉情",tTalk={{0,"鬼利……你没事吧？",},{52,"（鬼利坐在地上，目光呆滞）师娘……也去了。",},{0,"人死不能复生……对你师娘的遭遇，我很遗憾。",},{52,"前辈……你知道吗？我少年时，家破人亡……是师傅师娘他们教我养我，他们的恩情，我一辈子也还不了。",},{0,"鬼利……",},{52,"（鬼利深吸了口气，站起身来）能陪我走走吗？",},{0,"……好！",},},sSeekNpc="鬼莫",sBackground="heyang",}
ctTaskPlotConf[201164]={nID=201164,nIndex=379,nTaskId=201164,sTaskName="支线-尘事",sTaskDescription="与鬼利走走",tTalk={{130,"客官，要吃些什么？",},{52,"你这里……还有没有清蒸寐鱼？",},{230,"客官，莫非以前是我们山海苑的常客？这道清蒸寐鱼是我们当初的招牌菜，不过现在吃不到了。",},{52,"这是为何？",},{230,"还不是要怪那些杀千刀的兽妖，当日兽妖占据这里，方圆百里遭了秧，就连城外河里的鱼，都被席卷而空。",},{52,"算了，你看着来几样酒菜吧。",},{230,"好嘞！",},},sSeekNpc="店小二",sBackground="heyang",}
